INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(1, '1', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 1, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 1\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Beatus vir qui non abiit.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BLESSED is the man that hath not walked in the\r\n            counsel of the ungodly, nor stood in the way of sinners,\r\n            * and hath not sat in the seat of the scornful.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But his delight is in the law of the LORD;\r\n            * and in his law will he exercise himself day and night.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And he shall be like a tree planted by the water-side,\r\n            * that will bring forth his fruit in due season.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His leaf also shall not wither;\r\n            * and look, whatsoever he doeth, it shall prosper.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for the ungodly, it is not so with them;\r\n            * but they are like the chaff, which the wind scattereth\r\n            away from the face of the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore the ungodly shall not be able to stand\r\n            in the judgment,\r\n            * neither the sinners in the congregation of the righteous.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But the LORD knoweth the way of the righteous;\r\n            * and the way of the ungodly shall perish.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 2\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Quare fremuerent gentes?</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            WHY do the heathen so furiously rage together?\r\n            * and why do the people imagine a vain thing?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The kings of the earth stand up, and the rulers\r\n            take counsel together\r\n            * against the LORD, and against his Anointed:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let us break their bonds asunder,\r\n            * and cast away their cords from us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He that dwelleth in heaven shall laugh them to scorn:\r\n            * the Lord shall have them in derision.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then shall he speak unto them in his wrath,\r\n            * and vex them in his sore displeasure:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yet have I set my King\r\n            * upon my holy hill of Sion.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will rehearse the decree;\r\n            * the LORD hath said unto me, Thou art my Son,\r\n            this day have I begotten thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Desire of me, and I shall give thee the nations\r\n            for thine inheritance,\r\n            * and the utmost parts of the earth for thy possession.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt bruise them with a rod of iron,\r\n            * and break them in pieces like a potter\'s vessel.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Be wise now therefore, O ye kings;\r\n            * be instructed, ye that are judges of the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Serve the LORD in fear,\r\n            * and rejoice unto him with reverence.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Kiss the Son, lest he be angry, and so ye perish from\r\n            the right way, if his wrath be kindled, yea but a little.\r\n            * Blessed are all they that put their trust in him.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 3\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, quid multiplicati?</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            LORD, how are they increased that trouble me!\r\n            * many are they that rise against me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Many one there be that say of my soul,\r\n            * There is no help for him in his God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But thou, O LORD, art my defender;\r\n            * thou art my worship, and the lifter up of my head.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I did call upon the LORD with my voice,\r\n            * and he heard me out of his holy hill.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I laid me down and slept, and rose up again;\r\n            * for the LORD sustained me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will not be afraid for ten thousands of the people;\r\n            * that have set themselves against me round about.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Up, LORD, and help me, O my God,\r\n            * for thou smitest all mine enemies upon the cheek-bone;\r\n            thou hast broken the teeth of the ungodly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Salvation belongeth unto the LORD;\r\n            * and thy blessing is upon thy people.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 4\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Cum invocarem.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HEAR me when I call, O God of my righteousness:\r\n            * thou hast set me at liberty when I was in trouble; have\r\n            mercy upon me, and hearken unto my prayer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O ye sons of men, how long will ye blaspheme mine honour,\r\n            * and have such pleasure in vanity, and seek after falsehood?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Know this also, that the LORD hath chosen to himself the\r\n            man that is godly;\r\n            * when I call upon the LORD he will hear me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Stand in awe, and sin not;\r\n            * commune with your own heart, and in your chamber,\r\n            and be still.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Offer the sacrifice of righteousness,\r\n            * and put your trust in the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There be many that say,\r\n            * Who will show us any good? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            LORD, lift thou up\r\n            * the light of thy countenance upon us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast put gladness in my heart;\r\n            * yea, more than when their corn and wine and oil increase.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will lay me down in peace, and take my rest;\r\n            * for it is thou, LORD, only, that makest me dwell in safety.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 5\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Verba mea auribus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            PONDER my words, O LORD,\r\n            *  consider my meditation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O hearken thou unto the voice of my calling, my King\r\n            and my God:\r\n            *  for unto thee will I make my prayer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My voice shalt thou hear betimes, O LORD;\r\n            *  early in the morning will I direct my prayer unto thee,\r\n            and will look up.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou art the God that hast no pleasure in wickedness;\r\n            *  neither shall any evil dwell with thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Such as be foolish shall not stand in thy sight;\r\n            *  for thou hatest all them that work iniquity.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt destroy them that speak lies:\r\n            *  the LORD will abhor both the blood-thirsty and\r\n            deceitful man.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But as for me, in the multitude of thy mercy I will come\r\n            into thine house;\r\n            *  and in thy fear will I worship toward thy holy temple.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lead me, O LORD, in thy righteousness, because of\r\n            mine enemies;\r\n            *  make thy way plain before my face.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For there is no faithfulness in their mouth;\r\n            *  their inward parts are very wickedness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their throat is an open sepulchre;\r\n            *  they flatter with their tongue.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Destroy thou them, O God; let them perish through\r\n            their own imaginations;\r\n            *  cast them out in the multitude of their ungodliness;\r\n            for they have rebelled against thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And let all them that put their trust in thee rejoice:\r\n            *  they shall ever be giving of thanks, because thou\r\n            defendest them; they that love thy Name shall\r\n            be joyful in thee; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou, LORD, wilt give thy blessing unto\r\n            the righteous,\r\n            *  and with thy favourable kindness wilt thou defend\r\n            him as with a shield.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(2, '2', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 2, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 9\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Confitebor tibi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I WILL give thanks unto thee, O LORD, with my whole heart;\r\n            *  I will speak of all thy marvellous works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will be glad and rejoice in thee;\r\n            *  yea, my songs will I make of thy Name, O thou Most Highest.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            While mine enemies are driven back,\r\n            *  they shall fall and perish at thy presence.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou hast maintained my right and my cause;\r\n            *  thou art set in the throne that judgest right.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast rebuked the heathen, and destroyed the ungodly;\r\n            *  thou hast put out their name for ever and ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O thou enemy, thy destructions are come to a perpetual end;\r\n            *  even as the cities which thou hast destroyed,\r\n            whose memorial is perished with them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But the LORD shall endure for ever;\r\n            *  he hath also prepared his seat for judgment.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he shall judge the world in righteousness,\r\n            *  and minister true judgment unto the people.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD also will be a defence for the oppressed,\r\n            *  even a refuge in due time of trouble.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And they that know thy Name will put their trust in thee;\r\n            *  for thou, LORD, hast never failed them that seek thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O praise the LORD which dwelleth in Sion;\r\n            *  show the people of his doings.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For when he maketh inquisition for blood,\r\n            he remembereth them,\r\n            *  and forgetteth not the complaint of the poor.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Have mercy upon me, O LORD; consider the trouble\r\n            which I suffer of them that hate me,\r\n            *  thou that liftest me up from the gates of death; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            That I may show all thy praises within the gates of\r\n            the daughter of Sion:\r\n            *  I will rejoice in thy salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The heathen are sunk down in the pit that they made;\r\n            *  in the same net which they hid privily is their foot taken.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is known to execute judgment;\r\n            *  the ungodly is trapped in the work of his own hands.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The wicked shall be turned to destruction,\r\n            *  and all the people that forget God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the poor shall not alway be forgotten;\r\n            *  the patient abiding of the meek shall not perish for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Up, LORD, and let not man have the upper hand;\r\n            *  let the heathen be judged in thy sight.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Put them in fear, O LORD,\r\n            *  that the heathen may know themselves to be but men.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 10\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Tenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Ut quid, Domine?</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            WHY standest thou so far off, O LORD,\r\n            *  and hidest thy face in the needful time of trouble? <br/>\r\n            <br/>\r\n            The ungodly, for his own lust, doth persecute the poor:\r\n            *  let them be taken in the crafty wiliness that they\r\n            have imagined.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the ungodly hath made boast of his own\r\n            heart\'s desire,\r\n            *  and speaketh good of the covetous, whom\r\n            the LORD abhorreth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The ungodly is so proud, that he careth not for God,\r\n            *  neither is God in all his thoughts.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His ways are alway grievous;\r\n            *  thy judgments are far above out of his sight, and\r\n            therefore defieth he all his enemies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he hath said in his heart, Tush, I shall never\r\n            be cast down,\r\n            *  there shall no harm happen unto me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His mouth is full of cursing, deceit, and fraud;\r\n            *  under his tongue is ungodliness and vanity.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He sitteth lurking in the thievish corners of the streets,\r\n            *  and privily in his lurking dens doth he murder the\r\n            innocent; his eyes are set against the poor.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he lieth waiting secretly; even as a lion lurketh\r\n            he in his den,\r\n            *  that he may ravish the poor.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He doth ravish the poor,\r\n            *  when he getteth him into his net.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He falleth down, and humbleth himself,\r\n            *  that the congregation of the poor may fall into\r\n            the hands of his captains.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath said in his heart, Tush, God hath forgotten;\r\n            *  he hideth away his face, and he will never see it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Arise, O LORD God, and lift up thine hand;\r\n            *  forget not the poor.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wherefore should the wicked blaspheme God,\r\n            *  while he doth say in his heart, Tush, thou God carest\r\n            not for it? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Surely thou hast seen it;\r\n            *  for thou beholdest ungodliness and wrong, that thou\r\n            mayest take the matter into thy hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The poor committeth himself unto thee;\r\n            *  for thou art the helper of the friendless.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Break thou the power of the ungodly and malicious;\r\n            *  search out his ungodliness, until thou find none.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is King for ever and ever,\r\n            *  and the heathen are perished out of the land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            LORD, thou hast heard the desire of the poor;\r\n            *  thou preparest their heart, and thine ear hearkeneth; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            To help the fatherless and poor unto their right,\r\n            *  that the man of the earth be no more exalted\r\n            against them.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 11\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eleventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>In Domino confido.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            IN the LORD put I my trust;\r\n            *  how say ye then to my soul, that she should flee\r\n            as a bird unto the hill? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            For lo, the ungodly bend their bow, and make ready\r\n            their arrows within the quiver,\r\n            *  that they may privily shoot at them which are\r\n            true of heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If the foundations be destroyed,\r\n            *  what can the righteous do? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is in his holy temple;\r\n            *  the LORD\'S seat is in heaven.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His eyes consider the poor,\r\n            *  and his eyelids try the children of men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD approveth the righteous:\r\n            *  but the ungodly, and him that delighteth in wickedness,\r\n            doth his soul abhor.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Upon the ungodly he shall rain snares, fire and\r\n            brimstone, storm and tempest:\r\n            *  this shall be their portion to drink.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the righteous LORD loveth righteousness;\r\n            *  his countenance will behold the thing that is just.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(3, '3', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 3, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 15\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, quis habitabit?</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            LORD, who shall dwell in thy tabernacle?\r\n            * or who shall rest upon thy holy hill?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Even he that leadeth an uncorrupt life,\r\n            * and doeth the thing which is right, and speaketh the\r\n            truth from his heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He that hath used no deceit in his tongue,\r\n            nor done evil to his neighbour,\r\n            * and hath not slandered his neighbour.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He that setteth not by himself, but is\r\n            lowly in his own eyes,\r\n            * and maketh much of them that fear the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He that sweareth unto his neighbour,\r\n            and disappointeth him not,\r\n            * though it were to his own hindrance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He that hath not given his money upon usury,\r\n            * nor taken reward against the innocent.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whoso doeth these things\r\n            * shall never fall.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 16\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Conserva me, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            PRESERVE me, O God;\r\n            * for in thee have I put my trust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O my soul, thou hast said unto the LORD,\r\n            * Thou art my God; I have no good like unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All my delight is upon the saints that are in the earth,\r\n            * and upon such as excel in virtue.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But they that run after another god\r\n            * shall have great trouble.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their drink-offerings of blood will I not offer,\r\n            * neither make mention of their names within my lips.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD himself is the portion of mine inheritance, and of my cup;\r\n            * thou shalt maintain my lot.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The lot is fallen unto me in a fair ground;\r\n            * yea, I have a goodly heritage.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will thank the LORD for giving me warning;\r\n            * my reins also chasten me in the night season.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I have set the LORD alway before me;\r\n            * for he is on my right hand, therefore I shall not fall.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wherefore my heart is glad, and my glory rejoiceth:\r\n            * my flesh also shall rest in hope.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For why? thou shalt not leave my soul in hell;\r\n            * neither shalt thou suffer thy Holy One to see corruption.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt show me the path of life: in thy presence is the fulness of joy,\r\n            * and at thy right hand there is pleasure for evermore.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 17\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventeenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exaudi, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HEAR the right, O LORD, consider my complaint,\r\n            * and hearken unto my prayer, that goeth not out\r\n            of feigned lips.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let my sentence come forth from thy presence;\r\n            * and let thine eyes look upon the thing that is equal.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast proved and visited mine heart in the\r\n            night season; thou hast tried me, and shalt find\r\n            no wickedness in me;\r\n            * for I am utterly purposed that my mouth shall not offend.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for the works of men,\r\n            * by the word of thy lips I have kept me from the ways\r\n            of the destroyer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O hold thou up my goings in thy paths,\r\n            * that my footsteps slip not.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I have called upon thee, O God, for thou shalt hear me:\r\n            * incline thine ear to me, and hearken unto my words.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Show thy marvellous loving-kindness, thou that art the\r\n            Saviour of them which put their trust in thee,\r\n            * from such as resist thy right hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Keep me as the apple of an eye;\r\n            * hide me under the shadow of thy wings,<br/>\r\n            <small> &nbsp; <br/></small>\r\n            From the ungodly, that trouble me;\r\n            * mine enemies compass me round about, to take\r\n            away my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They are inclosed in their own fat,\r\n            * and their mouth speaketh proud things.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They lie waiting in our way on every side,\r\n            * watching to cast us down to the ground; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Like as a lion that is greedy of his prey,\r\n            * and as it were a lion\'s whelp lurking in secret places.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Up, LORD, disappoint him, and cast him down;\r\n            * deliver my soul from the ungodly, by thine own sword;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, by thy hand, O LORD; from the men of\r\n            the evil world;\r\n            * which have their portion in this life, whose bellies\r\n            thou fillest with thy hid treasure.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have children at their desire,\r\n            * and leave the rest of their substance for their babes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But as for me, I shall behold thy presence in  righteousness;\r\n            * and when I awake up after thy likeness, I shall be satisfied.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(4, '4', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 4, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 19\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Nineteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Caeli enarrant.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE heavens declare the glory of God;\r\n            * and the firmament showeth his handy-work.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            One day telleth another;\r\n            * and one night certifieth another.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There is neither speech nor language;\r\n            * but their voices are heard among them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their sound is gone out into all lands;\r\n            * and their words into the ends of the world.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In them hath he set a tabernacle for the sun;\r\n            * which cometh forth as a bridegroom out of his chamber,\r\n            and  rejoiceth as a giant to run his course.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            It goeth forth from the uttermost part of the heaven, and\r\n            runneth about unto the end of it again;\r\n            * and there is nothing hid from the heat thereof.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The law of the LORD is an undefiled law, converting\r\n            the soul;\r\n            * the testimony of the LORD is sure, and giveth wisdom\r\n            unto the simple.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The statutes of the LORD are right, and rejoice the heart;\r\n            * the commandment of the LORD is pure, and giveth light\r\n            unto the eyes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The fear of the LORD is clean, and endureth forever;\r\n            * the judgments of the LORD are true, and righteous\r\n            altogether.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            More to be desired are they than gold, yea, than much\r\n            fine gold;\r\n            * sweeter also than honey, and the honeycomb.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Moreover, by them is thy servant taught;\r\n            * and in keeping of them there is great reward.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who can tell how oft he offendeth?\r\n            * O cleanse thou me from my secret faults.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Keep thy servant also from presumptuous sins, lest\r\n            they get the dominion over me;\r\n            * so shall I be undefiled, and innocent from the\r\n            great offence.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the words of my mouth, and the meditation of my\r\n            heart, be alway acceptable in thy sight,\r\n            * O LORD, my strength and my redeemer.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 20\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twentieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exaudiat te Dominus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE LORD hear thee in the day of trouble;\r\n            * the Name of the God of Jacob defend thee:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Send thee help from the sanctuary,\r\n            * and strengthen thee out of Sion:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Remember all thy offerings,\r\n            * and accept thy burnt sacrifice:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Grant thee thy heart\'s desire,\r\n            * and fulfil all thy mind.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            We will rejoice in thy salvation, and triumph in the\r\n            Name of the Lord our God:\r\n            * the LORD perform all thy petitions.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Now know I that the LORD helpeth his anointed, and\r\n            will hear him from his holy heaven,\r\n            * even with the wholesome strength of his right hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Some put their trust in chariots, and some in horses;\r\n            * but we will remember the Name of the LORD our God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They are brought down and fallen;\r\n            * but we are risen and stand upright.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Save, LORD; and hear us, O King of heaven,\r\n            * when we call upon thee.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 21\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twentieth-First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, in virtute tua.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        THE King shall rejoice in thy strength, O LORD;\r\n        * exceeding glad shall he be of thy salvation.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast given him his heart\'s desire,\r\n        * and hast not denied him the request of his lips.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For thou shalt meet him with the blessings of goodness,\r\n        * and shalt set a crown of pure gold upon his head.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He asked life of thee; and thou gavest him a long life,\r\n        * even for ever and ever.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        His honour is great in thy salvation;\r\n        * glory and great worship shalt thou lay upon him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For thou shalt give him everlasting felicity,\r\n        * and make him glad with the joy of thy countenance.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 7 </i>And why? because the King putteth his trust in the\r\n        LORD;\r\n        * and in the mercy of the Most Highest he shall not\r\n        miscarry.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        All thine enemies shall feel thine hand;\r\n        * thy right hand shall find out them that hate thee.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou shalt make them like a fiery oven in time of\r\n        thy wrath:\r\n        * the LORD shall destroy them in his displeasure, and the\r\n        fire shall consume them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Their fruit shalt thou root out of the earth,\r\n        * and their seed from among the children of men.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For they intended mischief against thee,\r\n        * and imagined such a device as they are not able to\r\n        perform.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Therefore shalt thou put them to flight,\r\n        * and the strings of thy bow shalt thou make ready against\r\n        the face  of them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Be thou exalted, LORD, in thine own strength;\r\n        * so will we sing, and praise thy power.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(5, '5', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 5, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 24\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twenty-Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domini est terra.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE earth is the LORD\'S, and all that therein is;\r\n            * the compass of the world, and they that dwell therein.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he hath founded it upon the seas,\r\n            * and stablished it upon the floods.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who shall ascend into the hill of the LORD?\r\n            * or who shall rise up in his holy place?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Even he that hath clean hands, and a pure heart;\r\n            * and that hath not lift up his mind unto vanity, nor sworn\r\n            to deceive his neighbour.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall receive the blessing from the LORD,\r\n            * and righteousness from the God of his salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            This is the generation of them that seek him;\r\n            * even of them that seek thy face, O God of Jacob.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lift up your heads, O ye gates; and be ye lift up,\r\n            ye everlasting doors;\r\n            * and the King of glory shall come in.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who is this King of glory?\r\n            * It is the LORD strong and mighty, even the LORD\r\n            mighty in battle.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lift up your heads, O ye gates; and be ye lift up,\r\n            ye everlasting doors;\r\n            * and the King of glory shall come in.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who is this King of glory?\r\n            * Even the LORD of hosts, he is the King of glory.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 25\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twenty-Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Ad te, Domine, levavi.</i></h4></div>\r\n<hr/>\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            UNTO thee, O LORD, will I lift up my soul; my God, I have put my trust in thee:\r\n            * O let me not be confounded, neither let mine enemies triumph over me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For all they that hope in thee shall not be ashamed; * but such as transgress without a cause shall be put to\r\n            confusion.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Show me thy ways, O LORD, * and teach me thy paths.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lead me forth in thy truth, and learn me: * for thou art the God of my salvation; in thee hath been my\r\n            hope all the day long.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Call to remembrance, O LORD, thy tender mercies, * and thy loving-kindnesses, which have been ever of\r\n            old.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O remember not the sins and offences of my youth; * but according to thy mercy think thou upon me, O\r\n            LORD, for thy goodness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Gracious and righteous is the LORD; * therefore, will he teach sinners in the way.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Them that are meek shall he guide in judgment; * and such as are gentle, them shall he learn his\r\n            way.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All the paths of the LORD are mercy and truth, * unto such as keep his covenant and his testimonies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thy Name\'s sake, O LORD, * be merciful unto my sin; for it is great.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            What man is he that feareth the LORD? * him shall he teach in the way that he shall choose.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His soul shall dwell at ease, * and his seed shall inherit the land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The secret of the LORD is among them that fear him; * and he will show them his covenant.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Mine eyes are ever looking unto the LORD; * for he shall pluck my feet out of the net.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn thee unto me, and have mercy upon me; * for I am desolate, and in misery.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The sorrows of my heart are enlarged: * O bring thou me out of my troubles.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Look upon my adversity and misery, * and forgive me all my sin.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Consider mine enemies, how many they are; * and they bear a tyrannous hate against me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O keep my soul, and deliver me: * let me not be confounded, for I have put my trust in thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let perfectness and righteous dealing wait upon me; * for my hope hath been in thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Deliver Israel, O God, * out of all his troubles.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 26\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twenty-Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Judica me, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BE thou my Judge, O LORD,\r\n            for I have walked innocently:\r\n            * my trust hath been also in the LORD,\r\n            therefore shall I not fall.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Examine me, O LORD, and prove me;\r\n            * try out my reins and my heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thy loving-kindness is ever before mine eyes;\r\n            * and I will walk in thy truth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I have not dwelt with vain persons;\r\n            * neither will I have fellowship with the deceitful.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I have hated the congregation of the wicked;\r\n            * and will not sit among the ungodly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will wash my hands in innocency, O LORD;\r\n            * and so will I go to thine altar;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That I may show the voice of thanksgiving,\r\n            * and tell of all thy wondrous works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            LORD, I have loved the habitation of thy house,\r\n            * and the place where thine honour dwelleth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O shut not up my soul with the sinners,\r\n            * nor my life with the blood-thirsty;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In whose hands is wickedness,\r\n            * and their right hand is full of gifts.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But as for me, I will walk innocently:\r\n            * O deliver me, and be merciful unto me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My foot standeth right:\r\n            * I will praise the LORD in the congregations.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(6, '6', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 6, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 30\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirtieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exaltabo te, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I WILL magnify thee, O LORD; for thou hast set me up,\r\n            * and not made my foes to triumph over me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O LORD my God, I cried unto thee;\r\n            * and thou hast healed me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou, LORD, hast brought my soul out of hell:\r\n            * thou hast kept my life, that I should not go down\r\n            into the pit.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Sing praises unto the LORD, O ye saints of his;\r\n            * and give thanks unto him, for a remembrance of\r\n            his holiness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For his wrath endureth but the twinkling of an eye,\r\n            and in his pleasure is life;\r\n            * heaviness may endure for a night, but joy cometh in\r\n            the morning.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And in my prosperity I said, I shall never be removed:\r\n            * thou, LORD, of thy goodness, hast made my hill\r\n            so strong.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou didst turn thy face from me,\r\n            * and I was troubled.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then cried I unto thee, O LORD;\r\n            * and gat me to my LORD right humbly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            What profit is there in my blood,\r\n            * when I go down into the pit?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Shall the dust give thanks unto thee?\r\n            * or shall it declare thy truth?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hear, O LORD, and have mercy upon me;\r\n            * LORD, be thou my helper.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast turned my heaviness into joy;\r\n            * thou hast put off my sackcloth, and girded me\r\n            with gladness:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore shall every good man sing of thy praise\r\n            without ceasing.\r\n            * O my God, I will give thanks unto thee for ever.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 31\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirty-First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>In te, Domine, speravi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        IN thee, O LORD, have I put my trust; let me never\r\n        be put to confusion;\r\n        * deliver me in thy righteousness.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Bow down thine ear to me;\r\n        * make haste to deliver me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And be thou my strong rock, and house of defence,\r\n        * that thou mayest save me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For thou art my strong rock, and my castle:\r\n        * be thou also my guide, and lead me for thy\r\n        Name\'s sake.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Draw me out of the net that they have laid privily\r\n        for me;\r\n        * for thou art my strength.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Into thy hands I commend my spirit;\r\n        * for thou hast redeemed me, O LORD, thou God\r\n        of truth.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 7 </i>I have hated them that hold of lying vanities,\r\n        * and my trust hath been in the LORD.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will be glad and rejoice in thy mercy;\r\n        * for thou hast considered my trouble, and hast known\r\n        my soul in adversities.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast not shut me up into the hand of the enemy;\r\n        * but hast set my feet in a large room.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Have mercy upon me, O LORD, for I am in trouble,\r\n        * and mine eye is consumed for very heaviness; yea,\r\n        my soul and my body.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For my life is waxen old with heaviness,\r\n        * and my years with mourning.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My strength faileth me, because of mine iniquity,\r\n        * and my bones are consumed.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I became a reproach among all mine enemies, but\r\n        especially among my neighbours;\r\n        * and they of mine acquaintance were afraid of me;\r\n        and they that did see me without, conveyed\r\n        themselves from me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I am clean forgotten as a dead man out of mind;\r\n        * I am become like a broken vessel.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For I have heard the blasphemy of the multitude,\r\n        and fear is on every side;\r\n        * while they conspire together against me, and take their\r\n        counsel to take away my life.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But my hope hath been in thee, O LORD;\r\n        * I have said, Thou art my God.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My times are in thy hand; deliver me from the hand\r\n        of mine enemies,\r\n        * and from them that persecute me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Show thy servant the light of thy countenance,\r\n        * and save me for thy mercy\'s sake.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let me not be confounded, O LORD, for I have\r\n        called upon thee;\r\n        * let the ungodly be put to confusion, and be put to silence\r\n        in the grave.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let the lying lips be put to silence,\r\n        * which cruelly, disdainfully, and despitefully speak\r\n        against the righteous.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O how plentiful is thy goodness, which thou hast laid up\r\n        for them that fear thee,\r\n        * and that thou hast prepared for them that put their trust\r\n        in thee, even before the sons of men!<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou shalt hide them in the covert of thine own presence\r\n        from the plottings of men:\r\n        * thou shalt keep them secretly in thy tabernacle from the\r\n        strife of tongues.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thanks be to the LORD;\r\n        * for he hath showed me marvellous great kindness in\r\n        a strong city.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But in my haste I said,\r\n        * I am cast out of the sight of thine eyes.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Nevertheless, thou heardest the voice of my prayer,\r\n        * when I cried unto thee.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O love the LORD, all ye his saints;\r\n        * for the LORD preserveth them that are faithful,\r\n        and plenteously rewardeth the proud doer.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Be strong, and he shall establish your heart,\r\n        * all ye that put your trust in the LORD.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(7, '7', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 7, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 35\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirty-Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Judica, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            PLEAD thou my cause, O LORD, with them that strive with me,\r\n            *  and fight thou against them that fight against me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lay hand upon the shield and buckler,\r\n            *  and stand up to help me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Bring forth the spear, and stop the way against them that pursue me:\r\n            *  say unto my soul, I am thy salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them be confounded, and put to shame, that seek after my soul;\r\n            *  let them be turned back, and brought to confusion, that imagine mischief for me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them be as the dust before the wind,\r\n            *  and the angel of the LORD scattering them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let their way be dark and slippery,\r\n            *  and let the angel of the LORD pursue them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For they have privily laid their net to destroy me without a cause;\r\n            *  yea, even without a cause have they made a pit for my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let a sudden destruction come upon him unawares, and his net that he hath laid privily catch himself;\r\n            *  that he may fall into his own mischief.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And my soul shall be joyful in the LORD;\r\n            *  it shall rejoice in his salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All my bones shall say, LORD, who is like unto thee, who deliverest the poor from him that is too strong for him;\r\n            *  yea, the poor, and him that is in misery, from him that spoileth him?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            False witnesses did rise up:\r\n            *  they laid to my charge things that I knew not.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They rewarded me evil for good,\r\n            *  to the great discomfort of my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Nevertheless, when they were sick, I put on sackcloth, and humbled my soul with fasting;\r\n            *  and my prayer shall turn into mine own bosom.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I behaved myself as though it had been my friend or my brother;\r\n            *  I went heavily, as one that mourneth for his mother.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But in mine adversity they rejoiced, and gathered themselves together;\r\n            *  yea, the very abjects came together against me  unawares, making mouths at me, and ceased not.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            With the flatterers were busy mockers,\r\n            *  who gnashed upon me with their teeth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lord, how long wilt thou look upon this?\r\n            *  O deliver my soul from the calamities which they bring on me, and  my darling from the lions.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So will I give thee thanks in the great congregation;\r\n            *  I will praise thee among much people.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let not them that are mine enemies triumph over me ungodly;\r\n            *  neither let them wink with their eyes, that hate me without a cause.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And why? their communing is not for peace;\r\n            *  but they imagine deceitful words against them that are quiet in the land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They gaped upon me with their mouths, and said,\r\n            *  Fie on thee! fie on thee! we saw it with our eyes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            This thou hast seen, O LORD;\r\n            *  hold not thy tongue then; go not far from me, O Lord.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Awake, and stand up to judge my quarrel;\r\n            *  avenge thou my cause, my God and my Lord.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Judge me, O LORD my God, according to thy righteousness;\r\n            *  and let them not triumph over me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them not say in their hearts, There! there! so would we have it;\r\n            *  neither let them say, We have devoured him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them be put to confusion and shame together, that rejoice at my trouble;\r\n            *  let them be clothed with rebuke and dishonour, that boast themselves against me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them be glad and rejoice, that favour my righteous dealing;\r\n            *  yea, let them say alway, Blessed be the LORD, who hath pleasure in the prosperity of his servant.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And as for my tongue, it shall be talking of thy righteousness,\r\n            *  and of thy praise, all the day long.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 36\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirty-Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dixit injustus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        MY heart showeth me the wickedness of the ungodly,\r\n        * that there is no fear of God before his eyes.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For he flattereth himself in his own sight,\r\n        * until his abominable sin be found out.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The words of his mouth are unrighteous and\r\n        full of deceit:\r\n        * he hath left off to behave himself wisely, and to do good.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He imagineth mischief upon his bed, and hath\r\n        set himself in no good way;\r\n        * neither doth he abhor any thing that is evil.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 5 </i>Thy mercy, O LORD, reacheth unto the heavens,\r\n        * and thy faithfulness unto the clouds.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thy righteousness standeth like the strong mountains:\r\n        * thy judgments are like the great deep.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou, LORD, shalt save both man and beast:\r\n        how excellent is thy mercy, O God!\r\n        * and the children of men shall put their trust under\r\n        the shadow of thy wings.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They shall be satisfied with the plenteousness\r\n        of thy house;\r\n        * and thou shalt give them drink of thy pleasures,\r\n        as out of the river.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For with thee is the well of life;\r\n        * and in thy light shall we see light.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O continue forth thy loving-kindness unto them\r\n        that know thee,\r\n        * and thy righteousness unto them that are true of heart.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O let not the foot of pride come against me;\r\n        and let not the hand of the ungodly cast me down.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        There are they fallen, all that work wickedness;\r\n        * they are cast down, and shall not be able to stand.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(8, '8', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 8, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 38\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirty-Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, ne in furore.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            PUT me not to rebuke, O LORD, in thine anger;\r\n            *  neither chasten me in thy heavy displeasure:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thine arrows stick fast in me,\r\n            *  and thy hand presseth me sore.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There is no health in my flesh, because of thy displeasure;\r\n            *  neither is there any rest in my bones, by reason of my sin.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For my wickednesses are gone over my head,\r\n            *  and are like a sore burden, too heavy for me to bear.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My wounds stink, and are corrupt,\r\n            *  through my foolishness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I am brought into so great trouble and misery,\r\n            *  that I go mourning all the day long.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For my loins are filled with a sore disease,\r\n            *  and there is no whole part in my body.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I am feeble and sore smitten;\r\n            *  I have roared for the very disquietness of my heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lord, thou knowest all my desire;\r\n            *  and my groaning is not hid from thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My heart panteth, my strength hath failed me,\r\n            *  and the light of mine eyes is gone from me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My lovers and my neighbours did stand looking upon my trouble,\r\n            *  and my kinsmen stood afar off.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They also that sought after my life laid snares for me;\r\n            *  and they that went about to do me evil talked of wickedness, and imagined deceit all the day long.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for me, I was like a deaf man, and heard not;\r\n            *  and as one that is dumb, who doth not open his mouth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I became even as a man that heareth not,\r\n            *  and in whose mouth are no reproofs.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For in thee, O LORD, have I put my trust;\r\n            *  thou shalt answer for me, O Lord my God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I have required that they, even mine enemies, should not triumph over me;\r\n            *  for when my foot slipt, they rejoiced greatly against me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And I truly am set in the plague,\r\n            *  and my heaviness is ever in my sight.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For I will confess my wickedness,\r\n            *  and be sorry for my sin.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But mine enemies live, and are mighty;\r\n            *  and they that hate me wrongfully are many in number.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They also that reward evil for good are against me;\r\n            *  because I follow the thing that good is.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Forsake me not, O LORD my God;\r\n            *  be not thou far from me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Haste thee to help me,\r\n            *  O Lord God of my salvation.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 39\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirty-Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dixi, Custodiam.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I SAID, I will take heed to my ways,\r\n            * that I offend not in my tongue<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will keep my mouth as it were with a bridle,\r\n            * while the ungodly is in my sight. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            I held my tongue, and spake nothing:\r\n            * I kept silence, yea, even from good words; but it was pain and grief to me. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            My heart was hot within me: and while I was thus musing the fire kindled,\r\n            *and at the last I spake with my tongue: <br/>\r\n            <small> &nbsp; <br/></small>\r\n            LORD, let me know mine end, and the number of my days;\r\n            * that I may be certified how long I have to live. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, thou hast made my days as it were a span long, and mine age is even as nothing in respect of thee;\r\n            * and verily every man living is altogether vanity. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            For man walketh in a vain shadow, and disquieteth  himself in vain;\r\n            * he heapeth up riches, and cannot tell who shall gather them. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            And now, Lord, what is my hope?\r\n            * truly my hope is even in thee. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Deliver me from all mine offences;\r\n            * and make me not a rebuke unto the foolish. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            I became dumb, and opened not my mouth;\r\n            * for it was thy doing. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Take thy plague away from me:\r\n            * I am even consumed by the means of thy heavy hand. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            When thou with rebukes dost chasten man for sin, thou makest his beauty to consume away, like as it were a moth fretting a garment:\r\n            * every man therefore is but vanity. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hear my prayer, O LORD, and with thine ears consider my calling;\r\n            * hold not thy peace at my tears; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            For I am a stranger with thee, and a sojourner,\r\n            * as  all my fathers were. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            O spare me a little, that I may recover my strength,\r\n            *  before I go hence, and be no more seen.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 40\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fortieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Expectans expectavi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        I WAITED patiently for the LORD,\r\n        * and he inclined unto me, and heard my calling.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He brought me also out of the horrible pit,\r\n        out of the mire and clay,\r\n        * and set my feet upon the rock, and ordered my goings.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And he hath put a new song in my mouth,\r\n        * even a thanksgiving unto our God.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Many shall see it, and fear,\r\n        * and shall put their trust in the LORD.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Blessed is the man that hath set his hope in the LORD,\r\n        * and turned not unto the proud, and to such as go\r\n        about with lies.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O LORD my God, great are the wondrous works which\r\n        thou hast done, like as be also thy thoughts, which are\r\n        to us-ward;\r\n        * and yet there is no man that ordereth them unto thee.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        If I should declare them, and speak of them,\r\n        * they should be more than I am able to express.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Sacrifice and offering thou wouldest not,\r\n        * but mine ears hast thou opened.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Burnt-offering and sacrifice for sin hast thou\r\n        not required:\r\n        * then said I, LO, I come; <br/>\r\n        <small> &nbsp; <br/></small>\r\n        In the volume of the book it is written of me,\r\n        that I should fulfil thy will, O my God:\r\n        * I am content to do it; yea, thy law is within my heart.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I have declared thy righteousness in the great\r\n        congregation:\r\n        * lo, I will not refrain my lips, O LORD, and that\r\n        thou knowest.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I have not hid thy righteousness within my heart;\r\n        * my talk hath been of thy truth, and of thy salvation.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I have not kept back thy loving mercy and truth\r\n        * from the great congregation.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Withdraw not thou thy mercy from me, O LORD;\r\n        * let thy loving-kindness and thy truth alway preserve me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For innumerable troubles are come about me; my sins\r\n        have taken such hold upon me, that I am not able\r\n        to look up;\r\n        * yea, they are more in number than the hairs of my head,\r\n        and my heart hath failed me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O LORD, let it be thy pleasure to deliver me;\r\n        * make haste, O LORD, to help me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 17 </i>Let them be ashamed, and confounded together,\r\n        that seek after my soul to destroy it;\r\n        * let them be driven backward, and put to rebuke,\r\n        that wish me evil.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let them be desolate, and rewarded with shame,\r\n        * that say unto me, Fie upon thee! fie upon thee!<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let all those that seek thee, be joyful and glad in thee;\r\n        * and let such as love thy salvation, say alway,\r\n        The LORD be praised.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        As for me, I am poor and needy;\r\n        * but the Lord careth for me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou art my helper and redeemer;\r\n        * make no long tarrying, O my God.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(9, '9', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 9, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 44\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Forty-Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, auribus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            WE have heard with our ears, O God,\r\n            our fathers have told us\r\n            * what thou hast done in their time of old:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            How thou hast driven out the heathen with thy hand,\r\n            and planted our fathers in;\r\n            * how thou hast destroyed the nations, and made thy\r\n            people to flourish.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For they gat not the land in possession through\r\n            their own sword,\r\n            * neither was it their own arm that helped them; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            But thy right hand, and thine arm, and the light of\r\n            thy countenance;\r\n            * because thou hadst a favour unto them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou art my King, O God;\r\n            * send help unto Jacob.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Through thee will we overthrow our enemies,\r\n            * and in thy Name will we tread them under that rise\r\n            up against us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For I will not trust in my bow,\r\n            * it is not my sword that shall help me; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            But it is thou that savest us from our enemies,\r\n            * and puttest them to confusion that hate us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            We make our boast of God all day long,\r\n            * and will praise thy Name for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But now thou art far off, and puttest us to confusion,\r\n            * and goest not forth with our armies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou makest us to turn our backs upon our enemies,\r\n            * so that they which hate us spoil our goods.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou lettest us be eaten up like sheep,\r\n            * and hast scattered us among the heathen.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou sellest thy people for nought,\r\n            * and takest no money for them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou makest us to be rebuked of our neighbours,\r\n            * to be laughed to scorn, and had in derision of them\r\n            that are round about us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou makest us to be a by-word among the nations,\r\n            * and that the peoples shake their heads at us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My confusion is daily before me,\r\n            * and the shame of my face hath covered me; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the voice of the slanderer and blasphemer,\r\n            * for the enemy and avenger.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And though all this be come upon us, yet do we\r\n            not forget thee,\r\n            * nor behave ourselves frowardly in thy covenant.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Our heart is not turned back,\r\n            * neither our steps gone out of thy way; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            No, not when thou hast smitten us into the place\r\n            of dragons,\r\n            * and covered us with the shadow of death.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If we have forgotten the Name of our God, and holden\r\n            up our hands to any strange god,\r\n            * shall not God search it out? for he knoweth the very\r\n            secrets of the heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thy sake also are we killed all the day long,\r\n            * and are counted as sheep appointed to be slain.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Up, Lord, why sleepest thou?\r\n            * awake, and be not absent from us for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wherefore hidest thou thy face,\r\n            * and forgettest our misery and trouble? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            For our soul is brought low, even unto the dust;\r\n            * our belly cleaveth unto the ground.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Arise, and help us,\r\n            * and deliver us, for thy mercy\'s sake.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 45\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Forty-Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Eructavit cor meum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            MY heart overfloweth with a good matter; I speak the\r\n            things which I have made concerning the King.\r\n            * My tongue is the pen of a ready writer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou art fairer than the children of men;\r\n            * full of grace are thy lips, because God hath blessed\r\n            thee for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Gird thee with thy sword upon thy thigh,\r\n            O thou Most Mighty,\r\n            * according to thy worship and renown.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Good luck have thou with thine honour:\r\n            * ride on, because of the word of truth, of meekness,\r\n            and righteousness; and thy right hand shall teach\r\n            thee terrible things.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy arrows are very sharp in the heart of the\r\n            King\'s enemies,\r\n            * and the people shall be subdued unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy seat, O God, endureth for ever;\r\n            * the sceptre of thy kingdom is a right sceptre.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast loved righteousness, and hated iniquity;\r\n            * wherefore God, even thy God, hath anointed thee\r\n            with the oil of gladness above thy fellows.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All thy garments smell of myrrh, aloes, and cassia;\r\n            * out of the ivory palaces, whereby they have made\r\n            thee glad.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Kings\' daughters are among thy honourable women;\r\n            * upon thy right hand doth stand the queen in a vesture\r\n            of gold, wrought about with divers colours.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hearken, O daughter, and consider; incline thine ear;\r\n            * forget also thine own people, and thy father\'s house.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So shall the King have pleasure in thy beauty;\r\n            * for he is thy Lord, and worship thou him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And the daughter of Tyre shall be there with a gift;\r\n            * like as the rich also among the people shall make their\r\n            supplication before thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The King\'s daughter is all glorious within;\r\n            * her clothing is of wrought gold.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            She shall be brought unto the King in raiment\r\n            of needlework:\r\n            * the virgins that be her fellows shall bear her company,\r\n            and shall be brought unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            With joy and gladness shall they be brought,\r\n            * and shall enter into the King\'s palace.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Instead of thy fathers, thou shalt have children,\r\n            * whom thou mayest make princes in all lands.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will make thy Name to be remembered from one\r\n            generation to another;\r\n            * therefore shall the people give thanks unto thee, world\r\n            without end.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 46\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Forty-Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus noster refugium.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            GOD is our hope and strength,\r\n            * a very present help in trouble.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore will we not fear, though the earth be moved,\r\n            * and though the hills be carried into the midst of the sea;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Though the waters thereof rage and swell,\r\n            * and though the mountains shake at the tempest\r\n            of the same.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There is a river, the streams whereof make glad\r\n            the city of God;\r\n            *  the holy place of the tabernacle of the Most Highest.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God is in the midst of her, therefore shall she not\r\n            be removed;\r\n            * God shall help her, and that right early.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The nations make much ado, and the kingdoms\r\n            are moved;\r\n            * but God hath showed his voice, and the earth\r\n            shall melt away.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD of hosts is with us;\r\n            * the God of Jacob is our refuge.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O come hither, and behold the works of the LORD,\r\n            * what destruction he hath brought upon the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He maketh wars to cease in all the world;\r\n            * he breaketh the bow, and knappeth the spear in sunder,\r\n            and burneth the chariots in the fire.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Be still then, and know that I am God:\r\n            * I will be exalted among the nations, and I will\r\n            be exalted in the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD of hosts is with us;\r\n            * the God of Jacob is our refuge.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(10, '10', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 10, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 50\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fiftieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus deorum.</i></h4></div>\r\n<hr/>\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE LORD, even the Most Mighty God, hath spoken, * and called the world, from the rising up of the\r\n            sun unto the going down thereof.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Out of Sion hath God appeared * in perfect beauty.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Our God shall come, and shall not keep silence; * there shall go before him a consuming fire, and a\r\n            mighty tempest shall be stirred up round about him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall call the heaven from above, * and the earth, that he may judge his people.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Gather my saints together unto me; * those that have made a covenant with me with sacrifice.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And the heavens shall declare his righteousness; * for God is Judge himself.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hear, O my people, and I will speak; * I myself will testify against thee, O Israel; for I am God, even thy\r\n            God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will not reprove thee because of thy sacrifices; * as for thy burnt-offerings, they are alway before\r\n            me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will take no bullock out of thine house, * nor he-goats out of thy folds.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For all the beasts of the forest are mine, * and so are the cattle upon a thousand hills.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I know all the fowls upon the mountains, * and the wild beasts of the field are in my sight.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If I be hungry, I will not tell thee; * for the whole world is mine, and all that is therein.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thinkest thou that I will eat bulls\' flesh, * and drink the blood of goats?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Offer unto God thanksgiving, * and pay thy vows unto the Most Highest.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And call upon me in the time of trouble; * so will I hear thee, and thou shalt praise me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But unto the ungodly saith God, * Why dost thou preach my laws, and takest my covenant in thy mouth;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whereas thou hatest to be reformed, * and hast cast my words behind thee?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When thou sawest a thief, thou consentedst unto him; * and hast been partaker with the adulterers.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast let thy mouth speak wickedness, * and with thy tongue thou hast set forth deceit.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou sattest and spakest against thy brother; * yea, and hast slandered thine own mother\'s son.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            These things hast thou done, and I held my tongue, and thou thoughtest wickedly, that I am even such a\r\n            one as thyself; * but I will reprove thee, and set before thee the things that thou hast done.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O consider this, ye that forget God, * lest I pluck you away, and there be none to deliver you.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whoso offereth me thanks and praise, he honoureth me; * and to him that ordereth his way aright, will I\r\n            show the salvation of God.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 51\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifty-First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Miserere mei, Deus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HAVE mercy upon me, O God, after thy great goodness;\r\n            * according to the multitude of thy mercies do away\r\n            mine offences.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wash me throughly from my wickedness,\r\n            * and cleanse me from my sin.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For I acknowledge my faults,\r\n            * and my sin is ever before me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Against thee only have I sinned, and done this\r\n            evil in thy sight;\r\n            * that thou mightest be justified in thy saying,\r\n            and clear when thou shalt judge.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, I was shapen in wickedness,\r\n            * and in sin hath my mother conceived me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But lo, thou requirest truth in the inward parts,\r\n            * and shalt make me to understand wisdom secretly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt purge me with hyssop, and I shall be clean;\r\n            * thou shalt wash me, and I shall be whiter than snow.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt make me hear of joy and gladness,\r\n            * that the bones which thou hast broken may rejoice.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn thy face from my sins,\r\n            * and put out all my misdeeds.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Make me a clean heart, O God,\r\n            * and renew a right spirit within me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Cast me not away from thy presence,\r\n            * and take not thy holy Spirit from me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O give me the comfort of thy help again,\r\n            * and stablish me with thy free Spirit.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then shall I teach thy ways unto the wicked,\r\n            * and sinners shall be converted unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Deliver me from blood-guiltiness, O God,\r\n            thou that art the God of my health;\r\n            * and my tongue shall sing of thy righteousness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt open my lips, O Lord,\r\n            * and my mouth shall show thy praise.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou desirest no sacrifice, else would I give it thee;\r\n            * but thou delightest not in burnt-offerings.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The sacrifice of God is a troubled spirit:\r\n            * a broken and contrite heart, O God, shalt thou not despise.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O be favourable and gracious unto Sion;\r\n            * build thou the walls of Jerusalem.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then shalt thou be pleased with the sacrifice of\r\n            righteousness, with the burnt-offerings and oblations;\r\n            * then shall they offer young bullocks upon thine altar.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 52\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifty-Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Quid gloriaris?</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            WHY boastest thou thyself, thou tyrant,\r\n            *  that thou canst do mischief;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whereas the goodness of God\r\n            *  endureth yet daily?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy tongue imagineth wickedness,\r\n            *  and with lies thou cuttest like a sharp razor.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast loved unrighteousness more than goodness,\r\n            *  and falsehood more than righteousness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast loved to speak all words that may do hurt,\r\n            *  O thou false tongue.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore shall God destroy thee for ever;\r\n            *  he shall take thee, and pluck thee out of thy dwelling, and root thee out of the land of the living.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The righteous also shall see this, and fear,\r\n            *  and shall laugh him to scorn:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lo, this is the man that took not God for his strength;\r\n            *  but trusted unto the multitude of his riches, and strengthened himself in his wickedness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for me, I am like a green olive-tree in the house of God;\r\n            *  my trust is in the tender mercy of God for ever and ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will alway give thanks unto thee for that thou hast done;\r\n            *  and I will hope in thy Name, for thy saints like it well.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(11, '11', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 11, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 56\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifty-Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Miserere mei, Deus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BE merciful unto me, O God, for man goeth about to devour me;\r\n            * he is daily fighting, and troubling me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Mine enemies are daily at hand to swallow me up;\r\n            * for they be many that fight against me, O thou Most Highest.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Nevertheless, though I am sometime afraid,\r\n            * yet put I my trust in thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will praise God, because of his word:\r\n            * I have put my trust in God, and will not fear what flesh can do unto me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They daily mistake my words;\r\n            * all that they imagine is to do me evil.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They hold all together, and keep themselves close,\r\n            * and mark my steps, when they lay wait for my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Shall they escape for their wickedness?\r\n            * thou, O God, in thy displeasure shalt cast them down.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou tellest my wanderings; put my tears into thy bottle:\r\n            * are not these things noted in thy book?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whensoever I call upon thee, then shall mine enemies be put to flight:\r\n            * this I know; for God is on my side.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In God\'s word will I rejoice;\r\n            * in the LORD\'S word will I comfort me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, in God have I put my trust;\r\n            * I will not be afraid what man can do unto me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Unto thee, O God, will I pay my vows;\r\n            * unto thee will I give thanks.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou hast delivered my soul from death, and my feet from falling,\r\n            * that I may walk before God in the light of the living.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 57\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifty-Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Miserere mei, Deus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BE merciful unto me, O God, be merciful unto me; for my soul trusteth in thee;\r\n            * and under the shadow of thy wings shall be my refuge, until this tyranny be overpast.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will call unto the Most High God,\r\n            * even unto the God that shall perform the cause which I have in hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall send from heaven,\r\n            * and save me from the reproof of him that would eat me up.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God shall send forth his mercy and truth:\r\n            * my soul is among lions;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And I lie even among the children of men, that are set on fire,\r\n            * whose teeth are spears and arrows, and their tongue a sharp sword.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Set up thyself, O God, above the heavens;\r\n            * and thy glory above all the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have laid a net for my feet, and pressed down my soul;\r\n            *  they have digged a pit before me, and are fallen into the midst of it themselves.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My heart is fixed, O God, my heart is fixed;\r\n            *  I will sing and give praise.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Awake up, my glory; awake, lute and harp:\r\n            * I myself will awake right early.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will give thanks unto thee, O Lord, among the peoples;\r\n            * and I will sing unto thee among the nations.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the greatness of thy mercy reacheth unto the heavens,\r\n            * and thy truth unto the clouds.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Set up thyself, O God, above the heavens;\r\n            * and thy glory above all the earth.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 58\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifty-Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Si vere utique.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            ARE your minds set upon righteousness, O ye congregation?\r\n            *  and do ye judge the thing that is right, O ye sons of men?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, ye imagine mischief in your heart upon the earth,\r\n            *  and your hands deal with wickedness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The ungodly are froward, even from their mother\'s womb;\r\n            *  as soon as they are born, they go astray, and speak lies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They are as venomous as the poison of a serpent,\r\n            *  even like the deaf adder, that stoppeth her ears;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Which refuseth to hear the voice of the charmer,\r\n            *  charm he never so wisely.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Break their teeth, O God, in their mouths;\r\n            *  smite the jaw-bones of the lions, O LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them fall away like water that runneth apace;\r\n            *  when they shoot their arrows, let them be rooted out.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them consume away like a snail, and be like the untimely fruit of a woman;\r\n            *  and let them not see the sun.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Or ever your pots be made hot with thorns,\r\n            *  he shall take them away with a whirlwind, the green and the burning alike.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The righteous shall rejoice when he seeth the vengeance;\r\n            *  he shall wash his footsteps in the blood of the ungodly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So that a man shall say, Verily there is a reward for the righteous;\r\n            *  doubtless there is a God that judgeth the earth.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(12, '12', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 12, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 62\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixty-Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Nonne Deo?</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            MY soul truly waiteth still upon God; * for of him cometh my salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He verily is my strength and my salvation; * he is my defence, so that I shall not greatly fall.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            How long will ye imagine mischief against every man? * Ye shall be slain all the sort of you; yea, as a\r\n            tottering wall shall ye be, and like a broken hedge.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their device is only how to put him out whom God will exalt;\r\n            * their delight is in lies; they give good words with their mouth, but curse with their heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Nevertheless, my soul, wait thou still upon God; * for my hope is in him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He truly is my strength and my salvation; * he is my defence, so that I shall not fall.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In God is my health and my glory; * the rock of my might; and in God is my trust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O put your trust in him alway, ye people; * pour out your hearts before him, for God is our hope.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for the children of men, they are but vanity; the children of men are deceitful; * upon the weights they\r\n            are altogether lighter than vanity itself.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O trust not in wrong and robbery; give not yourselves unto vanity: * if riches increase, set not your heart\r\n            upon them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God spake once, and twice I have also heard the same, * that power belongeth unto God;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And that thou, Lord, art merciful; * for thou rewardest every man according to his work.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 63\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixty-Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, Deus Meus.</i></h4></div>\r\n<hr/>\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O GOD, thou art my God; * early will I seek thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul thirsteth for thee, my flesh also longeth after thee, * in a barren and dry land where no water\r\n            is.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thus have I looked for thee in the sanctuary, * that I might behold thy power and glory.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thy loving-kindness is better than the life itself:  * my lips shall praise thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As long as I live will I magnify thee in this manner, * and lift up my hands in thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul shall be satisfied, even as it were with marrow and fatness, * when my mouth praiseth thee with\r\n            joyful lips.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Have I not remembered thee in my bed, * and thought upon thee when I was waking?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Because thou hast been my helper; * therefore under the shadow of thy wings will I rejoice.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul hangeth upon thee; * thy right hand hath upholden me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            These also that seek the hurt of my soul, * they shall go under the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them fall upon the edge of the sword, * that they may be a portion for foxes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But the King shall rejoice in God; all they also that swear by him shall be commended; * for the mouth of\r\n            them that speak lies shall be stopped.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 64\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixty-Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exaudi, Deus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HEAR my voice, O God, in my prayer;\r\n            * preserve my life from fear of the enemy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hide me from the gathering together of the froward,\r\n            * and from the insurrection of wicked doers;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who have whet their tongue like a sword,\r\n            * and shoot out their arrows, even bitter words;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That they may privily shoot at him that is perfect:\r\n            * suddenly do they hit him, and fear not.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They encourage themselves in mischief,\r\n            * and commune among themselves, how they may lay snares; and say, that no man shall see them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They imagine wickedness, and practise it;\r\n            * that they  keep secret among themselves, every man in the deep of his heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But God shall suddenly shoot at them with a swift arrow,\r\n            * that they shall be wounded.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, their own tongues shall make them fall;\r\n            * insomuch that whoso seeth them shall laugh them to scorn.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And all men that see it shall say, This hath God done;\r\n            * for they shall perceive that it is his work.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The righteous shall rejoice in the LORD, and put his trust in him;\r\n            * and all they that are true of heart shall be glad.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(13, '13', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 13, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 68\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixty-Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exsurgat Deus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        LET God arise, and let his enemies be scattered;\r\n        * let them also that hate him flee before him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Like as the smoke vanisheth, so shalt thou\r\n        drive them away;\r\n        * and like as wax melteth at the fire, so let the\r\n        ungodly perish at the presence of God.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But let the righteous be glad, and rejoice before God;\r\n        * let them also be merry and joyful.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O sing unto God, and sing praises unto his Name;\r\n        magnify him that rideth upon the heavens;\r\n        * praise him in his Name JAH, and rejoice before him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He is a Father of the fatherless, and defendeth the\r\n        cause of the widows;\r\n        * even God in his holy habitation.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He is the God that maketh men to be of one mind in an\r\n        house, and bringeth the prisoners out of captivity;\r\n        * but letteth the runagates continue in scarceness.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O God, when thou wentest forth before the people;\r\n        * when thou wentest through the wilderness,<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The earth shook, and the heavens dropped at the\r\n        presence of God;\r\n        * even as Sinai also was moved at the presence of God,\r\n        who is the God of Israel.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou, O God, sentest a gracious rain upon thine\r\n        inheritance,\r\n        * and refreshedst it when it was weary.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thy congregation shall dwell therein;\r\n        * for thou, O God, hast of thy goodness prepared for\r\n        the poor.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The Lord gave the word;\r\n        * great was the company of women that bare the tidings.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Kings with their armies did flee, and were discomfited,\r\n        * and they of the household divided the spoil.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Though ye have lain among the sheep-folds, yet shall\r\n        ye be as the wings of a dove\r\n        * that is covered with silver wings, and her feathers\r\n        like gold.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        When the Almighty scattered kings for their sake,\r\n        * then were they as white as snow in Salmon.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        As the hill of Bashan, so is God\'s hill;\r\n        * even an high hill, as the hill of Bashan.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Why mock ye so, ye high hills? this is God\'s hill,\r\n        in the which it pleaseth him to dwell;\r\n        * yea, the LORD will abide in it for ever.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The chariots of God are twenty thousand, even\r\n        thousands of angels;\r\n        * and the Lord is among them as in the holy place of Sinai.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou art gone up on high, thou hast led captivity\r\n        captive, and received gifts from men;\r\n        * yea, even from thine enemies, that the LORD God\r\n        might dwell among them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Praised be the Lord daily,\r\n        * even the God who helpeth us, and poureth his benefits\r\n        upon us.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 20 </i>He is our God, even the God of whom cometh salvation:\r\n        * GOD is the Lord, by whom we escape death.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 21 </i>God shall wound the head of his enemies,\r\n        * and the hairy scalp of such a one as goeth on still in\r\n        his wickedness.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The Lord hath said, I will bring my people again,\r\n        as I did from Bashan;\r\n        * mine own will I bring again, as I did sometime from\r\n        the deep of the sea.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        That thy foot may be dipped in the blood of\r\n        thine enemies,\r\n        * and that the tongue of thy dogs may be red through\r\n        the same.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        It is well seen, O God, how thou goest;\r\n        * how thou, my God and King, goest in the sanctuary.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The singers go before, the minstrels follow after,\r\n        * in the midst of the damsels playing with the timbrels.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Give thanks unto God the Lord in the congregation,\r\n        * ye that are of the fountain of Israel.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        There is little Benjamin their ruler, and the princes of\r\n        Judah their council;\r\n        * the princes of Zebulon, and the princes of Naphthali.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thy God hath sent forth strength for thee;\r\n        * stablish the thing, O God, that thou hast wrought in us,<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For thy temple\'s sake at Jerusalem;\r\n        * so shall kings bring presents unto thee.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Rebuke thou the dragon and the bull, with the leaders of\r\n        the heathen, so that they humbly bring pieces of silver;\r\n        * scatter thou the peoples that delight in war;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Then shall the princes come out of Egypt;\r\n        * the Morians\' land shall soon stretch out her hands\r\n        unto God.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Sing unto God, O ye kingdoms of the earth;\r\n        * O sing praises unto the Lord;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Who sitteth in the heavens over all, from the beginning:\r\n        * lo, he doth send out his voice; yea, and that a\r\n        mighty voice.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Ascribe ye the power to God over Israel;\r\n        * his worship and strength is in the clouds.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O God, wonderful art thou in thy holy places:\r\n        * even the God of Israel, he will give strength and\r\n        power unto his people.  Blessed be God.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(14, '14', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 14, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 71\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventy-First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>In te, Domine, speravi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            IN thee, O LORD, have I put my trust; let me never be put to confusion,\r\n            *  but rid me and deliver me in thy righteousness; incline thine ear unto me, and save me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Be thou my stronghold, whereunto I may alway resort:\r\n            *  thou hast promised to help me, for thou art my house of defence, and my castle.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Deliver me, O my God, out of the hand of the ungodly,\r\n            *  out of the hand of the unrighteous and cruel man.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou, O Lord GOD, art the thing that I long for:\r\n            * thou art my hope, even from my youth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Through thee have I been holden up ever since I was born:\r\n            *  thou art he that took me out of my mother\'s womb: my praise shall be alway of thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I am become as it were a monster unto many,\r\n            *  but my sure trust is in thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let my mouth be filled with thy praise,\r\n            *  that I may sing of thy glory and honour all the day long.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Cast me not away in the time of age;\r\n            *  forsake me not when my strength faileth me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For mine enemies speak against me;\r\n            *  and they that lay wait for my soul take their counsel together, saying,<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God hath forsaken him;\r\n            *  persecute him, and take him, for there is none to deliver him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Go not far from me, O God;\r\n            *  my God, haste thee to help me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them be confounded and perish that are against my soul;\r\n            *  let them be covered with shame and dishonour that seek to do me evil.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for me, I will patiently abide alway,\r\n            *  and will praise thee more and more.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My mouth shall daily speak of thy righteousness and salvation;\r\n            *  for I know no end thereof.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will go forth in the strength of the Lord GOD,\r\n            *and will make mention of thy righteousness only.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou, O God, hast taught me from my youth up until now;\r\n            *  therefore will I tell of thy wondrous works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Forsake me not, O God, in mine old age, when I am gray-headed,\r\n            *  until I have showed thy strength unto this generation, and thy power to all them that are yet for to come.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy righteousness, O God, is very high,\r\n            *  and great things are they that thou hast done: O God, who is like unto thee!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O what great troubles and adversities hast thou showed me ! and yet didst thou turn and refresh me;\r\n            *  yea, and broughtest me from the deep of the earth again.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast brought me to great honour,\r\n            *  and comforted me on every side:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore will I praise thee, and thy faithfulness, O God, playing upon an instrument of music:\r\n            *  unto thee will I sing upon the harp, O thou Holy One of Israel.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My lips will be glad when I sing unto thee;\r\n            *  and so will my soul whom thou hast delivered.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My tongue also shall talk of thy righteousness all the day long;\r\n            *  for they are confounded and brought unto shame that seek to do me evil.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 72\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventy-Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, judicium.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            GIVE the King thy judgments, O God,\r\n            * and thy righteousness unto the King\'s son.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then shall he judge thy people according unto right,\r\n            * and defend the poor.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The mountains also shall bring peace,\r\n            * and the little hills righteousness unto the people.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall keep the simple folk by their right,\r\n            * defend the children of the poor, and punish the\r\n            wrong doer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They shall fear thee, as long as the sun and\r\n            moon endureth,\r\n            * from one generation to another.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall come down like the rain upon the mown grass,\r\n            * even as the drops that water the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In his time shall the righteous flourish;\r\n            * yea, and abundance of peace, so long as the\r\n            moon endureth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His dominion shall be also from the one sea to the other,\r\n            * and from the River unto the world\'s end.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They that dwell in the wilderness shall kneel before him;\r\n            * his enemies shall lick the dust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The kings of Tarshish and of the isles shall\r\n            give presents;\r\n            * the kings of Arabia and Saba shall bring gifts.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All kings shall fall down before him;\r\n            * all nations shall do him service.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he shall deliver the poor when he crieth;\r\n            * the needy also, and him that hath no helper.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall be favourable to the simple and needy,\r\n            * and shall preserve the souls of the poor.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall deliver their souls from falsehood and wrong;\r\n            * and dear shall their blood be in his sight.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall live, and unto him shall be given of the\r\n            gold of Arabia;\r\n            * prayer shall be made ever unto him, and daily shall\r\n            he be praised.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There shall be an heap of corn in the earth, high upon\r\n            the hills ; the fruit thereof shall shake like Lebanon:\r\n            * and they of the city shall flourish like grass upon\r\n            the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His Name shall endure for ever; his Name shall remain\r\n            under the sun among the posterities, which shall be\r\n            blessed in him;\r\n            * and all the nations shall praise him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed be the LORD God, even the God of Israel,\r\n            * which only doeth wondrous things;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And blessed be the Name of his majesty for ever:\r\n            * and all the earth shall be filled with his majesty. Amen, Amen.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(15, '15', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 15, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 75\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventy-Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Confitebimur tibi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            UNTO thee, O God, do we give thanks;\r\n            *  yea, unto thee do we give thanks.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy Name also is so nigh;\r\n            *  and that do thy wondrous works declare.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In the appointed time, saith God,\r\n            *  I shall judge according unto right.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The earth is weak, and all the inhabiters thereof:\r\n            *  I bear up the pillars of it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I said unto the fools, Deal not so madly;\r\n            *  and to the ungodly, Set not up your horn.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Set not up your horn on high,\r\n            *  and speak not with a stiff neck.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For promotion cometh neither from the east, nor from the west,\r\n            *  nor yet from the south.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And why? God is the Judge;\r\n            *  he putteth down one, and setteth up another.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For in the hand of the LORD there is a cup, and the wine is red;\r\n            *  it is full mixt, and he poureth out of the same.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for the dregs thereof,\r\n            *  all the ungodly of the earth shall drink them, and suck them out.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But I will talk of the God of Jacob,\r\n            *  and praise him for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All the horns of the ungodly also will I break,\r\n            *  and the horns of the righteous shall be exalted.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 76\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventy-Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Notus in Judaea.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            IN Judah is God known;\r\n            * his Name is great in Israel.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            At Salem is his tabernacle,\r\n            *  and his dwelling in Sion.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There brake he the arrows of the bow,\r\n            *  the shield, the sword, and the battle.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou art glorious in might,\r\n            *  when thou comest from the hills of the robbers.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The proud are robbed, they have slept their sleep;\r\n            *  and all the men whose hands were mighty have found nothing.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            At thy rebuke, O God of Jacob,\r\n            *  both the chariot and horse are fallen.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou, even thou art to be feared;\r\n            *  and who may stand in thy sight when thou art angry?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou didst cause thy judgment to be heard from heaven;\r\n            *  the earth trembled, and was still,<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When God arose to judgment,\r\n            *  and to help all the meek upon earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The fierceness of man shall turn to thy praise;\r\n            *  and the fierceness of them shalt thou refrain.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Promise unto the LORD your God, and keep it, all ye that are round about him;\r\n            *  bring presents unto him that ought to be feared.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall refrain the spirit of princes,\r\n            *  and is wonderful among the kings of the earth.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 77\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventy-Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Voce mea ad Dominum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        I WILL cry unto God with my voice;\r\n        * even unto God will I cry with my voice, and he shall hearken unto me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        In the time of my trouble I sought the Lord:\r\n        * I stretched forth my hands unto him, and ceased not in the night season; my soul refused comfort.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        When I am in heaviness, I will think upon God;\r\n        * when my heart is vexed, I will complain.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou holdest mine eyes waking:\r\n        * I am so feeble that I cannot speak.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I have considered the days of old,\r\n        * and the years that are past.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I call to remembrance my song,\r\n        * and in the night I commune with mine own heart, and search out my spirit.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Will the Lord absent himself for ever?\r\n        * and will he be no more intreated?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Is his mercy clean gone for ever?\r\n        * and is his promise come utterly to an end for evermore?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Hath God forgotten to be gracious?\r\n        * and will he shut up his loving-kindness in displeasure?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And I said, It is mine own infirmity;\r\n        * but I will remember the years of the right hand of the Most Highest.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 11 </i>I will remember the works of the LORD,\r\n        * and call to mind thy wonders of old time.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will think also of all thy works,\r\n        * and my talking shall be of thy doings.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thy way, O God, is holy:\r\n        * who is so great a God as our God?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou art the God that doest wonders,\r\n        * and hast declared thy power among the peoples.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast mightily delivered thy people,\r\n        * even the sons of Jacob and Joseph.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The waters saw thee, O God, the waters saw thee, and were afraid;\r\n        * the depths also were troubled.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The clouds poured out water, the air thundered,\r\n        * and thine arrows went abroad.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The voice of thy thunder was heard round about:\r\n        * the lightnings shone upon the ground; the earth was moved, and shook withal.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thy way is in the sea, and thy paths in the great waters,\r\n        * and thy footsteps are not known.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou leddest thy people like sheep,\r\n        * by the hand of Moses and Aaron.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(16, '16', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 16, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 79\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventy-Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, venerunt.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O GOD, the heathen are come into thine inheritance;\r\n            * thy holy temple have they defiled, and made Jerusalem an heap of stones.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The dead bodies of thy servants have they given to be meat unto the fowls of the air,\r\n            * and the flesh of thy saints unto the beasts of the land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their blood have they shed like water on every side of Jerusalem,\r\n            * and there was no man to bury them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            We are become an open shame to our enemies,\r\n            * a very scorn and derision unto them that are round about us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            LORD, how long wilt thou be angry?\r\n            * shall thy jealousy burn like fire for ever?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Pour out thine indignation upon the heathen that have not known thee;\r\n            * and upon the kingdoms that have not called upon thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For they have devoured Jacob,\r\n            * and laid waste his dwelling-place.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O remember not our old sins, but have mercy upon us, and that soon;\r\n            * for we are come to great misery.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Help us, O God of our salvation, for the glory of thy Name:\r\n            * O deliver us, and be merciful unto our sins, for thy Name\'s sake.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wherefore do the heathen say,\r\n            * Where is now their God?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let the vengeance of thy servants\' blood that is shed,\r\n            * be openly showed upon the heathen, in our sight.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let the sorrowful sighing of the prisoners come before thee;\r\n            * according to the greatness of thy power, preserve thou those that are appointed to die.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And for the blasphemy wherewith our neighbours have blasphemed thee,\r\n            * reward thou them, O Lord, seven-fold into their bosom.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So we, that are thy people, and sheep of thy pasture, shall give thee thanks for ever,\r\n            * and will alway be showing forth thy praise from generation to generation.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 80\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eightieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Qui regis Israel.</i></h4></div>\r\n<hr/>\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HEAR, O thou Shepherd of Israel, thou that leadest Joseph like a flock; * show thyself also, thou that sittest upon the Cherubim.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Before Ephraim, Benjamin, and Manasseh, * stir up thy strength, and come and help us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn us again, O God; * show the light of thy countenance, and we shall be whole.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O LORD God of hosts, * how long wilt thou be angry with thy people that prayeth?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou feedest them with the bread of tears, * and givest them plenteousness of tears to drink.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast made us a very strife unto our neighbours, * and our enemies laugh us to scorn.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn us again, thou God of hosts; * show the light of thy countenance, and we shall be whole.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast brought a vine out of Egypt; * thou hast cast out the heathen, and planted it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou madest room for it; * and when it had taken root, it filled the land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The hills were covered with the shadow of it, * and the boughs thereof were like the goodly cedar-trees.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            She stretched out her branches unto the sea, * and her boughs unto the River.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Why hast thou then broken down her hedge, * that all they that go by pluck off her grapes?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The wild boar out of the wood doth root it up, * and the wild beasts of the field devour it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn thee again, thou God of hosts, look down from heaven, * behold, and visit this vine;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And the place of the vineyard that thy right hand hath planted, * and the branch that thou madest so strong for thyself.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            It is burnt with fire, and cut down; * and they shall perish at the rebuke of thy countenance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let thy hand be upon the man of thy right hand, * and upon the son of man, whom thou madest so strong for thine own self.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And so will not we go back from thee: * let us live, and we shall call upon thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn us again, O LORD God of hosts; * show the light of thy countenance, and we shall be whole.<br/>\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 81\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighty-First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exultate Deo.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n\r\n            SING we merrily unto God our strength; * make a cheerful noise unto the God of Jacob.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Take the psalm, bring hither the tabret, * the merry harp with the lute.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blow up the trumpet in the new moon, * even in the time appointed, and upon our solemn feast-day.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For this was made a statute for Israel, * and a law of the God of Jacob.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            This he ordained in Joseph for a testimony, * when he came out of the land of Egypt, and had heard a strange language.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I eased his shoulder from the burden, * and his hands were delivered from making the pots.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou calledst upon me in troubles, and I delivered thee; * and heard thee what time as the storm fell upon thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I proved thee also * at the waters of strife.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hear, O my people; and I will assure thee, O Israel, * if thou wilt hearken unto me,<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There shall no strange god be in thee, * neither shalt thou worship any other god.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I am the LORD thy God, who brought thee out of the land of Egypt: * open thy mouth wide, and I shall fill it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But my people would not hear my voice; * and Israel would not obey me;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So I gave them up unto their own hearts\' lusts, * and let them follow their own imaginations.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O that my people would have hearkened unto me! * for if Israel had walked in my ways,<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I should soon have put down their enemies, * and turned my hand against their adversaries.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The haters of the LORD should have submitted themselves unto him; * but their time should have endured forever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I would have fed them also with the finest wheat-flour; * and with honey out of the stony rock would I have satisfied thee.<br/>\r\n\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(17, '17', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 17, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 86\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighty-Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Inclina, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BOW down thine ear, O LORD, and hear me;\r\n            * for I am poor, and in misery.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Preserve thou my soul, for I am holy:\r\n            * my God, save thy servant that putteth his trust in thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Be merciful unto me, O Lord;\r\n            * for I will call daily upon thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Comfort the soul of thy servant;\r\n            * for unto thee, O Lord, do I lift up my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou, Lord, art good and gracious,\r\n            * and of great mercy unto all them that call upon thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Give ear, LORD, unto my prayer,\r\n            * and ponder the voice of my humble desires.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In the time of my trouble I will call upon thee;\r\n            * for thou hearest me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Among the gods there is none like unto thee, O Lord;\r\n            * there is not one that can do as thou doest.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All nations whom thou hast made shall come and worship thee, O Lord;\r\n            * and shall glorify thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou art great, and doest wondrous things:\r\n            * thou art God alone.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Teach me thy way, O LORD, and I will walk in thy truth:\r\n            * O knit my heart unto thee, that I may fear thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will thank thee, O Lord my God, with all my heart;\r\n            * and will praise thy Name for evermore.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For great is thy mercy toward me;\r\n            * and thou hast delivered my soul from the nethermost hell.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O God, the proud are risen against me;\r\n            * and the congregations of violent men have sought after my soul,\r\n            and have not set thee before their eyes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But thou, O Lord God, art full of compassion and mercy,\r\n            * long-suffering, plenteous in goodness and truth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O turn thee then unto me, and have mercy upon me;\r\n            * give thy strength unto thy servant, and help the son of thine handmaid.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Show some token upon me for good; that they who hate me may see it, and be ashamed,\r\n            * because thou, LORD, hast holpen me, and comforted me.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 87\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighty-Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Fundamenta ejus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HER foundations are upon the holy hills:\r\n            * the LORD loveth the gates of Sion more than\r\n            all the dwellings of Jacob.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Very excellent things are spoken of thee,\r\n            * thou city of God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will make mention of Egypt and Babylon,\r\n            * among them that know me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, Philistia also; and Tyre, with Ethiopia;\r\n            * lo, in Sion were they born.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, of Sion it shall be reported, this one and that\r\n            one were born in her;\r\n            * and the Most High shall stablish her.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD shall record it, when he writeth up\r\n            the peoples;\r\n            * lo, in Sion were they born.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The singers also and trumpeters shall make answer:\r\n            * All my fresh springs are in thee.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 88\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighty-Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, Deus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O LORD God of my salvation, I have cried day and night before thee:\r\n            * O let my prayer enter into thy presence, incline thine ear unto my calling;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For my soul is full of trouble,\r\n            * and my life draweth nigh unto the grave.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I am counted as one of them that go down into the  pit,\r\n            * and I am even as a man that hath no strength;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Cast off among the dead, like unto them that are slain, and lie in the grave,\r\n            * who are out of remembrance, and are cut away from thy hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast laid me in the lowest pit,\r\n            * in a place of darkness, and in the deep.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thine indignation lieth hard upon me,\r\n            * and thou hast vexed me with all thy storms.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast put away mine acquaintance far from me,\r\n            * and made me to be abhorred of them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I am so fast in prison\r\n            * that I cannot get forth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My sight faileth for very trouble;\r\n            * LORD, I have called daily upon thee, I have stretched forth my hands unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Dost thou show wonders among the dead?\r\n            * or shall the dead rise up again, and praise thee?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Shall thy loving-kindness be showed in the grave?\r\n            * or thy faithfulness in destruction?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Shall thy wondrous works be known in the dark?\r\n            * and thy righteousness in the land where all things are forgotten?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Unto thee have I cried, O LORD;\r\n            * and early shall my prayer come before thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            LORD, why abhorrest thou my soul,\r\n            * and hidest thou thy face from me?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I am in misery, and like unto him that is at the point to die;\r\n            * even from my youth up, thy terrors have I suffered with a troubled mind.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy wrathful displeasure goeth over me,\r\n            * and the fear of thee hath undone me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They came round about me daily like water,\r\n            * and compassed me together on every side.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My lovers and friends hast thou put away from me,\r\n            * and hid mine acquaintance out of my sight.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(18, '18', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 18, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 90\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninetieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, refugium.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            LORD, thou hast been our refuge,\r\n            * from one generation to another.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Before the mountains were brought forth,\r\n            or ever the earth and the world were made,\r\n            * thou art God from everlasting, and world without end.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou turnest man to destruction;\r\n            * again thou sayest, Come again, ye children of men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For a thousand years in thy sight are but as yesterday\r\n            when it is past,\r\n            * and as a watch in the night.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As soon as thou scatterest them they are even as a sleep;\r\n            * and fade away suddenly like the grass.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In the morning it is green, and groweth up;\r\n            * but in the evening it is cut down, dried up, and withered.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For we consume away in thy displeasure,\r\n            * and are afraid at thy wrathful indignation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast set our misdeeds before thee;\r\n            * and our secret sins in the light of thy countenance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For when thou art angry all our days are gone:\r\n            * we bring our years to an end, as it were a tale that is told.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The days of our age are threescore years and ten;\r\n            and  though men be so strong that they come to\r\n            fourscore years,\r\n            * yet is their strength then but labour and sorrow;\r\n            so soon passeth it away, and we are gone.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But who regardeth the power of thy wrath?\r\n            * or feareth aright thy indignation?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So teach us to number our days,\r\n            * that we may apply our hearts unto wisdom.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn thee again, O LORD, at the last,\r\n            * and be gracious unto thy servants.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O satisfy us with thy mercy, and that soon:\r\n            * so shall we rejoice and be glad all the days of our life.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Comfort us again now after the time that thou hast\r\n            plagued us;\r\n            * and for the years wherein we have suffered adversity.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Show thy servants thy work,\r\n            * and their children thy glory.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And the glorious majesty of the LORD our God\r\n            be upon us:\r\n            * prosper thou the work of our hands upon us;\r\n            O prosper thou our handy-work.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 91\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninety-First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Qui habitat.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            WHOSO dwelleth under the defence of the Most High,\r\n            * shall abide under the shadow of the Almighty.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will say unto the LORD, Thou art my hope,\r\n            and my stronghold;\r\n            * my God, in him will I trust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he shall deliver thee from the snare of the hunter,\r\n            * and from the noisome pestilence.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall defend thee under his wings, and thou shalt be\r\n            safe under his feathers;\r\n            * his faithfulness and truth shall be thy shield and buckler.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt not be afraid for any terror by night,\r\n            * nor for the arrow that flieth by day;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the pestilence that walketh in darkness,\r\n            * nor for the sickness that destroyeth in the noon-day.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            A thousand shall fall beside thee, and ten thousand at\r\n            thy right hand;\r\n            * but it shall not come nigh thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, with thine eyes shalt thou behold,\r\n            * and see the reward of the ungodly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou, LORD, art my hope;\r\n            * thou hast set thine house of defence very high.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There shall no evil happen unto thee,\r\n            * neither shall any plague come nigh thy dwelling.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he shall give his angels charge over thee,\r\n            * to keep thee in all thy ways.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They shall bear thee in their hands,\r\n            * that thou hurt not thy foot against a stone.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt go upon the lion and adder:\r\n            * the young lion and the dragon shalt thou tread under\r\n            thy feet.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Because he hath set his love upon me, therefore\r\n            will I deliver him;\r\n            * I will set him up, because he hath known my Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall call upon me, and I will hear him;\r\n            * yea, I am with him in trouble; I will deliver him,\r\n            and bring him to honour.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            With long life will I satisfy him,\r\n            * and show him my salvation.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 92\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninety-Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Bonum est confiteri.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            IT is a good thing to give thanks unto the LORD,\r\n            * and to sing praises unto thy Name, O Most Highest;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            To tell of thy loving-kindness early in the morning,\r\n            * and of thy truth in the night season;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Upon an instrument of ten strings, and upon the lute;\r\n            * upon a loud instrument, and upon the harp.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou, LORD, hast made me glad through thy works;\r\n            * and I will rejoice in giving praise for the operations\r\n            of thy hands.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O LORD, how glorious are thy works!\r\n            * thy thoughts are very deep.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            An unwise man doth not well consider this,\r\n            * and a fool doth not understand it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When the ungodly are green as the grass, and when all\r\n            the workers of wickedness do flourish,\r\n            * then shall they be destroyed for ever; but thou, LORD,\r\n            art the Most Highest for evermore.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For lo, thine enemies, O LORD, lo, thine enemies\r\n            shall perish;\r\n            * and all the workers of wickedness shall be destroyed.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But my horn shall be exalted like the horn of an unicorn;\r\n            * for I am anointed with fresh oil.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Mine eye also shall see his lust of mine enemies,\r\n            * and mine ear shall hear his desire of the wicked that arise\r\n            up against me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The righteous shall flourish like a palm-tree,\r\n            * and shall spread abroad like a cedar in Lebanon.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Such as are planted in the house of the LORD,\r\n            * shall flourish in the courts of the house of our God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They also shall bring forth more fruit in their age,\r\n            * and shall be fat and well-liking;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That they may show how true the LORD my strength is,\r\n            * and that there is no unrighteousness in him.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(19, '19', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 19, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 95\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninety-Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Venite, exultemus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O COME, let us sing unto the LORD;\r\n            * let us heartily rejoice in the strength of our salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let us come before his presence with thanksgiving;\r\n            * and show ourselves glad in him with psalms.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the LORD is a great God;\r\n            * and a great King above all gods.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In his hand are all the corners of the earth;\r\n            * and the strength of the hills is his also.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The sea is his, and he made it;\r\n            * and his hands prepared the dry land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O come, let us worship and fall down,\r\n            * and kneel before the LORD our Maker.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he is the Lord our God;\r\n            * and we are the people of his pasture, and the sheep of his hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            To-day if ye will hear his voice, harden not your hearts\r\n            * as in the provocation, and as in the day of temptation in the wilderness;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When your fathers tempted me,\r\n            * proved me, and saw my works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Forty years long was I grieved with this generation, and said,\r\n            * It is a people that do err in their hearts, for they have not known my ways:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Unto whom I sware in my wrath,\r\n            * that they should not enter into my rest.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 96\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninety-Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Cantate Domino.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O SING unto the LORD a new song;\r\n            * sing unto the LORD, all the whole earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Sing unto the LORD, and praise his Name;\r\n            * be telling of his salvation from day to day.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Declare his honour unto the heathen,\r\n            * and his wonders unto all peoples.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the LORD is great, and cannot worthily be praised;\r\n            * he is more to be feared than all gods.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for all the gods of the heathen, they are but idols;\r\n            * but it is the LORD that made the heavens.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Glory and worship are before him;\r\n            * power and honour are in his sanctuary.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ascribe unto the LORD, O ye kindreds of the peoples,\r\n            * ascribe unto the LORD worship and power.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ascribe unto the LORD the honour due unto his Name;\r\n            * bring presents, and come into his courts.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O worship the LORD in the beauty of holiness;\r\n            * let the whole earth stand in awe of him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Tell it out among the heathen, that the LORD is King,\r\n            and that it is he who hath made the round world so\r\n            fast that it cannot be moved;\r\n            * and how that he shall judge the peoples righteously.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the heavens rejoice, and let the earth be glad;\r\n            * let the sea make a noise, and all that therein is.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the field be joyful, and all that is in it;\r\n            * then shall all the trees of the wood rejoice\r\n            before the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he cometh, for he cometh to judge the earth;\r\n            * and with righteousness to judge the world,\r\n            and the peoples with his truth.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 97\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninety-Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dominus regnavit.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE LORD is King, the earth may be glad thereof;\r\n            * yea, the multitude of the isles may be glad thereof.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Clouds and darkness are round about him:\r\n            * righteousness and judgment are the habitation\r\n            of his seat.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There shall go a fire before him,\r\n            * and burn up his enemies on every side.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His lightnings gave shine unto the world:\r\n            * the earth saw it, and was afraid.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The hills melted like wax at the presence of the LORD;\r\n            * at the presence of the Lord of the whole earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The heavens have declared his righteousness,\r\n            * and all the peoples have seen his glory.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Confounded be all they that worship carved images, and\r\n            that delight in vain gods:\r\n            * worship him, all ye gods.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Sion heard of it, and rejoiced; and the daughters of\r\n            Judah were glad,\r\n            * because of thy judgments, O LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou, LORD, art higher than all that are in the earth:\r\n            * thou art exalted far above all gods.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O ye that love the LORD, see that ye hate the thing\r\n            which is evil:\r\n            * the Lord preserveth the souls of his saints; he shall\r\n            deliver them from the hand of the ungodly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There is sprung up a light for the righteous,\r\n            * and joyful gladness for such as are true-hearted.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Rejoice in the LORD, ye righteous;\r\n            * and give thanks for a remembrance of his holiness.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(20, '20', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 20, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 102\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, exaudi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        HEAR my prayer, O LORD,\r\n        * and let my crying come unto thee.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Hide not thy face from me in the time of my trouble;\r\n        * incline thine ear unto me when I call; O hear me,\r\n        and that right soon.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For my days are consumed away like smoke,\r\n        * and my bones are burnt up as it were a firebrand.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My heart is smitten down, and withered like grass;\r\n        * so that I forget to eat my bread.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For the voice of my groaning,\r\n        * my bones will scarce cleave to my flesh.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I am become like a pelican in the wilderness,\r\n        * and like an owl that is in the desert.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I have watched, and am even as it were a sparrow,\r\n        * that sitteth alone upon the housetop.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Mine enemies revile me all the day long;\r\n        * and they that are mad upon me are sworn together\r\n        against me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For I have eaten ashes as it were bread,\r\n        * and mingled my drink with weeping;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And that, because of thine indignation and wrath;\r\n        * for thou hast taken me up, and cast me down.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My days are gone like a shadow,\r\n        * and I am withered like grass.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But thou, O LORD, shalt endure for ever,\r\n        * and thy remembrance throughout all generations.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou shalt arise, and have mercy upon Sion;\r\n        * for it is time that thou have mercy upon her, yea,\r\n        the time is come.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And why? thy servants think upon her stones,\r\n        * and it pitieth them to see her in the dust.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 15 </i>The nations shall fear thy Name, O LORD;\r\n        * and all the kings of the earth thy majesty;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        When the LORD shall build up Sion,\r\n        * and when his glory shall appear;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        When he turneth him unto the prayer of the\r\n        poor destitute,\r\n        * and despiseth not their desire.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        This shall be written for those that come after,\r\n        * and the people which shall be born shall praise\r\n        the LORD.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For he hath looked down from his sanctuary;\r\n        * out of the heaven did the LORD behold the earth;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        That he might hear the mournings of such as are\r\n        in captivity,\r\n        * and deliver them that are appointed unto death;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        That they may declare the Name of the LORD in Sion,\r\n        * and his worship at Jerusalem;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        When the peoples are gathered together,\r\n        * and the kingdoms also, to serve the LORD.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He brought down my strength in my journey,\r\n        * and shortened my days.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But I said, O my God, take me not away in the midst\r\n        of mine age;\r\n        * as for thy years, they endure throughout all generations.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou, Lord, in the beginning hast laid the foundation\r\n        of the earth,\r\n        * and the heavens are the work of thy hands.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They shall perish, but thou shalt endure:\r\n        * they all shall wax old as doth a garment;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And as a vesture shalt thou change them, and they\r\n        shall be changed;\r\n        * but thou art the same, and thy years shall not fail.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The children of thy servants shall continue,\r\n        * and their seed shall stand fast in thy sight.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 103\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Benedic, anima mea.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            PRAISE the LORD, O my soul;\r\n            * and all that is within me, praise his holy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise the LORD, O my soul,\r\n            * and forget not all his benefits:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who forgiveth all thy sin,\r\n            * and healeth all thine infirmities;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who saveth thy life from destruction,\r\n            * and crowneth thee with mercy and loving-kindness;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who satisfieth thy mouth with good things,\r\n            * making thee young and lusty as an eagle.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD executeth righteousness and judgment\r\n            * for all them that are oppressed with wrong.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He showed his ways unto Moses,\r\n            * his works unto the children of Israel.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is full of compassion and mercy,\r\n            * long-suffering, and of great goodness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He will not alway be chiding;\r\n            * neither keepeth he his anger for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath not dealt with us after our sins;\r\n            * nor rewarded us according to our wickednesses.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For look how high the heaven is in comparison of\r\n            the earth;\r\n            * so great is his mercy also toward them that fear him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Look how wide also the east is from the west;\r\n            * so far hath he set our sins from us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, like as a father pitieth his own children;\r\n            * even so is the LORD merciful unto them that fear him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he knoweth whereof we are made;\r\n            * he remembereth that we are but dust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The days of man are but as grass;\r\n            * for he flourisheth as a flower of the field.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For as soon as the wind goeth over it, it is gone;\r\n            * and the place thereof shall know it no more.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But the merciful goodness of the LORD endureth\r\n            for ever and ever upon them that fear him;\r\n            * and his righteousness upon children\'s children;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Even upon such as keep his covenant,\r\n            * and think upon his commandments to do them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD hath prepared his seat in heaven,\r\n            * and his kingdom ruleth over all.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O praise the LORD, ye angels of his, ye that excel\r\n            in strength;\r\n            * ye that fulfil his commandment, and hearken unto\r\n            the voice of his word.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O praise the LORD, all ye his hosts;\r\n            * ye servants of his that do his pleasure.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O speak good of the LORD, all ye works of his,\r\n            in all places of his dominion:\r\n            * praise thou the LORD, O my soul.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(21, '21', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 21, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 105\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Confitemini Domino.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O GIVE thanks unto the LORD, and call upon his Name;\r\n            * tell the people what things he hath done.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let your songs be of him, and praise him;\r\n            * and let your talking be of all his wondrous works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Rejoice in his holy Name;\r\n            * let the heart of them rejoice that seek the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Seek the LORD and his strength;\r\n            * seek his face evermore.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Remember the marvellous works that he hath done;\r\n            * his wonders, and the judgments of his mouth;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O ye seed of Abraham his servant,\r\n            * ye children of Jacob his chosen.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He is the LORD our God;\r\n            * his judgments are in all the world.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath been alway mindful of his covenant and promise,\r\n            * that he made to a thousand generations;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Even the covenant that he made with Abraham;\r\n            * and the oath that he sware unto Isaac;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And appointed the same unto Jacob for a law,\r\n            * and to Israel for an everlasting testament;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Saying, Unto thee will I give the land of Canaan,\r\n            * the lot of your inheritance:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When there were yet but a few of them,\r\n            * and they strangers in the land;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            What time as they went from one nation to another,\r\n            * from one kingdom to another people;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He suffered no man to do them wrong,\r\n            * but reproved even kings for their sakes;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Touch not mine anointed,\r\n            * and do my prophets no harm.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Moreover, he called for a dearth upon the land,\r\n            * and destroyed all the provision of bread.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But he had sent a man before them,\r\n            * even Joseph, who was sold to be a bond-servant;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whose feet they hurt in the stocks;\r\n            * the iron entered into his soul;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Until the time came that his cause was known:\r\n            * the word of the LORD tried him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The king sent, and delivered him;\r\n            * the prince of the people let him go free.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He made him lord also of his house,\r\n            * and ruler of all his substance;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That he might inform his princes after his will,\r\n            * and teach his senators wisdom.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Israel also came into Egypt,\r\n            * and Jacob was a stranger in the land of Ham.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And he increased his people exceedingly,\r\n            * and made them stronger than their enemies;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whose heart turned so, that they hated his people,\r\n            * and dealt untruly with his servants.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then sent he Moses his servant,\r\n            * and Aaron whom he had chosen.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And these showed his tokens among them,\r\n            * and wonders in the land of Ham.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He sent darkness, and it was dark;\r\n            * and they were not obedient unto his word.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He turned their waters into blood,\r\n            * and slew their fish.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their land brought forth frogs;\r\n            * yea, even in their kings\' chambers.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He spake the word, and there came all manner of flies,\r\n            * and lice in all their quarters.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He gave them hailstones for rain;\r\n            * and flames of fire in their land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He smote their vines also and fig-trees;\r\n            * and destroyed the trees that were in their coasts.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He spake the word, and the grasshoppers came, and caterpillars innumerable,\r\n            * and did eat up all the grass in their land, and devoured the fruit of their ground.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He smote all the firstborn in their land;\r\n            * even the chief of all their strength.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He brought them forth also with silver and gold;\r\n            * there was not one feeble person among their tribes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Egypt was glad at their departing;\r\n            * for they were afraid of them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He spread out a cloud to be a covering,\r\n            * and fire to give light in the night season.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            At their desire he brought quails;\r\n            * and he filled them with the bread of heaven.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He opened the rock of stone, and the waters flowed out,\r\n            * so that rivers ran in the dry places.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For why? he remembered his holy promise;\r\n            * and Abraham his servant.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And he brought forth his people with joy,\r\n            * and his chosen with gladness;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And gave them the lands of the heathen;\r\n            * and they took the labours of the people in possession;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That they might keep his statutes,\r\n            * and observe his laws.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(22, '22', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 22, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 107\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Confitemini Domino.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        O GIVE thanks unto the LORD, for he is gracious,\r\n        * and his mercy endureth for ever.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let them give thanks whom the LORD hath redeemed,\r\n        *  and delivered from the hand of the enemy;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And gathered them out of the lands, from the east, and from the west;\r\n        *  from the north, and from the south.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They went astray in the wilderness out of the way,\r\n        * and found no city to dwell in.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Hungry and thirsty,\r\n        *  their soul fainted in them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        So they cried unto the LORD in their trouble,\r\n        *  and he delivered them from their distress.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He led them forth by the right way,\r\n        *  that they might go to the city where they dwelt.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O that men would therefore praise the LORD for his goodness;\r\n        *  and declare the wonders that he doeth for the children of men!<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For he satisfieth the empty soul,\r\n        *  and filleth the hungry soul with goodness.\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 10 </i>Such as sit in darkness, and in the shadow of death,\r\n        * being fast bound in misery and iron;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Because they rebelled against the words of the Lord,\r\n        * and lightly regarded the counsel of the Most Highest;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He also brought down their heart through heaviness:\r\n        *  they fell down, and there was none to help them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        So when they cried unto the LORD in their trouble,\r\n        * he delivered them out of their distress.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For he brought them out of darkness, and out of the shadow of death,\r\n        *  and brake their bonds in sunder.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O that men would therefore praise the LORD for his goodness;\r\n        *  and declare the wonders that he doeth for the children of men !<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For he hath broken the gates of brass,\r\n        *  and smitten the bars of iron in sunder.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 17 </i>Foolish men are plagued for their offence,\r\n        *  and because of their wickedness.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Their soul abhorred all manner of meat,\r\n        *  and they were even hard at death\'s door.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        So when they cried unto the LORD in their trouble,\r\n        * he delivered them out of their distress.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He sent his word, and healed them;\r\n        *  and they were saved from their destruction.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O that men would therefore praise the LORD for his goodness;\r\n        *  and declare the wonders that he doeth for the children of men !<br/>\r\n        <small> &nbsp; <br/></small>\r\n        That they would offer unto him the sacrifice of thanksgiving,\r\n        *  and tell out his works with gladness!<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They that go down to the sea in ships,\r\n        *  and occupy their business in great waters;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        These men see the works of the LORD,\r\n        *  and his wonders in the deep.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For at his word the stormy wind ariseth,\r\n        *  which lifteth up the waves thereof.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They are carried up to the heaven, and down again to the deep;\r\n        *  their soul melteth away because of the trouble.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They reel to and fro, and stagger like a drunken man,\r\n        *  and are at their wit\'s end.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        SO when they cry unto the LORD in their trouble,\r\n        * he delivereth them out of their distress.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For he maketh the storm to cease,\r\n        *  so that the waves thereof are still.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Then are they glad, because they are at rest;\r\n        *  and so he bringeth them unto the haven where they would be.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O that men would therefore praise the LORD for his goodness;\r\n        *  and declare the wonders that he doeth for the children of men!<br/>\r\n        <small> &nbsp; <br/></small>\r\n        That they would exalt him also in the congregation of the people;\r\n        *  and praise him in the seat of the elders!<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 33 </i>He turneth the floods into a wilderness,\r\n        *  and drieth up the water-springs.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        A fruitful land maketh he barren,\r\n        *  for the wickedness of them that dwell therein.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Again, he maketh the wilderness a standing water,\r\n        * and water-springs of a dry ground.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And there he setteth the hungry,\r\n        *  that they may build them a city to dwell in;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        That they may sow their land, and plant vineyards,\r\n        * to yield them fruits of increase.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He blesseth them, so that they multiply exceedingly;\r\n        *  and suffereth not their cattle to decrease.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And again, when they are minished and brought low\r\n        *  through oppression, through any plague or trouble;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Though he suffer them to be evil entreated through tyrants,\r\n        *  and let them wander out of the way in the wilderness;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Yet helpeth he the poor out of misery,\r\n        *  and maketh him households like a flock of sheep.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The righteous will consider this, and rejoice;\r\n        *  and the mouth of all wickedness shall be stopped.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Whoso is wise, will ponder these things;\r\n        *  and they shall understand the loving-kindness of the LORD.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(23, '23', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 23, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 110\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Tenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dixit Dominus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE LORD said unto my Lord,\r\n            * Sit thou on my right hand, until I make thine enemies\r\n            thy footstool.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD shall send the rod of thy power out of  Sion:\r\n            * be thou ruler, even in the midst among thine enemies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In the day of thy power shall thy people offer themselves\r\n            willingly with an holy worship:\r\n            * thy young men come to thee as dew from the womb\r\n            of the morning.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD sware, and will not repent,\r\n            * Thou art a Priest for ever after the order of Melchizedek.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The Lord upon thy right hand\r\n            * shall wound even kings in the day of his wrath.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall judge among the heathen;\r\n            * he shall fill the places with the dead bodies, and smite\r\n            in sunder the heads over divers countries.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall drink of the brook in the way;\r\n            * therefore shall he lift up his head.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 111\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Eleventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Confitebor tibi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I WILL give thanks unto the LORD with my whole heart,\r\n            * secretly among the faithful, and in the congregation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The works of the LORD are great,\r\n            * sought out of all them that have pleasure therein.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His work is worthy to be praised and had in honour,\r\n            * and his righteousness endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The merciful and gracious LORD hath so done\r\n            his marvellous works,\r\n            * that they ought to be had in remembrance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath given meat unto them that fear him;\r\n            * he shall ever be mindful of his covenant.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath showed his people the power of his works,\r\n            * that he may give them the heritage of the heathen.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The works of his hands are verity and judgment;\r\n            * all his commandments are true.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They stand fast for ever and ever,\r\n            * and are done in truth and equity.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He sent redemption unto his people;\r\n            * he hath commanded his covenant for ever;\r\n            holy and reverend is his Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The fear of the LORD is the beginning of wisdom;\r\n            * a good understanding have all they that do thereafter;\r\n            his praise endureth for ever.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 112\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twelfth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Beatus vir.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BLESSED is the man that feareth the LORD;\r\n            * he hath great delight in his commandments.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His seed shall be mighty upon earth;\r\n            * the generation of the faithful shall be blessed.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Riches and plenteousness shall be in his house;\r\n            * and his righteousness endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Unto the godly there ariseth up light in the darkness;\r\n            * he is merciful, loving, and righteous.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            A good man is merciful, and lendeth;\r\n            * and will guide his words with discretion.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he shall never be moved:\r\n            * and the righteous shall be had in everlasting remembrance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He will not be afraid of any evil tidings;\r\n            * for his heart standeth fast, and believeth in the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His heart is stablished, and will not shrink,\r\n            * until he see his desire upon his enemies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath dispersed abroad, and given to the poor,\r\n            * and his righteousness remaineth for ever; his horn\r\n            shall be exalted with honour.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The ungodly shall see it, and it shall grieve him;\r\n            * he shall gnash with his teeth, and consume away; the\r\n            desire of the ungodly shall perish.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 113\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Laudate, pueri.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            PRAISE the LORD, ye servants;\r\n            * O praise the Name of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed be the Name of the LORD\r\n            * from this time forth for evermore.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD\'S Name is praised\r\n            * from the rising up of the sun unto the going down\r\n            of the same.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is high above all nations,\r\n            * and his glory above the heavens.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who is like unto the LORD our God, that hath his\r\n            dwelling so high,\r\n            * and yet humbleth himself to behold the things that are\r\n            in heaven and earth!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He taketh up the simple out of the dust,\r\n            * and lifteth the poor out of the mire;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That he may set him with the princes,\r\n            * even with the princes of his people.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He maketh the barren woman to keep house,\r\n            * and to be a joyful mother of children.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(24, '24', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 24, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 116\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Sixteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dilexi, quoniam.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            MY delight is in the LORD;\r\n            *  because he hath heard the voice of my prayer;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Because he hath inclined his ear unto me;\r\n            *  therefore   will I call upon him as long as I live.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The snares of death compassed me round about,\r\n            * and the pains of hell gat hold upon me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I found trouble and heaviness; then called I upon the Name of the LORD;\r\n            *  O LORD, I beseech thee, deliver my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Gracious is the LORD, and righteous;\r\n            *  yea, our God is merciful.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD preserveth the simple:\r\n            *  I was in misery, and he helped me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn again then unto thy rest, O my soul;\r\n            *  for the LORD hath rewarded thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And why? thou hast delivered my soul from death,\r\n            * mine eyes from tears, and my feet from falling.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will walk before the LORD\r\n            *  in the land of the living.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I believed, and therefore will I speak; but I was sore troubled:\r\n            *  I said in my haste, All men are liars.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            What reward shall I give unto the LORD\r\n            *  for all the benefits that he hath done unto me?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will receive the cup of salvation,\r\n            *  and call upon the Name of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will pay my vows now in the presence of all his people:\r\n            *  right dear in the sight of the LORD is the death of his saints.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, O LORD, how that I am thy servant;\r\n            *  I am thy servant, and the son of thine handmaid; thou hast broken my bonds in sunder.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will offer to thee the sacrifice of thanksgiving,\r\n            *  and will call upon the Name of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will pay my vows unto the LORD, in the sight of all his people,\r\n            *  in the courts of the LORD\'S house; even in the midst of thee, O Jerusalem.  Praise the LORD.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 117\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Seventeenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Laudate Dominum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O PRAISE the LORD, all ye nations;\r\n            * praise him, all ye peoples.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For his merciful kindness is ever more and more toward us;\r\n            * and the truth of the LORD endureth for ever.\r\n            Praise the LORD.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 118\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Eighteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Confitemini Domino.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O GIVE thanks unto the LORD, for he is gracious;\r\n            * because his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let Israel now confess that he is gracious,\r\n            * and that his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the house of Aaron now confess,\r\n            * that his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, let them now that fear the LORD confess,\r\n            * that his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I called upon the LORD in trouble;\r\n            * and the LORD heard me at large.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is on my side;\r\n            * I will not fear what man doeth unto me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD taketh my part with them that help me;\r\n            * therefore shall I see my desire upon mine enemies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            It is better to trust in the LORD,\r\n            * than to put any confidence in man.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            It is better to trust in the LORD,\r\n            * than to put any confidence in princes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All nations compassed me round about;\r\n            * but in the Name of the LORD will I destroy them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They kept me in on every side, they kept me in, I say,\r\n            on every side;\r\n            * but in the Name of the LORD will I destroy them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They came about me like bees, and are extinct even as\r\n            the fire among the thorns;\r\n            * for in the Name of the LORD I will destroy them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast thrust sore at me, that I might fall;\r\n            * but the LORD was my help.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is my strength, and my song;\r\n            * and is become my salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The voice of joy and health is in the dwellings of\r\n            the righteous;\r\n            * the right hand of the LORD bringeth mighty things\r\n            to pass.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The right hand of the LORD hath the pre-eminence;\r\n            * the right hand of the LORD bringeth mighty things\r\n            to pass.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I shall not die, but live,\r\n            * and declare the works of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD hath chastened and corrected me;\r\n            * but he hath not given me over unto death.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Open me the gates of righteousness,\r\n            * that I may go into them, and give thanks unto the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            This is the gate of the LORD,\r\n            * the righteous shall enter into it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will thank thee; for thou hast heard me,\r\n            * and art become my salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The same stone which the builders refused,\r\n            * is become the head-stone in the corner.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            This is the LORD\'S doing,\r\n            * and it is marvellous in our eyes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            This is the day which the LORD hath made;\r\n            * we will rejoice and be glad in it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Help me now, O LORD:\r\n            * O LORD, send us now prosperity.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed be he that cometh in the Name of the LORD:\r\n            * we have wished you good luck, we that are of the house\r\n            of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God is the LORD, who hath showed us light:\r\n            * bind the sacrifice with cords, yea, even unto the horns\r\n            of the altar.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou art my God, and I will thank thee;\r\n            * thou art my God, and I will praise thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O give thanks unto the LORD; for he is gracious,\r\n            * and his mercy endureth for ever.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(25, '25', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 25, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 119 (Section V - IX)\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Nineteenth Psalm</h3></div>\r\n\r\n<div class=\"text-center\">\r\n    <table width=\"90%\">\r\n\r\n        <tbody><tr>\r\n            <td> <h4><a href=\"#section1\">\r\n                I. Beati immaculati</a><br/>\r\n                <i>(vs 1-8)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section9\">\r\n                IX. Bonitatem fecisti</a><br/>\r\n                <i>(vs 65-72)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section17\">\r\n                XVII. Mirabilia</a><br/>\r\n                <i>(vs 129-136)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section2\">\r\n                II. In quo corrigit?</a><br/>\r\n                <i>(vs 9-16)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section10\">\r\n                X. Manus tuae fecerunt me</a><br/>\r\n                <i>(vs 73-80)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section18\">\r\n                XVIII. Justus es, Domine</a><br/>\r\n                <i>(vs 137-144)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section3\">\r\n                III. Retribue servo tuo</a><br/>\r\n                <i>(vs 17-24)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section11\">\r\n                XI. Defecit anima mea</a><br/>\r\n                <i>(vs 81-88)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section19\">\r\n                XIX. Clamavi in toto corde meo</a><br/>\r\n                <i>(vs 145-152)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section4\">\r\n                IV. Adhaesit pavimento</a><br/>\r\n                <i>(vs 25-32)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section12\">\r\n                XII. In aeternum, Domine</a><br/>\r\n                <i>(vs 89-96)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section20\">\r\n                XX. Vide humilitatem</a><br/>\r\n                <i>(vs 153-160)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section5\">\r\n                V. Legem pone</a><br/>\r\n                <i>(vs 33-40)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section13\">\r\n                XIII. Quomodo dilexi!</a><br/>\r\n                <i>(vs 97-104)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section21\">\r\n                XXI. Principes persecuti sunt</a><br/>\r\n                <i>(vs 161-168)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section6\">\r\n                VI. Et veniat super me</a><br/>\r\n                <i>(vs 41-48)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section14\">\r\n                XIV. Lucerna pedibus meus</a><br/>\r\n                <i>(vs 105-112)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section22\">\r\n                XXII. Appropinquet deprecatio</a><br/>\r\n                <i>(vs 169-176)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section7\">\r\n                VII. Memor esto verbi tui</a><br/>\r\n                <i>(vs 49-56)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section15\">\r\n                XV. Iniquos odio habui</a><br/>\r\n                <i>(vs 113-120)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section8\">\r\n                VIII. Portio mea, Domine</a><br/>\r\n                <i>(vs 57-64)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section16\">\r\n                XVI. Feci judicium</a><br/>\r\n                <i>(vs 121-128)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n        </tbody></table>\r\n</div>\r\n\r\n\r\n<a name=\"section1\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    I. Beati immaculati <i>(vs 1-8)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    BLESSED are those that are undefiled in the way, * and walk in the law of the LORD.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed are they that keep his testimonies, * and seek him with their whole heart;<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Even they who do no wickedness, * and walk in his ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast charged * that we shall diligently keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O that my ways were made so direct, * that I might keep thy statutes!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I not be confounded, * while I have respect unto all thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will thank thee with an unfeigned heart, * when I shall have learned the judgments of thy\r\n    righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will keep thy statutes; * O forsake me not utterly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section2\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    II. In quo corrigit? <i>(vs 9-16)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    WHEREWITHAL shall a young man cleanse his way? * even by ruling himself after thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my whole heart have I sought thee; * O let me not go wrong out of thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word have I hid within my heart, * that I should not sin against thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed art Thou, O LORD; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my lips have I been telling * of all the judgments of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have had as great delight in the way of thy testimonies, * as in all manner of riches.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will talk of thy commandments, * and have respect unto thy ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My delight shall be in thy statutes, * and I will not forget thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section3\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    III. Retribue servo tuo <i>(vs 17-24)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O DO well unto thy servant; * that I may live, and keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Open thou mine eyes; * that I may see the wondrous things of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a stranger upon earth; * O hide not thy commandments from me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul breaketh out for the very fervent desire * that it hath alway unto thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast rebuked the proud; * and cursed are they that do err from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn from me shame and rebuke; * for I have kept thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Princes also did sit and speak against me; * but thy servant is occupied in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For thy testimonies are my delight, * and my counsellors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section4\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IV. Adhaesit pavimento <i>(vs 25-32)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul cleaveth to the dust; * O quicken thou me, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have acknowledged my ways, and thou heardest me: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to understand the way of thy commandments; * and so shall I talk of thy wondrous works.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul melteth away for very heaviness; * comfort thou me according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take from me the way of lying, * and cause thou me to make much of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have chosen the way of truth, * and thy judgments have I laid before me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have stuck unto thy testimonies; * O LORD, confound me not.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will run the way of thy commandments, * when thou hast set my heart at liberty.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section5\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    V. Legem pone <i>(vs 33-40)</i><br/>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    <small> &nbsp; <br/></small>\r\n    TEACH me, O LORD, the way of thy statutes, * and I shall keep it unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Give me understanding, and I shall keep thy law; * yea, I shall keep it with my whole heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to go in the path of thy commandments; * for therein is my desire.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Incline my heart unto thy testimonies, * and not to covetousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn away mine eyes, lest they behold vanity; * and quicken thou me in thy way.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish thy word in thy servant, * that I may fear thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take away the rebuke that I am afraid of; * for thy judgments are good.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Behold, my delight is in thy commandments; * O quicken me in thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section6\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VI. Et veniat super me <i>(vs 41-48)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET thy loving mercy come also unto me, O LORD, * even thy salvation, according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I make answer unto my blasphemers; * for my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O take not the word of thy truth utterly out of my mouth; * for my hope is in thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I alway keep thy law; * yea, for ever and ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And I will walk at liberty; * for I seek thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will speak of thy testimonies also, even before kings, * and will not be ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And my delight shall be in thy commandments, * which I have loved.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My hands also will I lift up unto thy commandments, which I have loved; * and my study shall be in thy\r\n    statutes.<br/>\r\n</b></blockquote>\r\n<small> &nbsp; <br/></small>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section7\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VII. Memor esto verbi tui <i>(vs 49-56)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O THINK upon thy servant, as concerning thy word, * wherein thou hast caused me to put my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The same is my comfort in my trouble; * for thy word hath quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have had me exceedingly in derision; * yet have I not shrinked from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I remembered thine everlasting judgments, O LORD, * and received comfort.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am horribly afraid, * for the ungodly that forsake thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy statutes have been my songs, * in the house of my pilgrimage.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have thought upon thy Name, O LORD, in the night season, * and have kept thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    This I had, * because I kept thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section8\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VIII. Portio mea, Domine <i>(vs 57-64)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THOU art my portion, O LORD; * I have promised to keep thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made my humble petition in thy presence with my whole heart; * O be merciful unto me, according to\r\n    thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I called mine own ways to remembrance, * and turned my feet unto thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made haste, and prolonged not the time, * to keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The snares of the ungodly have compassed me about; * but I have not forgotten thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    At midnight I will rise to give thanks unto thee, * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a companion of all them that fear thee, * and keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The earth, O LORD, is full of thy mercy: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section9\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IX. Bonitatem fecisti <i>(vs 65-72)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thou hast dealt graciously with thy servant, * according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O teach me true understanding and knowledge; * for I have believed thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Before I was troubled, I went wrong; * but now have I kept thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art good and gracious; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have imagined a lie against me; * but I will keep thy commandments with my whole\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Their heart is as fat as brawn; * but my delight hath been in thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is good for me that I have been in trouble; * that I may learn thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The law of thy mouth is dearer unto me * than thousands of gold and silver.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section10\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    X. Manus tuae fecerunt me <i>(vs 73-80)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY hands have made me and fashioned me: * O give me understanding, that I may learn  thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They that fear thee will be glad when they see me; * because I have put my trust in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I know, O LORD, that thy judgments are right, * and that thou of very faithfulness hast caused me to be\r\n    troubled.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy merciful kindness be my comfort, * according to thy word unto thy servant.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy loving mercies come unto me, that I may live; * for thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the proud be confounded, for they go wickedly about to destroy me; * but I will be occupied in thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let such as fear thee, and have known thy testimonies, * be turned unto me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my heart be sound in thy statutes, * that I be not ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section11\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XI. Defecit anima mea <i>(vs 81-88)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul hath longed for thy salvation, * and I have a good hope because of thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes-long sore for thy word; * saying, O when wilt thou comfort me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I am become like a bottle in the smoke; * yet do I not forget thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    How many are the days of thy servant? * when wilt thou be avenged of them that persecute me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have digged pits for me, * which are not after thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    All thy commandments are true: * they persecute me falsely; O be thou my help.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They had almost made an end of me upon earth; * but I forsook not thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O quicken me after thy loving-kindness; * and so shall I keep the testimonies of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section12\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XII. In aeternum, Domine <i>(vs 89-96)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thy word * endureth for ever in heaven.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy truth also remaineth from one generation to another; * thou hast laid the foundation of the earth, and\r\n    it abideth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They continue this day according to thine ordinance; * for all things serve thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    If my delight had not been in thy law, * I should have perished in my trouble.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will never forget thy commandments; * for with them thou hast quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thine: O save me, * for I have sought thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly laid wait for me, to destroy me; * but I will consider thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I see that all things come to an end; * but thy commandment is exceeding broad.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section13\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIII. Quomodo dilexi! <i>(vs 97-104)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LORD, what love have I unto thy law! * all the day long is my study in it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou, through thy commandments, hast made me wiser than mine enemies; * for they are ever with\r\n    me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have more understanding than my teachers; * for thy testimonies are my study.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am wiser than the aged; * because I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have refrained my feet from every evil way, * that I may keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have not shrunk from thy judgments; * for thou teachest me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O how sweet are thy words unto my throat; * yea, sweeter than honey unto my mouth!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Through thy commandments I get understanding: * therefore I hate all evil ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section14\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIV. Lucerna pedibus meus <i>(vs 105-112)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY word is a lantern unto my feet, * and a light unto my paths.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have sworn, and am stedfastly purposed, * to keep thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am troubled above measure: * quicken me, O LORD, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the free-will offerings of my mouth please thee, O LORD; * and teach me thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul is alway in my hand; * yet do I not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly have laid a snare for me; * but yet I swerved not from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy testimonies have I claimed as mine heritage for ever; * and why? they are the very joy of my\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have applied my heart to fulfil thy statutes alway, * even unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section15\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XV. Iniquos odio habui <i>(vs 113-120)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I HATE them that imagine evil things; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art my defence and shield; * and my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Away from me, ye wicked; * I will keep the commandments of my God.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish me according to thy word, that I may live; * and let me not be disappointed of my hope.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hold thou me up, and I shall be safe; * yea, my delight shall be ever in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast trodden down all them that depart  from thy statutes; * for they imagine but deceit.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou puttest away all the ungodly of the earth like dross; * therefore I love thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My flesh trembleth for fear of thee; * and I am afraid of thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section16\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVI. Feci judicium <i>(vs 121-128)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I DEAL with the thing that is lawful and right; * O give me not over unto mine oppressors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make thou thy servant to delight in that which is good, * that the proud do me no wrong.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes are wasted away with looking for thy health, * and for the word of thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deal with thy servant according unto thy loving mercy, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thy servant; O grant me understanding, * that I may know thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is time for thee, LORD, to lay to thine hand; * for they have destroyed thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I love thy commandments * above gold and precious stones.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Therefore hold I straight all thy commandments; * and all false ways I utterly abhor.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section17\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVII. Mirabilia <i>(vs 129-136)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY testimonies are wonderful; * therefore doth my soul keep them.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    When thy word goeth forth, * it giveth light and understanding unto the simple.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I opened my mouth, and drew in my breath; * for my delight was in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O look thou upon me, and be merciful unto me, * as thou usest to do unto those that love thy Name.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Order my steps in thy word; * and so shall no wickedness have dominion over me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deliver me from the wrongful dealings of men; * and so shall I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Show the light of thy countenance upon thy servant, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes gush out with water, * because men keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section18\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVIII. Justus es, Domine <i>(vs 137-144)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    RIGHTEOUS art thou, O LORD; * and true are thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The testimonies that thou hast commanded * are exceeding righteous and true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My zeal hath even consumed me; * because mine enemies have forgotten thy words.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is tried to the uttermost, * and thy servant loveth it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am small and of no reputation; * yet do I not forget thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy righteousness is an everlasting righteousness, * and thy law is the truth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Trouble and heaviness have taken hold upon me; * yet is my delight in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The righteousness of thy testimonies is everlasting: * O grant me understanding, and I shall live.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section19\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIX. Clamavi in toto corde meo <i>(vs 145-152)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I CALL with my whole heart; * hear me, O LORD; I will keep thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, even unto thee do I call; * help me, and I shall keep thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Early in the morning do I cry unto thee; * for in thy word is my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes prevent the night watches; * that I might be occupied in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hear my voice, O LORD, according unto thy loving-kindness; * quicken me, according to thy\r\n    judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They draw nigh that of malice persecute me, * and are far from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Be thou nigh at hand, O LORD; * for all thy commandments are true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As concerning thy testimonies, I have known long since, * that thou hast grounded them for ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section20\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XX. Vide humilitatem <i>(vs 153-160)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O CONSIDER mine adversity, and deliver me, * for I do not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Avenge thou my cause, and deliver me; * quicken me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Health is far from the ungodly; * for they regard not thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is thy mercy, O LORD; * quicken me, as thou art wont.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Many there are that trouble me, and persecute me; * yet do I not swerve from thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It grieveth me when I see the transgressors; * because they keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Consider, O LORD, how I love thy commandments; * O quicken me, according to thy loving-\r\n    kindness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is true from everlasting; * all the judgments of thy righteousness endure for evermore.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section21\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXI. Principes persecuti sunt <i>(vs 161-168)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    PRINCES have persecuted me without a cause; * but my heart standeth in awe of thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am as glad of thy word, * as one that findeth great spoils.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As for lies, I hate and abhor them; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Seven times a day do I praise thee; * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is the peace that they have who love thy law; * and they have none occasion of stumbling.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    LORD, I have looked for thy saving health, * and done after thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul hath kept thy testimonies, * and loved them exceedingly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have kept thy commandments and testimonies; * for all my ways are before thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section22\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXII. Appropinquet deprecatio <i>(vs 169-176)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET my complaint come before thee, O LORD; * give me understanding according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let my supplication come before thee; * deliver me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My lips shall speak of thy praise, * when thou hast taught me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, my tongue shall sing of thy word; * for all thy commandments are righteous.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let thine hand help me; * for I have chosen thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have longed for thy saving health, O LORD; * and in thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my soul live, and it shall praise thee; * and thy judgments shall help me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have gone astray like a sheep that is lost; * O seek thy servant, for I do not forget thy\r\n    commandments.<br/>\r\n</b></blockquote>\r\n\r\n\r\n<a name=\"finish\"> &nbsp;</a>\r\n\r\n\r\n\r\n\r\n\r\n\r\n', 'Morning', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(26, '26', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 26, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 119 (Section XIV - XVIII)\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Nineteenth Psalm</h3></div>\r\n\r\n<div class=\"text-center\">\r\n    <table width=\"90%\">\r\n\r\n        <tbody><tr>\r\n            <td> <h4><a href=\"#section1\">\r\n                I. Beati immaculati</a><br/>\r\n                <i>(vs 1-8)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section9\">\r\n                IX. Bonitatem fecisti</a><br/>\r\n                <i>(vs 65-72)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section17\">\r\n                XVII. Mirabilia</a><br/>\r\n                <i>(vs 129-136)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section2\">\r\n                II. In quo corrigit?</a><br/>\r\n                <i>(vs 9-16)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section10\">\r\n                X. Manus tuae fecerunt me</a><br/>\r\n                <i>(vs 73-80)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section18\">\r\n                XVIII. Justus es, Domine</a><br/>\r\n                <i>(vs 137-144)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section3\">\r\n                III. Retribue servo tuo</a><br/>\r\n                <i>(vs 17-24)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section11\">\r\n                XI. Defecit anima mea</a><br/>\r\n                <i>(vs 81-88)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section19\">\r\n                XIX. Clamavi in toto corde meo</a><br/>\r\n                <i>(vs 145-152)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section4\">\r\n                IV. Adhaesit pavimento</a><br/>\r\n                <i>(vs 25-32)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section12\">\r\n                XII. In aeternum, Domine</a><br/>\r\n                <i>(vs 89-96)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section20\">\r\n                XX. Vide humilitatem</a><br/>\r\n                <i>(vs 153-160)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section5\">\r\n                V. Legem pone</a><br/>\r\n                <i>(vs 33-40)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section13\">\r\n                XIII. Quomodo dilexi!</a><br/>\r\n                <i>(vs 97-104)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section21\">\r\n                XXI. Principes persecuti sunt</a><br/>\r\n                <i>(vs 161-168)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section6\">\r\n                VI. Et veniat super me</a><br/>\r\n                <i>(vs 41-48)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section14\">\r\n                XIV. Lucerna pedibus meus</a><br/>\r\n                <i>(vs 105-112)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section22\">\r\n                XXII. Appropinquet deprecatio</a><br/>\r\n                <i>(vs 169-176)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section7\">\r\n                VII. Memor esto verbi tui</a><br/>\r\n                <i>(vs 49-56)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section15\">\r\n                XV. Iniquos odio habui</a><br/>\r\n                <i>(vs 113-120)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section8\">\r\n                VIII. Portio mea, Domine</a><br/>\r\n                <i>(vs 57-64)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section16\">\r\n                XVI. Feci judicium</a><br/>\r\n                <i>(vs 121-128)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n        </tbody></table>\r\n</div>\r\n\r\n\r\n<a name=\"section1\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    I. Beati immaculati <i>(vs 1-8)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    BLESSED are those that are undefiled in the way, * and walk in the law of the LORD.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed are they that keep his testimonies, * and seek him with their whole heart;<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Even they who do no wickedness, * and walk in his ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast charged * that we shall diligently keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O that my ways were made so direct, * that I might keep thy statutes!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I not be confounded, * while I have respect unto all thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will thank thee with an unfeigned heart, * when I shall have learned the judgments of thy\r\n    righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will keep thy statutes; * O forsake me not utterly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section2\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    II. In quo corrigit? <i>(vs 9-16)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    WHEREWITHAL shall a young man cleanse his way? * even by ruling himself after thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my whole heart have I sought thee; * O let me not go wrong out of thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word have I hid within my heart, * that I should not sin against thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed art Thou, O LORD; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my lips have I been telling * of all the judgments of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have had as great delight in the way of thy testimonies, * as in all manner of riches.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will talk of thy commandments, * and have respect unto thy ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My delight shall be in thy statutes, * and I will not forget thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section3\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    III. Retribue servo tuo <i>(vs 17-24)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O DO well unto thy servant; * that I may live, and keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Open thou mine eyes; * that I may see the wondrous things of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a stranger upon earth; * O hide not thy commandments from me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul breaketh out for the very fervent desire * that it hath alway unto thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast rebuked the proud; * and cursed are they that do err from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn from me shame and rebuke; * for I have kept thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Princes also did sit and speak against me; * but thy servant is occupied in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For thy testimonies are my delight, * and my counsellors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section4\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IV. Adhaesit pavimento <i>(vs 25-32)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul cleaveth to the dust; * O quicken thou me, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have acknowledged my ways, and thou heardest me: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to understand the way of thy commandments; * and so shall I talk of thy wondrous works.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul melteth away for very heaviness; * comfort thou me according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take from me the way of lying, * and cause thou me to make much of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have chosen the way of truth, * and thy judgments have I laid before me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have stuck unto thy testimonies; * O LORD, confound me not.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will run the way of thy commandments, * when thou hast set my heart at liberty.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section5\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    V. Legem pone <i>(vs 33-40)</i><br/>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    <small> &nbsp; <br/></small>\r\n    TEACH me, O LORD, the way of thy statutes, * and I shall keep it unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Give me understanding, and I shall keep thy law; * yea, I shall keep it with my whole heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to go in the path of thy commandments; * for therein is my desire.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Incline my heart unto thy testimonies, * and not to covetousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn away mine eyes, lest they behold vanity; * and quicken thou me in thy way.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish thy word in thy servant, * that I may fear thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take away the rebuke that I am afraid of; * for thy judgments are good.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Behold, my delight is in thy commandments; * O quicken me in thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section6\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VI. Et veniat super me <i>(vs 41-48)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET thy loving mercy come also unto me, O LORD, * even thy salvation, according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I make answer unto my blasphemers; * for my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O take not the word of thy truth utterly out of my mouth; * for my hope is in thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I alway keep thy law; * yea, for ever and ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And I will walk at liberty; * for I seek thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will speak of thy testimonies also, even before kings, * and will not be ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And my delight shall be in thy commandments, * which I have loved.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My hands also will I lift up unto thy commandments, which I have loved; * and my study shall be in thy\r\n    statutes.<br/>\r\n</b></blockquote>\r\n<small> &nbsp; <br/></small>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section7\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VII. Memor esto verbi tui <i>(vs 49-56)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O THINK upon thy servant, as concerning thy word, * wherein thou hast caused me to put my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The same is my comfort in my trouble; * for thy word hath quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have had me exceedingly in derision; * yet have I not shrinked from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I remembered thine everlasting judgments, O LORD, * and received comfort.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am horribly afraid, * for the ungodly that forsake thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy statutes have been my songs, * in the house of my pilgrimage.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have thought upon thy Name, O LORD, in the night season, * and have kept thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    This I had, * because I kept thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section8\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VIII. Portio mea, Domine <i>(vs 57-64)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THOU art my portion, O LORD; * I have promised to keep thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made my humble petition in thy presence with my whole heart; * O be merciful unto me, according to\r\n    thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I called mine own ways to remembrance, * and turned my feet unto thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made haste, and prolonged not the time, * to keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The snares of the ungodly have compassed me about; * but I have not forgotten thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    At midnight I will rise to give thanks unto thee, * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a companion of all them that fear thee, * and keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The earth, O LORD, is full of thy mercy: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section9\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IX. Bonitatem fecisti <i>(vs 65-72)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thou hast dealt graciously with thy servant, * according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O teach me true understanding and knowledge; * for I have believed thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Before I was troubled, I went wrong; * but now have I kept thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art good and gracious; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have imagined a lie against me; * but I will keep thy commandments with my whole\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Their heart is as fat as brawn; * but my delight hath been in thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is good for me that I have been in trouble; * that I may learn thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The law of thy mouth is dearer unto me * than thousands of gold and silver.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section10\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    X. Manus tuae fecerunt me <i>(vs 73-80)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY hands have made me and fashioned me: * O give me understanding, that I may learn  thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They that fear thee will be glad when they see me; * because I have put my trust in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I know, O LORD, that thy judgments are right, * and that thou of very faithfulness hast caused me to be\r\n    troubled.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy merciful kindness be my comfort, * according to thy word unto thy servant.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy loving mercies come unto me, that I may live; * for thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the proud be confounded, for they go wickedly about to destroy me; * but I will be occupied in thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let such as fear thee, and have known thy testimonies, * be turned unto me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my heart be sound in thy statutes, * that I be not ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section11\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XI. Defecit anima mea <i>(vs 81-88)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul hath longed for thy salvation, * and I have a good hope because of thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes-long sore for thy word; * saying, O when wilt thou comfort me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I am become like a bottle in the smoke; * yet do I not forget thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    How many are the days of thy servant? * when wilt thou be avenged of them that persecute me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have digged pits for me, * which are not after thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    All thy commandments are true: * they persecute me falsely; O be thou my help.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They had almost made an end of me upon earth; * but I forsook not thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O quicken me after thy loving-kindness; * and so shall I keep the testimonies of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section12\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XII. In aeternum, Domine <i>(vs 89-96)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thy word * endureth for ever in heaven.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy truth also remaineth from one generation to another; * thou hast laid the foundation of the earth, and\r\n    it abideth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They continue this day according to thine ordinance; * for all things serve thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    If my delight had not been in thy law, * I should have perished in my trouble.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will never forget thy commandments; * for with them thou hast quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thine: O save me, * for I have sought thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly laid wait for me, to destroy me; * but I will consider thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I see that all things come to an end; * but thy commandment is exceeding broad.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section13\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIII. Quomodo dilexi! <i>(vs 97-104)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LORD, what love have I unto thy law! * all the day long is my study in it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou, through thy commandments, hast made me wiser than mine enemies; * for they are ever with\r\n    me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have more understanding than my teachers; * for thy testimonies are my study.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am wiser than the aged; * because I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have refrained my feet from every evil way, * that I may keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have not shrunk from thy judgments; * for thou teachest me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O how sweet are thy words unto my throat; * yea, sweeter than honey unto my mouth!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Through thy commandments I get understanding: * therefore I hate all evil ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section14\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIV. Lucerna pedibus meus <i>(vs 105-112)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY word is a lantern unto my feet, * and a light unto my paths.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have sworn, and am stedfastly purposed, * to keep thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am troubled above measure: * quicken me, O LORD, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the free-will offerings of my mouth please thee, O LORD; * and teach me thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul is alway in my hand; * yet do I not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly have laid a snare for me; * but yet I swerved not from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy testimonies have I claimed as mine heritage for ever; * and why? they are the very joy of my\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have applied my heart to fulfil thy statutes alway, * even unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section15\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XV. Iniquos odio habui <i>(vs 113-120)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I HATE them that imagine evil things; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art my defence and shield; * and my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Away from me, ye wicked; * I will keep the commandments of my God.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish me according to thy word, that I may live; * and let me not be disappointed of my hope.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hold thou me up, and I shall be safe; * yea, my delight shall be ever in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast trodden down all them that depart  from thy statutes; * for they imagine but deceit.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou puttest away all the ungodly of the earth like dross; * therefore I love thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My flesh trembleth for fear of thee; * and I am afraid of thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section16\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVI. Feci judicium <i>(vs 121-128)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I DEAL with the thing that is lawful and right; * O give me not over unto mine oppressors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make thou thy servant to delight in that which is good, * that the proud do me no wrong.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes are wasted away with looking for thy health, * and for the word of thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deal with thy servant according unto thy loving mercy, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thy servant; O grant me understanding, * that I may know thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is time for thee, LORD, to lay to thine hand; * for they have destroyed thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I love thy commandments * above gold and precious stones.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Therefore hold I straight all thy commandments; * and all false ways I utterly abhor.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section17\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVII. Mirabilia <i>(vs 129-136)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY testimonies are wonderful; * therefore doth my soul keep them.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    When thy word goeth forth, * it giveth light and understanding unto the simple.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I opened my mouth, and drew in my breath; * for my delight was in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O look thou upon me, and be merciful unto me, * as thou usest to do unto those that love thy Name.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Order my steps in thy word; * and so shall no wickedness have dominion over me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deliver me from the wrongful dealings of men; * and so shall I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Show the light of thy countenance upon thy servant, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes gush out with water, * because men keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section18\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVIII. Justus es, Domine <i>(vs 137-144)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    RIGHTEOUS art thou, O LORD; * and true are thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The testimonies that thou hast commanded * are exceeding righteous and true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My zeal hath even consumed me; * because mine enemies have forgotten thy words.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is tried to the uttermost, * and thy servant loveth it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am small and of no reputation; * yet do I not forget thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy righteousness is an everlasting righteousness, * and thy law is the truth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Trouble and heaviness have taken hold upon me; * yet is my delight in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The righteousness of thy testimonies is everlasting: * O grant me understanding, and I shall live.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section19\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIX. Clamavi in toto corde meo <i>(vs 145-152)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I CALL with my whole heart; * hear me, O LORD; I will keep thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, even unto thee do I call; * help me, and I shall keep thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Early in the morning do I cry unto thee; * for in thy word is my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes prevent the night watches; * that I might be occupied in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hear my voice, O LORD, according unto thy loving-kindness; * quicken me, according to thy\r\n    judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They draw nigh that of malice persecute me, * and are far from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Be thou nigh at hand, O LORD; * for all thy commandments are true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As concerning thy testimonies, I have known long since, * that thou hast grounded them for ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section20\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XX. Vide humilitatem <i>(vs 153-160)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O CONSIDER mine adversity, and deliver me, * for I do not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Avenge thou my cause, and deliver me; * quicken me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Health is far from the ungodly; * for they regard not thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is thy mercy, O LORD; * quicken me, as thou art wont.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Many there are that trouble me, and persecute me; * yet do I not swerve from thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It grieveth me when I see the transgressors; * because they keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Consider, O LORD, how I love thy commandments; * O quicken me, according to thy loving-\r\n    kindness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is true from everlasting; * all the judgments of thy righteousness endure for evermore.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section21\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXI. Principes persecuti sunt <i>(vs 161-168)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    PRINCES have persecuted me without a cause; * but my heart standeth in awe of thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am as glad of thy word, * as one that findeth great spoils.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As for lies, I hate and abhor them; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Seven times a day do I praise thee; * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is the peace that they have who love thy law; * and they have none occasion of stumbling.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    LORD, I have looked for thy saving health, * and done after thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul hath kept thy testimonies, * and loved them exceedingly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have kept thy commandments and testimonies; * for all my ways are before thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section22\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXII. Appropinquet deprecatio <i>(vs 169-176)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET my complaint come before thee, O LORD; * give me understanding according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let my supplication come before thee; * deliver me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My lips shall speak of thy praise, * when thou hast taught me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, my tongue shall sing of thy word; * for all thy commandments are righteous.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let thine hand help me; * for I have chosen thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have longed for thy saving health, O LORD; * and in thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my soul live, and it shall praise thee; * and thy judgments shall help me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have gone astray like a sheep that is lost; * O seek thy servant, for I do not forget thy\r\n    commandments.<br/>\r\n</b></blockquote>\r\n\r\n\r\n<a name=\"finish\"> &nbsp;</a>\r\n\r\n\r\n\r\n\r\n\r\n\r\n', 'Morning', '0000-00-00 00:00:00'),
(27, '27', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 27, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 120\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twentieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Ad Dominum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            WHEN I was in trouble, I called upon the LORD,\r\n            * and he heard me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Deliver my soul, O LORD, from lying lips,\r\n            * and from a deceitful tongue.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            What reward shall be given or done unto thee, thou false tongue?\r\n            * even mighty and sharp arrows, with hot burning coals.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Woe is me, that I am constrained to dwell with Meshech,\r\n            * and to have my habitation among the tents of Kedar!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul hath long dwelt among them\r\n            * that are enemies unto peace.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I labour for peace; but when I speak unto them thereof,\r\n            * they make them ready to battle.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 121\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twenty First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Levavi oculos.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I WILL lift up mine eyes unto the hills;\r\n            * from whence cometh my help?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My help cometh even from the LORD,\r\n            * who hath made heaven and earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He will not suffer thy foot to be moved;\r\n            * and he that keepeth thee will not sleep.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, he that keepeth Israel\r\n            * shall neither slumber nor sleep.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD himself is thy keeper;\r\n            * the LORD is thy defence upon thy right hand;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So that the sun shall not burn thee by day,\r\n            * neither the moon by night.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD shall preserve thee from all evil;\r\n            * yea, it is even he that shall keep thy soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD shall preserve thy going out,\r\n            and thy coming in,\r\n            * from this time forth for evermore.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 122\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twenty Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Laetatus sum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I WAS glad when they said unto me,\r\n            * We will go into the house of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Our feet shall stand in thy gates,\r\n            * O Jerusalem.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Jerusalem is built as a city\r\n            * that is at unity in itself.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thither the tribes go up, even the tribes\r\n            of the LORD,\r\n            * to testify unto Israel, to give thanks unto\r\n            the Name of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For there is the seat of judgment,\r\n            * even the seat of the house of David.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O pray for the peace of Jerusalem;\r\n            * they shall prosper that love thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Peace be within thy walls,\r\n            * and plenteousness within thy palaces.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For my brethren and companions\' sakes,\r\n            * I will wish thee prosperity.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, because of the house of the LORD our God,\r\n            * I will seek to do thee good.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 123\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twenty Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Ad te levavi oculos meos.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            UNTO thee lift I up mine eyes,\r\n            * O thou that dwellest in the heavens.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, even as the eyes of servants look unto the hand of their masters, and as the eyes of a maiden unto the hand of her mistress,\r\n            * even so our eyes wait upon the LORD our God, until he have mercy upon us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Have mercy upon us, O LORD, have mercy upon us;\r\n            * for we are utterly despised.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Our soul is filled with the scornful reproof of the wealthy,\r\n            * and with the despitefulness of the proud.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 124\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twenty Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Nisi quia Dominus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            IF the LORD himself had not been on our side, now may Israel say;\r\n            * if the LORD himself had not been on our side, when men rose up against us;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They had swallowed us up alive;\r\n            * when they were so wrathfully displeased at us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, the waters had drowned us,\r\n            * and the stream had gone over our soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The deep waters of the proud\r\n            * had gone even over our soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But praised be the LORD,\r\n            * who hath not given us over for a prey unto their teeth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Our soul is escaped even as a bird out of the snare of the fowler;\r\n            * the snare is broken, and we are delivered.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Our help standeth in the Name of the LORD,\r\n            * who hath made heaven and earth.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 125\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twenty Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Qui confidunt.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THEY that put their trust in the LORD shall be even as the mount Sion,\r\n            * which may not be removed, but standeth fast for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The hills stand about Jerusalem;\r\n            * even so standeth the LORD round about his people, from this time forth for evermore.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the sceptre of the ungodly shall not abide upon the lot of the righteous;\r\n            * lest the righteous put their hand unto wickedness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Do well, O LORD,\r\n            * unto those that are good and true of heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for such as turn back unto their own wickedness,\r\n            * the LORD shall lead them forth with the evil doers; but peace shall be upon Israel.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(28, '28', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 28, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 132\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirty Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Memento, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            LORD, remember David,\r\n            * and all his trouble:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            How he sware unto the LORD,\r\n            * and vowed a vow unto the Almighty God of Jacob:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will not come within the tabernacle of mine house,\r\n            * nor climb up into my bed;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will not suffer mine eyes to sleep, nor mine eyelids\r\n            to slumber;\r\n            * neither the temples of my head to take any rest;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Until I find out a place for the temple of the LORD;\r\n            * an habitation for the Mighty God of Jacob.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lo, we heard of the same at Ephratah,\r\n            * and found it in the wood.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            We will go into his tabernacle,\r\n            * and fall low on our knees before his footstool.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Arise, O LORD, into thy resting-place;\r\n            * thou, and the ark of thy strength.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let thy priests be clothed with righteousness;\r\n            * and let thy saints sing with joyfulness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thy servant David\'s sake,\r\n            * turn not away the face of thine anointed.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD hath made a faithful oath unto David,\r\n            * and he shall not shrink from it:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Of the fruit of thy body\r\n            * shall I set upon thy throne.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If thy children will keep my covenant, and my\r\n            testimonies that I shall teach them;\r\n            * their children also shall sit upon thy throne for evermore.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the LORD hath chosen Sion to be an habitation\r\n            for himself;\r\n            * he hath longed for her.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            This shall be my rest for ever:\r\n            * here will I dwell, for I have a delight therein.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will bless her victuals with increase,\r\n            * and will satisfy her poor with bread.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will deck her priests with health,\r\n            * and her saints shall rejoice and sing.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There shall I make the horn of David to flourish:\r\n            * I have ordained a lantern for mine anointed.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for his enemies, I shall clothe them with shame;\r\n            * but upon himself shall his crown flourish.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 133\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirty Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Ecce, quam bonum!</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BEHOLD, how good and joyful a thing it is,\r\n            * for brethren to dwell together in unity!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            It is like the precious oil upon the head, that ran down unto the beard,\r\n            * even unto Aaron\'s beard, and went down to the skirts of his clothing.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Like as the dew of Hermon,\r\n            * which fell upon the hill of Sion.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For there the LORD promised his blessing,\r\n            * and life for evermore.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 134\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirty Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Ecce nunc.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BEHOLD now, praise the LORD,\r\n            * all ye servants of the LORD;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ye that by night stand in the house of the LORD,\r\n            * even in the courts of the house of our God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lift up your hands in the sanctuary,\r\n            * and praise the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD that made heaven and earth\r\n            * give thee blessing out of Sion.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 135\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirty Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Laudate Nomen.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O PRAISE the LORD, laud ye the Name of the LORD;\r\n            * praise it, O ye servants of the LORD;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ye that stand in the house of the LORD,\r\n            * in the courts of the house of our God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O praise the LORD, for the LORD is gracious;\r\n            * O sing praises unto his Name, for it is lovely.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For why? the LORD hath chosen Jacob unto himself,\r\n            * and Israel for his own possession.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For I know that the LORD is great,\r\n            * and that our Lord is above all gods.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whatsoever the LORD pleased, that did he in heaven, and in earth;\r\n            * and in the sea, and in all deep places.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He bringeth forth the clouds from the ends of the world,\r\n            * and sendeth forth lightnings with the rain, bringing the winds out of his treasuries.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He smote the firstborn of Egypt,\r\n            * both of man and beast.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath sent tokens and wonders into the midst of thee, O thou land of Egypt;\r\n            * upon Pharaoh, and all his servants.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He smote divers nations,\r\n            * and slew mighty kings:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Sihon, king of the Amorites; and Og, the king of Bashan;\r\n            * and all the kingdoms of Canaan;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And gave their land to be an heritage,\r\n            * even an heritage unto Israel his people.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy Name, O LORD, endureth for ever;\r\n            * so doth thy memorial, O LORD, from one generation to another.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the LORD will avenge his people,\r\n            * and be gracious unto his servants.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for the images of the heathen, they are but silver and gold;\r\n            * the work of men\'s hands.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have mouths, and speak not;\r\n            * eyes have they, but they see not.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have ears, and yet they hear not;\r\n            * neither is there any breath in their mouths.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They that make them are like unto them;\r\n            * and so are all they that put their trust in them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise the LORD, ye house of Israel;\r\n            * praise the LORD, ye house of Aaron.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise the LORD, ye house of Levi;\r\n            * ye that fear the LORD, praise the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praised be the LORD out of Sion,\r\n            * who dwelleth at Jerusalem.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(29, '29', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 29, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 139\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirty Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, probasti.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O LORD, thou hast searched me out, and known me.\r\n            * Thou knowest my down-sitting, and mine uprising; thou understandest my thoughts long before.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou art about my path, and about my bed;\r\n            * and art acquainted with all my ways.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For lo, there is not a word in my tongue,\r\n            * but thou, O LORD, knowest it altogether.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast beset me behind and before,\r\n            * and laid thine hand upon me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Such knowledge is too wonderful and excellent for me;\r\n            * I cannot attain unto it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whither shall I go then from thy Spirit?\r\n            * or whither shall I go then from thy presence?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If I climb up into heaven, thou art there;\r\n            * if I go down to hell, thou art there also.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If I take the wings of the morning,\r\n            * and remain in the uttermost parts of the sea;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Even there also shall thy hand lead me,\r\n            * and thy right hand shall hold me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If I say, Peradventure the darkness shall cover me;\r\n            * then shall my night be turned to day.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, the darkness is no darkness with thee, but the night is as clear as the day;\r\n            * the darkness and light to thee are both alike.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For my reins are thine;\r\n            * thou hast covered me in my mother\'s womb.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will give thanks unto thee, for I am fearfully and wonderfully made:\r\n            * marvellous are thy works, and that my soul knoweth right well.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My bones are not hid from thee,\r\n            * though I be made secretly, and fashioned beneath in the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thine eyes did see my substance, yet being imperfect;\r\n            * and in thy book were all my members written;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Which day by day were fashioned,\r\n            * when as yet there was none of them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            How dear are thy counsels unto me, O God;\r\n            * O how great is the sum of them!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If I tell them, they are more in number than the sand:\r\n            * when I wake up, I am present with thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wilt thou not slay the wicked, O God?\r\n            * Depart from me, ye blood-thirsty men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For they speak unrighteously against thee;\r\n            * and thine enemies take thy Name in vain.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Do not I hate them, O LORD, that hate thee?\r\n            * and am not I grieved with those that rise up against thee?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, I hate them right sore;\r\n            * even as though they were mine enemies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Try me, O God, and seek the ground of my heart;\r\n            * prove me, and examine my thoughts.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Look well if there be any way of wickedness in me;\r\n            * and lead me in the way everlasting.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 140\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Fortieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Eripe me, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            DELIVER me, O LORD, from the evil man;\r\n            * and preserve me from the wicked man;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who imagine mischief in their hearts,\r\n            * and stir up strife all the day long.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have sharpened their tongues like a serpent;\r\n            * adder\'s poison is under their lips.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Keep me, O LORD, from the hands of the ungodly;\r\n            * preserve me from the wicked men, who are purposed to  overthrow my goings.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The proud have laid a snare for me, and spread a net abroad with cords;\r\n            * yea, and set traps in my way.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I said unto the LORD, Thou art my God,\r\n            * hear the voice of my prayers, O LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O LORD God, thou strength of my health;\r\n            * thou hast covered my head in the day of battle.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let not the ungodly have his desire, O LORD;\r\n            * let not his mischievous imagination prosper, lest they be too  proud.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the mischief of their own lips fall upon the head of them\r\n            * that compass me about.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let hot burning coals fall upon them;\r\n            * let them be cast into the fire, and into the pit, that they never rise up again.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            A man full of words shall not prosper upon the earth:\r\n            * evil shall hunt the wicked person to overthrow him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Sure I am that the LORD will avenge the poor,\r\n            * and maintain the cause of the helpless.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The righteous also shall give thanks unto thy Name;\r\n            * and the just shall continue in thy sight.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(30, '30', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 30, Morning<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 144\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Forty Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Benedictus Dominus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BLESSED be the LORD my strength,\r\n            * who teacheth my hands to war, and my\r\n            fingers to fight:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My hope and my fortress, my castle and deliverer,\r\n            my defender in whom I trust;\r\n            * who subdueth my people that is under me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            LORD, what is man, that thou hast such\r\n            respect unto him?\r\n            * or the son of man, that thou so regardest him?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Man is like a thing of nought;\r\n            * his time passeth away like a shadow.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Bow thy heavens, O LORD, and come down;\r\n            * touch the mountains, and they shall smoke.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Cast forth thy lightning, and tear them;\r\n            * shoot out thine arrows, and consume them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Send down thine hand from above;\r\n            * deliver me, and take me out of the great waters,\r\n            from the hand of strangers;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whose mouth talketh of vanity,\r\n            * and their right hand is a right hand of wickedness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will sing a new song unto thee, O God;\r\n            * and sing praises unto thee upon a ten-stringed lute.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast given victory unto kings,\r\n            * and hast delivered David thy servant from the peril\r\n            of the sword.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Save me, and deliver me from the hand of strangers,\r\n            * whose mouth talketh of vanity, and their right\r\n            hand is a right hand of iniquity:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That our sons may grow up as the young plants,\r\n            * and that our daughters may be as the polished\r\n            corners of the temple;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That our garners may be full and plenteous with\r\n            all manner of store;\r\n            * that our sheep may bring forth thousands, and ten thousands in our fields;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That our oxen may be strong to labour; that there\r\n            be no decay,\r\n            * no leading into captivity, and no complaining\r\n            in our streets.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Happy are the people that are in such a case;\r\n            * yea, blessed are the people who have the LORD\r\n            for their God.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 145\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Forty Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exaltabo te, Deus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I WILL magnify thee, O God, my King;\r\n            * and I will praise thy Name for ever and ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Every day will I give thanks unto thee;\r\n            * and praise thy Name for ever and ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Great is the LORD, and marvellous worthy to be praised;\r\n            * there is no end of his greatness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            One generation shall praise thy works unto another,\r\n            * and declare thy power.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for me, I will be talking of thy worship,\r\n            * thy glory, thy praise, and wondrous works;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So that men shall speak of the might of thy\r\n            marvellous acts;\r\n            * and I will also tell of thy greatness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The memorial of thine abundant kindness shall\r\n            be showed;\r\n            * and men shall sing of thy righteousness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is gracious and merciful;\r\n            * long-suffering, and of great goodness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is loving unto every man;\r\n            * and his mercy is over all his works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All thy works praise thee, O LORD;\r\n            * and thy saints give thanks unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They show the glory of thy kingdom,\r\n            * and talk of thy power;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That thy power, thy glory, and mightiness of\r\n            thy kingdom,\r\n            * might be known unto men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy kingdom is an everlasting kingdom,\r\n            * and thy dominion endureth throughout all ages.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD upholdeth all such as fall,\r\n            * and lifteth up all those that are down.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The eyes of all wait upon thee, O Lord;\r\n            * and thou givest them their meat in due season.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou openest thine hand,\r\n            * and fillest all things living with plenteousness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is righteous in all his ways,\r\n            * and holy in all his works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is nigh unto all them that call upon him;\r\n            * yea, all such as call upon him faithfully.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He will fulfil the desire of them that fear him;\r\n            * he also will hear their cry, and will help them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD preserveth all them that love him;\r\n            * but scattereth abroad all the ungodly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My mouth shall speak the praise of the LORD;\r\n            * and let all flesh give thanks unto his holy Name\r\n            for ever and ever.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 146\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Forty Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Lauda, anima mea.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            PRAISE the LORD, O my soul: while I live, will I praise\r\n            the LORD;\r\n            * yea, as long as I have any being, I will sing praises unto\r\n            my God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O put not your trust in princes, nor in any child of man;\r\n            * for there is no help in them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For when the breath of man goeth forth, he shall turn\r\n            again to his earth,\r\n            * and then all his thoughts perish.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed is he that hath the God of Jacob for his help,\r\n            * and whose hope is in the LORD his God:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who made heaven and earth, the sea, and all that\r\n            therein is;\r\n            * who keepeth his promise for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who helpeth them to right that suffer wrong;\r\n            * who feedeth the hungry.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD looseth men out of prison;\r\n            * the LORD giveth sight to the blind.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD helpeth them that are fallen;\r\n            * the LORD careth for the righteous.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD careth for the strangers; he defendeth\r\n            the fatherless and widow:\r\n            * as for the way of the ungodly, he turneth it upside down.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD thy God, O Sion, shall be King for ever-more,\r\n            * and throughout all generations.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Morning', '0000-00-00 00:00:00'),
(31, '1', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 1, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 6\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, ne in furore.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O LORD, rebuke me not in thine indignation,\r\n            *  neither chasten me in thy displeasure.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Have mercy upon me, O LORD, for I am weak;\r\n            *  O LORD, heal me, for my bones are vexed.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul also is sore troubled:\r\n            *  but, LORD, how long wilt thou punish me? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn thee, O LORD, and deliver my soul;\r\n            *  O save me, for thy mercy\'s sake.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For in death no man remembereth thee;\r\n            *  and who will give thee thanks in the pit? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            I am weary of my groaning;\r\n            *  every night wash I my bed, and water my couch\r\n            with my tears.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My beauty is gone for very trouble,\r\n            *  and worn away because of all mine enemies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Away from me, all ye that work iniquity;\r\n            *  for the LORD hath heard the voice of my weeping.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD hath heard my petition;\r\n            *  the LORD will receive my prayer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All mine enemies shall be confounded, and sore vexed;\r\n            *  they shall be turned back, and put to shame suddenly.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 7\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, Deus meus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O LORD my God, in thee have I put my trust:\r\n            * save me from all them that persecute me, and deliver me;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lest he devour my soul like a lion, and tear it in pieces,\r\n            * while there is none to help.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O LORD my God, if I have done any such thing;\r\n            * or if there be any wickedness in my hands; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            If I have rewarded evil unto him that dealt\r\n            friendly with me;\r\n            *  (yea, I have delivered him that without any cause\r\n            is mine enemy;) <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then let mine enemy persecute my soul, and take me;\r\n            *  yea, let him tread my life down upon the earth,\r\n            and lay mine honour in the dust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Stand up, O LORD, in thy wrath, and lift up thyself,\r\n            because of the indignation of mine enemies;\r\n            *  arise up for me in the judgment that thou hast\r\n            commanded.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And so shall the congregation of the peoples\r\n            come about thee:\r\n            *  for their sakes therefore lift up thyself again.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD shall judge the peoples: give sentence\r\n            with me, O LORD,\r\n            * according to my righteousness, and according to\r\n            the innocency that is in me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let the wickedness of the ungodly come to an end;\r\n            * but guide thou the just.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the righteous God\r\n            * trieth the very hearts and reins.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My help cometh of God,\r\n            * who preserveth them that are true of heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God is a righteous Judge, strong, and patient;\r\n            * and God is provoked every day.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If a man will not turn, he will whet his sword;\r\n            * he hath bent his bow, and made it ready.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath prepared for him the instruments of death;\r\n            * he ordaineth his arrows against the persecutors.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, the ungodly travaileth with iniquity;\r\n            * he hath conceived mischief, and brought forth falsehood.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath graven and digged up a pit,\r\n            * and is fallen himself into the destruction that he\r\n            made for other.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For his travail shall come upon his own head,\r\n            * and his wickedness shall fall on his own pate.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will give thanks unto the LORD, according\r\n            to his righteousness;\r\n            * and I will praise the Name of the LORD Most High.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 8\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, Dominus noster.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O LORD our Governor, how excellent is thy Name\r\n            in all the world;\r\n            * thou that hast set thy glory above the heavens!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Out of the mouth of very babes and sucklings hast\r\n            thou ordained strength, because of thine enemies,\r\n            * that thou mightest still the enemy and the avenger.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When I consider thy heavens, even the work\r\n            of thy fingers;\r\n            * the moon and the stars which thou hast ordained;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            What is man, that thou art mindful of him?\r\n            * and the son of man, that thou visitest him?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou madest him lower than the angels,\r\n            * to crown him with glory and worship.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou makest him to have dominion of the\r\n            works of thy hands;\r\n            * and thou hast put all things in subjection under his feet:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All sheep and oxen;\r\n            * yea, and the beasts of the field;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The fowls of the air, and the fishes of the sea;\r\n            * and whatsoever walketh through the paths of the seas.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O LORD our Governor,\r\n            * how excellent is thy Name in all the world!\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(32, '2', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 2, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 12\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twelfth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Salvum me fac.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HELP me, LORD, for there is not one godly man left;\r\n            *  for the faithful are minished from among the\r\n            children of men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They talk of vanity every one with his neighbour;\r\n            *  they do but flatter with their lips, and dissemble in\r\n            their double heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD shall root out all deceitful lips,\r\n            *  and the tongue that speaketh proud things;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Which have said, With our tongue will we prevail;\r\n            *  we are they that ought to speak; who is lord over us?<br/>\r\n            <br/>\r\n            Now, for the comfortless troubles\' sake of the needy,\r\n            *  and because of the deep sighing of the poor,<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will up, saith the LORD;\r\n            *  and will help every one from him that swelleth against\r\n            him, and will set him at rest.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The words of the LORD are pure words;\r\n            *  even as the silver which from the earth is tried,\r\n            and purified seven times in the fire.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt keep them, O LORD;\r\n            *  thou shalt preserve them from this generation for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The ungodly walk on every side:\r\n            *  when they are exalted, the children of men are\r\n            put to rebuke.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 13\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Usquequo, Domine?</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HOW long wilt thou forget me, O LORD; for ever?\r\n            * how long wilt thou hide thy face from me?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            How long shall I seek counsel in my soul, and be so\r\n            vexed in my heart?\r\n            * how long shall mine enemy triumph over me? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Consider, and hear me, O LORD my God;\r\n            * lighten mine eyes, that I sleep not in death; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lest mine enemy say, I have prevailed against him:\r\n            * for if I be cast down, they that trouble me will\r\n            rejoice at it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But my trust is in thy mercy,\r\n            * and my heart is joyful in thy salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will sing of the LORD, because he hath dealt so\r\n            lovingly with me;\r\n            * yea, I will praise the Name of the Lord Most Highest.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 14\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fourteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dixit insipiens.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE fool hath said in his heart,\r\n            * There is no God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They are corrupt, and become abominable in\r\n            their doings;\r\n            * there is none that doeth good, no not one.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD looked down from heaven upon the\r\n            children of men,\r\n            * to see if there were any that would understand,\r\n            and seek after God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But they are all gone out of the way, they are altogether\r\n            become abominable;\r\n            * there is none that doeth good, no not one.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Have they no knowledge, that they are all such workers\r\n            of mischief,\r\n            * eating up my people as it were bread, and call not\r\n            upon the LORD?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There were they brought in great fear, even where no\r\n            fear was;\r\n            * for God is in the generation of the righteous.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for you, ye have made a mock at the counsel\r\n            of the poor;\r\n            * because he putteth his trust in the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who shall give salvation unto Israel out of Sion?\r\n            * When the LORD turneth the captivity of his people, then\r\n            shall Jacob rejoice, and Israel shall be glad.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(33, '3', '\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 3, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 18\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Diligam te, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        I WILL love thee, O LORD, my strength.\r\n        * The LORD is my stony rock, and my defence;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My Saviour, my God, and my might, in whom\r\n        I will trust;\r\n        * my buckler, the horn also of my salvation,\r\n        and my refuge.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will call upon the LORD, which is worthy\r\n        to be praised;\r\n        * so shall I be safe from mine enemies.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The sorrows of death compassed me,\r\n        * and the overflowings of ungodliness made me afraid.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The pains of hell came about me;\r\n        * the snares of death overtook me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        In my trouble I called upon the LORD,\r\n        * and complained unto my God:<br/>\r\n        <small> &nbsp; <br/></small>\r\n        So he heard my voice out of his holy temple,\r\n        * and my complaint came before him; it entered\r\n        even into his ears.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The earth trembled and quaked,\r\n        * the very foundations also of the hills shook, and\r\n        were removed, because he was wroth.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        There went a smoke out in his presence,\r\n        * and a consuming fire out of his mouth, so that\r\n        coals were kindled at it.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He bowed the heavens also, and came down,\r\n        * and it was dark under his feet.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He rode upon the Cherubim, and did fly;\r\n        * he came flying upon the wings of the wind.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He made darkness his secret place,\r\n        * his pavilion round about him with dark water,\r\n        and thick clouds to cover him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        At the brightness of his presence his clouds removed;\r\n        * hailstones and coals of fire.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The LORD also thundered out of heaven, and the\r\n        Highest gave his thunder;\r\n        * hailstones and coals of fire.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He sent out his arrows, and scattered them;\r\n        * he cast forth lightnings, and destroyed them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The springs of waters were seen, and the foundations of\r\n        the round world were discovered,\r\n        * at thy chiding, O LORD, at the blasting of the breath\r\n        of thy displeasure.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He sent down from on high to fetch me,\r\n        * and took me out of many waters.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He delivered me from my strongest enemy, and from\r\n        them which hate me;\r\n        * for they were too mighty for me<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They came upon me in the day of my trouble;\r\n        * but the LORD was my upholder.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He brought me forth also into a place of liberty;\r\n        * he brought me forth, even because he had a\r\n        favour unto me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 21 </i>The LORD rewarded me after my righteous dealing,\r\n        * according to the cleanness of my hands did he\r\n        recompense me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Because I have kept the ways of the LORD,\r\n        * and have not forsaken my God, as the wicked doth.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For I have an eye unto all his laws,\r\n        * and will not cast out his commandments from me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I was also uncorrupt before him,\r\n        * and eschewed mine own wickedness.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Therefore the LORD rewarded me after my\r\n        righteous dealing,\r\n        * and according unto the cleanness of my hands\r\n        in his eyesight.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        With the holy thou shalt be holy,\r\n        * and with a perfect man thou shalt be perfect.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        With the clean thou shalt be clean,\r\n        * and with the froward thou shalt be froward.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For thou shalt save the people that are in adversity,\r\n        * and shalt bring down the high looks of the proud.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou also shalt light my candle;\r\n        * the LORD my God shall make my darkness to be light.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For in thee I shall discomfit an host of men,\r\n        * and with the help of my God I shall leap over the wall.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The way of God is an undefiled way:\r\n        * the word of the LORD also is tried in the fire; he is the\r\n        defender of all them that put their trust in him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For who is God, but the LORD?\r\n        * or who hath any strength, except our God?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        It is God that girdeth me with strength of war,\r\n        * and maketh my way perfect.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He maketh my feet like harts\' feet,\r\n        * and setteth me up on high.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He teacheth mine hands to fight,\r\n        * and mine arms shall bend even a bow of steel.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 36 </i>Thou hast given me the defence of thy salvation;\r\n        * thy right hand also shall hold me up, and thy loving\r\n        correction shall make me great.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou shalt make room enough under me for to go,\r\n        * that my footsteps shall not slide.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will follow upon mine enemies, and overtake them;\r\n        * neither will I turn again till I have destroyed them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will smite them, that they shall not be able to stand,\r\n        * but fall under my feet.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast girded me with strength unto the battle;\r\n        * thou shalt throw down mine enemies under me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast made mine enemies also to turn their\r\n        backs upon me,\r\n        * and I shall destroy them that hate me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They shall cry, but there shall be none to help them;\r\n        * yea, even unto the LORD shall they cry, but he shall\r\n        not hear them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will beat them as small as the dust before the wind:\r\n        * I will cast them out as the clay in the streets.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou shalt deliver me from the strivings of the people,\r\n        * and thou shalt make me the head of the nations; a people\r\n        whom I have not known shall serve me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        As soon as they hear of me, they shall obey me;\r\n        * the strangers shall feign obedience unto me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The strangers shall fail,\r\n        * and come trembling out of their strongholds.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The LORD liveth; and blessed be my strong helper,\r\n        * and praised be the God of my salvation; <br/>\r\n        <small> &nbsp; <br/></small>\r\n        Even the God that seeth that I be avenged,\r\n        * and subdueth the people unto me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        It is he that delivereth me from my cruel enemies,\r\n        and setteth me up above mine adversaries:\r\n        * thou shalt rid me from the wicked man.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For this cause will I give thanks unto thee, O LORD,\r\n        among the Gentiles,\r\n        * and sing praises unto thy Name.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Great prosperity giveth he unto his King,\r\n        * and showeth loving-kindness unto David his anointed,\r\n        and unto his seed for evermore.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(34, '4', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 4, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 22\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twenty-Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, deus meus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        MY God, my God, look upon me; why hast thou forsaken me? * and art so far from my health, and from\r\n        the words of my complaint?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O my God, I cry in the day-time, but thou hearest not; * and in the night season also I take no rest.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And thou continuest holy, * O thou Worship of Israel.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Our fathers hoped in thee; * they trusted in thee, and thou didst deliver them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They called upon thee, and were holpen; * they put their trust in thee, and were not confounded.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But as for me, I am a worm, and no man; * a very scorn of men, and the outcast of the people.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        All they that see me laugh me to scorn; * they shoot out their lips, and shake their heads, saying,<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He trusted in the LORD, that he would deliver him; * let him deliver him, if he will have him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But thou art he that took me out of my mother\'s womb; * thou wast my hope, when I hanged yet upon my\r\n        mother\'s breasts.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I have been left unto thee ever since I was born; * thou art my God even from my mother\'s womb.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O go not from me; for trouble is hard at hand, * and there is none to help me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Many oxen are come about me; * fat bulls of Bashan close me in on every side.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They gape upon me with their mouths, * as it were a ramping and a roaring lion.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I am poured out like water, and all my bones are out of joint; * my heart also in the midst of my body is\r\n        even like melting wax.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My strength is dried up like a potsherd, and my tongue cleaveth to my gums, * and thou bringest me into\r\n        the dust of death.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For many dogs are come about me, * and the council of the wicked layeth siege against me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They pierced my hands and my feet: I may tell all my bones: * they stand staring and looking upon me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They part my garments among them, * and cast lots upon my vesture.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But be not thou far from me, O LORD; * thou art my succour, haste thee to help me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 21 </i>Deliver my soul from the sword, * my darling from the power of the dog.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Save me from the lion\'s mouth; * thou hast heard me also from among the horns of the unicorns.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will declare thy Name unto my brethren; * in the midst of the congregation will I praise thee.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 23 </i>O praise the LORD, ye that fear him: * magnify him, all ye of the seed of Jacob; and fear him, all ye seed\r\n        of Israel.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For he hath not despised nor abhorred the low estate of the poor; * he hath not hid his face from him; but\r\n        when he called unto him he heard him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My praise is of thee in the great congregation; * my vows will I perform in the sight of them that fear him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The poor shall eat, and be satisfied; they that seek after the LORD shall praise him: * your heart shall live\r\n        for ever.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        All the ends of the world shall remember themselves, and be turned unto the LORD; * and all the\r\n        kindreds of the nations shall worship before him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For the kingdom is the LORD\'S, * and he is the Governor among the nations.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        All such as be fat upon earth * have eaten, and worshipped.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        All they that go down into the dust shall kneel before him; * and no man hath quickened his own soul.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My seed shall serve him: * they shall be counted unto the Lord for a generation.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They shall come, and shall declare his righteousness * unto a people that shall be born, whom the Lord\r\n        hath made.\r\n    </b>\r\n    <br/>\r\n    <br/>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 23\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twenty-Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dominus regit me.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE LORD is my shepherd;\r\n            * therefore can I lack nothing.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall feed me in a green pasture,\r\n            * and lead me forth beside the waters of comfort.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall convert my soul,\r\n            * and bring me forth in the paths of righteousness for\r\n            his Name\'s sake.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, though I walk through the valley of the shadow of\r\n            death, I will fear no evil;\r\n            * for thou art with me; thy rod and thy staff comfort me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt prepare a table before me in the presence of\r\n            them that trouble me;\r\n            * thou hast anointed my head with oil, and my cup shall\r\n            be full.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Surely thy loving-kindness and mercy shall follow\r\n            me all the days of my life;\r\n            * and I will dwell in the house of the LORD for ever.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Evening', '0000-00-00 00:00:00'),
(35, '5', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 5, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 27\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twenty-Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dominus illuminatio.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE LORD is my light and my salvation; whom then\r\n            shall I fear?\r\n            * the LORD is the strength of my life; of whom then shall\r\n            I be afraid?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When the wicked, even mine enemies and my foes,\r\n            came upon me to eat up my flesh,\r\n            * they stumbled and fell.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Though an host of men were laid against me, yet shall\r\n            not my heart be afraid;\r\n            * and though there rose up war against me, yet will I\r\n            put my trust in him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            One thing have I desired of the LORD, which\r\n            I will require;\r\n            * even that I may dwell in the house of the LORD all the\r\n            days of my life, to behold the fair beauty of the LORD,\r\n            and to visit his temple.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For in the time of trouble he shall hide me in\r\n            his tabernacle;\r\n            * yea, in the secret place of his dwelling shall he hide me,\r\n            and set me up upon a rock of stone.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And now shall he lift up mine head\r\n            * above mine enemies round about me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore will I offer in his dwelling an oblation,\r\n            with great gladness:\r\n            * I will sing and speak praises unto the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hearken unto my voice, O LORD, when I cry unto thee;\r\n            * have mercy upon me, and hear me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My heart hath talked of thee, Seek ye my face:\r\n            * Thy face, LORD, will I seek.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O hide not thou thy face from me,\r\n            * nor cast thy servant away in displeasure.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast been my succour;\r\n            * leave me not, neither forsake me, O God of my salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When my father and my mother forsake me,\r\n            * the LORD taketh me up.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Teach me thy way, O LORD,\r\n            * and lead me in the right way, because of mine enemies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Deliver me not over into the will of mine adversaries:\r\n            * for there are false witnesses risen up against me,\r\n            and such as speak wrong.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I should utterly have fainted,\r\n            * but that I believe verily to see the goodness of the LORD\r\n            in the land of the living.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O tarry thou the LORD\'S leisure;\r\n            * be strong, and he shall comfort thine heart; and put thou\r\n            thy trust in the LORD.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 28\r\n    </h3>\r\n</div>\r\n\r\n<div class=\"text-center\"><h3>The Twenty-Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Ad te, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            UNTO thee will I cry, O LORD, my strength: * think no scorn of me; lest, if thou make as though thou\r\n            hearest not, I become like them that go down into the pit.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hear the voice of my humble petitions, when I cry unto thee; * when I hold up my hands towards the\r\n            mercy-seat of thy holy temple.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O pluck me not away, neither destroy me with the ungodly and wicked doers, * which speak friendly to\r\n            their neighbours, but imagine mischief in their hearts.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Reward them according to their deeds, * and according to the wickedness of their own inventions.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Recompense them after the work of their hands; * pay them that they have deserved.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For they regard not in their mind the works of the LORD, nor the operation of his hands; * therefore shall\r\n            he break them down, and not build them up.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praised be the LORD; * for he hath heard the voice of my humble petitions.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is my strength, and my shield; my heart hath trusted in him, and I am helped; * therefore my\r\n            heart danceth for joy, and in my song will I praise him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is my strength, * and he is the wholesome defence of his anointed.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O save thy people, and give thy blessing unto thine inheritance: * feed them, and set them up for ever.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 29\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Twenty-Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Afferte Domino.</i></h4></div>\r\n<hr/>\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            ASCRIBE unto the LORD, O ye mighty, * ascribe unto the LORD worship and strength.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ascribe unto the LORD the honour due unto his Name; * worship the LORD with holy worship.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The voice of the LORD is upon the waters; * it is the glorious God that maketh the thunder.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            It is the LORD that ruleth the sea; the voice of the  LORD is mighty in operation; * the voice of the LORD\r\n            is a glorious voice.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The voice of the LORD breaketh the cedar-trees; * yea, the LORD breaketh the cedars of Lebanon.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He maketh them also to skip like a calf; * Lebanon also, and Sirion, like a young unicorn.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The voice of the LORD divideth the flames of fire; the voice of the LORD shaketh the wilderness; * yea,\r\n            the LORD shaketh the wilderness of Kadesh.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The voice of the LORD maketh the hinds to bring  forth young, and strippeth bare the forests: * in his\r\n            temple doth every thing speak of his honour.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD sitteth above the water-flood, * and the LORD remaineth a King for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD shall give strength unto his people; * the LORD shall give his people the blessing of peace.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Evening', '0000-00-00 00:00:00'),
(36, '6', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 6, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 32\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirty-Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Beati quorum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BLESSED is he whose unrighteousness is forgiven,\r\n            * and whose sin is covered.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed is the man unto whom the LORD\r\n            imputeth no sin,\r\n            * and in whose spirit there is no guile.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For whilst I held my tongue,\r\n            * my bones consumed away through my daily complaining.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thy hand was heavy upon me day and night,\r\n            * and my moisture was like the drought in summer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I acknowledged my sin unto thee;\r\n            * and mine unrighteousness have I not hid.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I said, I will confess my sins unto the LORD;\r\n            * and so thou forgavest the wickedness of my sin.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For this shall every one that is godly make his prayer\r\n            unto thee, in a time when thou mayest be found;\r\n            * surely the great water-floods shall not come nigh him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou art a place to hide me in; thou shalt preserve\r\n            me from trouble;\r\n            * thou shalt compass me about with songs of deliverance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will inform thee, and teach thee in the way wherein\r\n            thou shalt go;\r\n            * and I will guide thee with mine eye.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Be ye not like to horse and mule, which have\r\n            no understanding;\r\n            * whose mouths must be held with bit and bridle,\r\n            else they will not obey thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Great plagues remain for the ungodly;\r\n            * but whoso putteth his trust in the LORD, mercy\r\n            embraceth him on every side.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Be glad, O ye righteous, and rejoice in the LORD;\r\n            * and be joyful, all ye that are true of heart.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 33\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirty-Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exultate, justi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            REJOICE in the LORD, O ye righteous; * for it be-cometh well the just to be thankful.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise the LORD with harp; * sing praises unto him with the lute, and instrument of ten strings.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Sing unto the Lord a new song; * sing praises lustily unto him with a good courage.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the word of the LORD is true; * and all his works are faithful.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He loveth righteousness and judgment; * the earth is full of the goodness of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            By the word of the LORD were the heavens made; * and all the host of them by the breath of his\r\n            mouth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He gathereth the waters of the sea together, as it were upon an heap; * and layeth up the deep, as in a\r\n            treasure-house.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let all the earth fear the LORD: * stand in awe of him, all ye that dwell in the world.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he spake, and it was done; * he commanded, and it stood fast.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD bringeth the counsel of the heathen to nought,\r\n            * and maketh the devices of the people to be of none effect, and casteth out the counsels of princes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The counsel of the LORD shall endure for ever, * and the thoughts of his heart from generation to\r\n            generation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed are the people whose God is the Lord JEHOVAH; * and blessed are the folk that he hath chosen\r\n            to him, to be his inheritance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD looketh down from heaven, and beholdeth all the children of men; * from the habitation of his\r\n            dwelling, he considereth all them that dwell on the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He fashioneth all the hearts of them, * and understandeth all their works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There is no king that can be saved by the multitude of an host; * neither is any mighty man delivered by\r\n            much strength.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            A horse is counted but a vain thing to save a man;\r\n            * neither shall he deliver any man by his great strength.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, the eye of the LORD is upon them that fear him,\r\n            * and upon them that put their trust in his mercy;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            To deliver their soul from death, * and to feed them in the time of dearth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Our soul hath patiently tarried for the LORD; * for he is our help and our shield.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For our heart shall rejoice in him; * because we have hoped in his holy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let thy merciful kindness, O LORD, be upon us, * like as we do put our trust in thee.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 34\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirty-Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Benedicam Dominum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I WILL alway give thanks unto the LORD;\r\n            * his praise shall ever be in my mouth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul shall make her boast in the LORD;\r\n            * the humble shall hear thereof, and be glad.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O praise the LORD with me,\r\n            * and let us magnify his Name together.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I sought the LORD, and he heard me;\r\n            * yea, he delivered me out of all my fear.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They had an eye unto him, and were lightened;\r\n            * and their faces were not ashamed.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lo, the poor crieth, and the LORD heareth him;\r\n            * yea, and saveth him out of all his troubles.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The angel of the LORD tarrieth round about them\r\n            that fear him;\r\n            * and delivereth them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O taste, and see, how gracious the LORD is:\r\n            * blessed is the man that trusteth in him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O fear the LORD, ye that are his saints;\r\n            * for they that fear him lack nothing.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The lions do lack, and suffer hunger;\r\n            * but they who seek the LORD shall want no manner of\r\n            thing that is good.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Come, ye children, and hearken unto me;\r\n            * I will teach you the fear of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            What man is he that lusteth to live,\r\n            * and would fain see good days?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Keep thy tongue from evil,\r\n            * and thy lips, that they speak no guile.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Eschew evil, and do good;\r\n            * seek peace, and ensue it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The eyes of the LORD are over the righteous,\r\n            * and his ears are open unto their prayers.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The countenance of the LORD is against them that\r\n            do evil,\r\n            * to root out the remembrance of them from the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The righteous cry, and the LORD heareth them,\r\n            * and delivereth them out of all their troubles.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is nigh unto them that are of a contrite heart, * and will save such as be of an humble\r\n            spirit.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Great are the troubles of the righteous;\r\n            * but the LORD delivereth him out of all.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He keepeth all his bones,\r\n            * so that not one of them is broken.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But misfortune shall slay the ungodly;\r\n            * and they that hate the righteous shall be desolate.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD delivereth the souls of his servants;\r\n            * and all they that put their trust in him shall not\r\n            be destitute.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(37, '7', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 7, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 37\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Thirty-Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Noli aemulari.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        FRET not thyself because of the ungodly;\r\n        * neither be thou envious against the evil doers.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For they shall soon be cut down like the grass,\r\n        * and be withered even as the green herb.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Put thou thy trust in the LORD, and be doing good;\r\n        * dwell in the land, and verily thou shalt be fed.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Delight thou in the LORD,\r\n        * and he shall give thee thy heart\'s desire.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Commit thy way unto the LORD, and put thy\r\n        trust in him,\r\n        * and he shall bring it to pass.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He shall make thy righteousness as clear as the light,\r\n        * and thy just dealing as the noon-day.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Hold thee still in the LORD, and abide patiently\r\n        upon him:\r\n        * but grieve not thyself at him whose way doth prosper,\r\n        against the man that doeth after evil counsels.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Leave off from wrath, and let go displeasure:\r\n        * fret not thyself, else shalt thou be moved to do evil.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Wicked doers shall be rooted out;\r\n        * and they that patiently abide the LORD, those\r\n        shall inherit the land.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Yet a little while, and the ungodly shall be clean gone:\r\n        * thou shalt look after his place, and he shall be away.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But the meek-spirited shall possess the earth,\r\n        * and shall be refreshed in the multitude of peace.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The ungodly seeketh counsel against the just,\r\n        * and gnasheth upon him with his teeth.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The Lord shall laugh him to scorn;\r\n        * for he hath seen that his day is coming.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The ungodly have drawn out the sword, and have\r\n        bent their bow,\r\n        * to cast down the poor and needy, and to slay such as\r\n        be upright in their ways.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Their sword shall go through their own heart,\r\n        * and their bow shall be broken.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        A small thing that the righteous hath,\r\n        * is better than great riches of the ungodly.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For the arms of the ungodly shall be broken,\r\n        * and the LORD upholdeth the righteous.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The LORD knoweth the days of the godly;\r\n        * and their inheritance shall endure for ever.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They shall not be confounded in the perilous time;\r\n        * and in the days of dearth they shall have enough.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        As for the ungodly, they shall perish, and the enemies of\r\n        the LORD shall consume as the fat of lambs:\r\n        * yea, even as the smoke shall they consume away.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The ungodly borroweth, and payeth not again;\r\n        * but the righteous is merciful and liberal.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Such as are blessed of God, shall possess the land;\r\n        * and they that are cursed of him, shall be rooted out.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The LORD ordereth a good man\'s going,\r\n        * and maketh his way acceptable to himself.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Though he fall, he shall not be cast away;\r\n        * for the LORD upholdeth him with his hand.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <i>vs 25 </i>\r\n        I have been young, and now am old;\r\n        * and yet saw I never the righteous forsaken,\r\n        nor his seed begging their bread.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 26 </i>The righteous is ever merciful, and lendeth;\r\n        * and his seed is blessed.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Flee from evil, and do the thing that is good;\r\n        * and dwell for evermore.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For the LORD loveth the thing that is right;\r\n        * he forsaketh not his that be godly, but they are\r\n        preserved for ever.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The unrighteous shall be punished;\r\n        * as for the seed of the ungodly, it shall be rooted out.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The righteous shall inherit the land,\r\n        * and dwell therein for ever.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The mouth of the righteous is exercised in wisdom,\r\n        * and his tongue will be talking of judgment.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The law of his God is in his heart,\r\n        * and his goings shall not slide.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The ungodly watcheth the righteous,\r\n        * and seeketh occasion to slay him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The LORD will not leave him in his hand,\r\n        * nor condemn him when he is judged.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Hope thou in the LORD, and keep his way, and he shall\r\n        promote thee, that thou shalt possess the land:\r\n        * when the ungodly shall perish, thou shalt see it.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I myself have seen the ungodly in great power,\r\n        * and flourishing like a green bay-tree.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I went by, and lo, he was gone:\r\n        * I sought him, but his place could no where be found.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Keep innocency, and take heed unto the thing\r\n        that is right;\r\n        * for that shall bring a man peace at the last.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        As for the transgressors, they shall perish together;\r\n        * and the end of the ungodly is, they shall be rooted\r\n        out at the last.<br/>\r\n        But the salvation of the righteous cometh of the LORD;\r\n        * who is also their strength in the time of trouble.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And the LORD shall stand by them, and save them:\r\n        * he shall deliver them from the ungodly, and shall save\r\n        them, because they put their trust in him.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(38, '8', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 8, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 41\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Forty-First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Beatus qui intelligit.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BLESSED is he that considereth the poor and needy;\r\n            * the LORD shall deliver him in the time of trouble.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD preserve him, and keep him alive, that he may be blessed upon earth;\r\n            * and deliver not thou him into the will of his enemies. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD comfort him when he lieth sick upon his bed;\r\n            * make thou all his bed in his sickness. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            I said, LORD, be merciful unto me;\r\n            * heal my soul, for I have sinned against thee. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Mine enemies speak evil of me,\r\n            * When shall he die, and his name perish? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            And if he come to see me, he speaketh vanity,\r\n            * and his heart conceiveth falsehood within himself; and when he cometh forth, he telleth it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All mine enemies whisper together against me;\r\n            * even against me do they imagine this evil. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            An evil disease, say they, cleaveth fast unto him;\r\n            * and now that he lieth, he shall rise up no more. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, even mine own familiar friend whom I trusted,\r\n            * who did also eat of my bread, hath laid great wait for me. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            But be thou merciful unto me, O LORD;\r\n            * raise thou me up again, and I shall reward them. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            By this I know thou favourest me,\r\n            * that mine enemy doth not triumph against me. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            And in my innocency thou upholdest me,\r\n            * and shalt set me before thy face for ever. <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed be the LORD God of Israel,\r\n            * world without end. Amen.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 42\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Forty-Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Quemadmodum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            LIKE as the hart desireth the water-brooks,\r\n            * so longeth my soul after thee, O God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul is athirst for God, yea, even for the living God:\r\n            * when shall I come to appear before the presence of God?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My tears have been my meat day and night,\r\n            * while they daily say unto me, Where is now thy God? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Now when I think thereupon, I pour out my\r\n            heart by myself;\r\n            * for I went with the multitude, and brought them forth\r\n            into the house of God; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            In the voice of praise and thanksgiving,\r\n            * among such as keep holy-day.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Why art thou so full of heaviness, O my soul?\r\n            * and why art thou so disquieted within me? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            O put thy trust in God;\r\n            * for I will yet thank him, which is the help of my\r\n            countenance, and my God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul is vexed within me;\r\n            * therefore will I remember thee from the land of Jordan,\r\n            from Hermon and the little hill.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            One deep calleth another, because of the noise of\r\n            thy water-floods;\r\n            * all thy waves and storms are gone over me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD will grant his loving-kindness\r\n            in the daytime;\r\n            * and in the night season will I sing of him, and make\r\n            my prayer unto the God of my life.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will say unto the God of my strength, Why hast\r\n            thou forgotten me?\r\n            * why go I thus heavily, while the enemy oppresseth me? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            My bones are smitten asunder as with a sword,\r\n            * while mine enemies that trouble me cast me in the teeth; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Namely, while they say daily unto me,\r\n            * Where is now thy God? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            Why art thou so vexed, O my soul?\r\n            * and why art thou so disquieted within me? <br/>\r\n            <small> &nbsp; <br/></small>\r\n            O put thy trust in God;\r\n            * for I will yet thank him, which is the help of my\r\n            countenance, and my God.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 43\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Forty-Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Judica me, Deus</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            GIVE sentence with me, O God, and defend my cause\r\n            against the ungodly people;\r\n            * O deliver me from the deceitful and wicked man.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou art the God of my strength; why hast thou\r\n            put me from thee?\r\n            * and why go I so heavily, while the enemy oppresseth me?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O send out thy light and thy truth, that they\r\n            may lead me,\r\n            * and bring me unto thy holy hill, and to thy dwelling; <br/>\r\n            <small> &nbsp; <br/></small>\r\n            And that I may go unto the altar of God, even unto\r\n            the God of my joy and gladness;\r\n            * and upon the harp will I give thanks unto thee,\r\n            O God, my God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Why art thou so heavy, O my soul?\r\n            * and why art thou so disquieted within me?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O put thy trust in God;\r\n            * for I will yet give him thanks, which is the help of my\r\n            countenance, and my God.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(39, '9', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 9, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 47\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Forty-Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Omnes gentes, plaudite.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O CLAP your hands together, all ye peoples:\r\n            * O sing unto God with the voice of melody.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the LORD is high, and to be feared;\r\n            * he is the great King upon all the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall subdue the peoples under us,\r\n            * and the nations under our feet.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall choose out an heritage for us,\r\n            * even the excellency of Jacob, whom he loved.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God is gone up with a merry noise,\r\n            * and the LORD with the sound of the trump.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O sing praises, sing praises unto our God;\r\n            * O sing praises, sing praises unto our King.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For God is the King of all the earth:\r\n            * sing ye praises with understanding.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God reigneth over the nations;\r\n            * God sitteth upon his holy seat.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The princes of the peoples are joined unto the people of  the God of Abraham;\r\n            * for God, which is very high exalted, doth defend the earth, as it were with a shield.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 48\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Forty-Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Magnus Dominus.</i></h4></div>\r\n<hr/>\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            GREAT is the LORD, and highly to be praised * in the city of our God, even upon his holy hill.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The hill of Sion is a fair place, and the joy of the whole earth; * upon the north side lieth the city of the\r\n            great King: God is well known in her palaces as a sure refuge.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For lo, the kings of the earth * were gathered, and gone by together.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They marvelled to see such things; * they were astonished, and suddenly cast down.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Fear came there upon them; and sorrow, * as upon a woman in her travail.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou dost break the ships of the sea * through the east-wind.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Like as we have heard, so have we seen in the city of the LORD of hosts, in the city of our God; * God\r\n            upholdeth the same for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            We wait for thy loving-kindness, O God, * in the midst of thy temple.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O God, according to thy Name, so is thy praise unto the world\'s end; * thy right hand is full of\r\n            righteousness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the mount Sion rejoice, and the daughters of Judah be glad, * because of thy judgments.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Walk about Sion, and go round about her; * and tell the towers thereof.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Mark well her bulwarks, consider her palaces, * that ye may tell them that come after.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For this God is our God for ever and ever: * he shall be our guide unto death.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 49\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Forty-Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Audite haec, omnes.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O HEAR ye this, all ye people;\r\n            * ponder it with your ears, all ye that dwell in the world;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            High and low, rich and poor,\r\n            * one with another.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My mouth shall speak of wisdom,\r\n            * and my heart shall muse of understanding.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will incline mine ear to the parable,\r\n            * and show my dark speech upon the harp.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wherefore should I fear in the days of evil,\r\n            * when wickedness at my heels compasseth me round about?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There be some that put their trust in their goods,\r\n            * and boast themselves in the multitude of their riches.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But no man may deliver his brother,\r\n            * nor give a ransom unto God for him,<br/>\r\n            <small> &nbsp; <br/></small>\r\n            (For it cost more to redeem their souls,\r\n            * so that he must let that alone for ever;)<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That he shall live alway,\r\n            * and not see the grave.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he seeth that wise men also die and perish together,\r\n            * as well as the ignorant and foolish, and leave their riches for other.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And yet they think that their houses shall continue for ever, and that their dwelling-places shall endure from one generation to another;\r\n            * and call the lands after their own names.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Nevertheless, man being in honour abideth not,\r\n            * seeing he may be compared unto the beasts that perish;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            This their way is very foolishness;\r\n            * yet their posterity praise their saying.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They lie in the grave like sheep; death is their shepherd; and the righteous shall have dominion over them in the morning:\r\n            * their beauty shall consume in the sepulchre, and have no abiding.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But God hath delivered my soul from the power of the grave;\r\n            * for he shall receive me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Be not thou afraid, though one be made rich,\r\n            * or if the glory of his house be increased;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he shall carry nothing away with him when he dieth,\r\n            * neither shall his pomp follow him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For while he lived, he counted himself an happy man;\r\n            * and so long as thou doest well unto thyself, men will speak good of thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall follow the generation of his fathers,\r\n            * and shall never see light.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Man that is in honour but hath no understanding\r\n            * is compared unto the beasts that perish.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(40, '10', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 10, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 53\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifty-Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dixit insipiens.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE foolish body hath said in his heart,\r\n            *  There is no God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Corrupt are they, and become abominable in their wickedness;\r\n            *  there is none that doeth good.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God looked down from heaven upon the children of men,\r\n            *  to see if there were any that would understand, and seek after God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But they are all gone out of the way, they are altogether become abominable;\r\n            *  there is also none that doeth good, no not one.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Are not they without understanding that work wickedness,\r\n            *  eating up my people as if they would eat bread?  they have not called upon God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They were afraid where no fear was;\r\n            *  for God hath broken the bones of him that besieged thee; thou hast put them to confusion, because God hath despised them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O that the salvation were given unto Israel out of Sion!\r\n            *  O that the Lord would deliver his people out of captivity!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then should Jacob rejoice,\r\n            *  and Israel should be right glad.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 54\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifty-Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, in nomine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            SAVE me, O God, for thy Name\'s sake,\r\n            * and avenge me in thy strength.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hear my prayer, O God,\r\n            * and hearken unto the words of my mouth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For strangers are risen up against me;\r\n            * and tyrants, which have not God before their eyes,\r\n            seek after my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, God is my helper;\r\n            * the Lord is with them that uphold my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall reward evil unto mine enemies:\r\n            * destroy thou them in thy truth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            An offering of a free heart will I give thee,\r\n            and praise thy Name, O LORD;\r\n            * because it is so comfortable.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he hath delivered me out of all my trouble;\r\n            * and mine eye hath seen his desire upon mine enemies.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 55\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifty-Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exaudi, Deus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HEAR my prayer, O God,\r\n            * and hide not thyself from my petition.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Take heed unto me, and hear me,\r\n            * how I mourn in my prayer, and am vexed;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The enemy crieth so, and the ungodly cometh on so fast;\r\n            * for they are minded to do me some mischief, so maliciously are they set against me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My heart is disquieted within me,\r\n            * and the fear of death is fallen upon me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Fearfulness and trembling are come upon me,\r\n            * and an horrible dread hath overwhelmed me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And I said, O that I had wings like a dove!\r\n            * for then would I flee away, and be at rest.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lo, then would I get me away far off,\r\n            * and remain in the wilderness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I would make haste to escape,\r\n            * because of the stormy wind and tempest.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Destroy their tongues, O Lord, and divide them;\r\n            * for I have spied unrighteousness and strife in the city.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Day and night they go about within the walls thereof:\r\n            * mischief also and sorrow are in the midst of it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wickedness is therein;\r\n            * deceit and guile go not out of her streets.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For it is not an open enemy that hath done me this  dishonour;\r\n            * for then I could have borne it;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Neither was it mine adversary that did magnify himself against me;\r\n            * for then peradventure I would have hid myself from him;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But it was even thou, my companion,\r\n            * my guide, and mine own familiar friend.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            We took sweet counsel together,\r\n            * and walked in the house of God as friends.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let death come hastily upon them, and let them go down alive into the pit;\r\n            * for wickedness is in their dwellings, and among them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for me, I will call upon God,\r\n            * and the LORD shall save me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In the evening, and morning, and at noon-day will I pray, and that instantly;\r\n            * and he shall hear my voice.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            It is he that hath delivered my soul in peace from the battle that was against me;\r\n            * for there were many that strove with me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, even God, that endureth for ever, shall hear me, and bring them down;\r\n            * for they will not turn, nor fear God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He laid his hands upon such as be at peace with him,\r\n            * and he brake his covenant.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The words of his mouth were softer than butter, having war in his heart;\r\n            * his words were smoother than oil, and yet be they very swords.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O cast thy burden upon the LORD, and he shall nourish thee,\r\n            * and shall not suffer the righteous to fall for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And as for them,\r\n            * thou, O God, shalt bring them into the pit of destruction.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The blood-thirsty and deceitful men shall not live out half their days:\r\n            * nevertheless, my trust shall be in thee, O Lord.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(41, '11', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 11, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 59\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Fifty-Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Eripe me de inimicis.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            DELIVER me from mine enemies, O God;\r\n            *  defend me from them that rise up against me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O deliver me from the wicked doers,\r\n            *  and save me from the blood-thirsty men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For lo, they lie waiting for my soul;\r\n            *  the mighty men are gathered against me, without any offence or fault of me, O LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They run and prepare themselves without my fault;\r\n            *  arise thou therefore to help me, and behold.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Stand up, O LORD God of hosts, thou God of Israel, to visit all the heathen,\r\n            *  and be not merciful unto them that offend of malicious wickedness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They go to and fro in the evening,\r\n            *  they grin like a dog, and run about through the city.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, they speak with their mouth, and swords are in their lips;\r\n            *  for who doth hear?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But thou, O LORD, shalt have them in derision,\r\n            *  and thou shalt laugh all the heathen to scorn.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My strength will I ascribe unto thee;\r\n            *  for thou art the God of my refuge.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God showeth me his goodness plenteously;\r\n            *  and God shall let me see my desire upon mine enemies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Slay them not, lest my people forget it;\r\n            *  but scatter them abroad among the people, and put them down, O Lord our defence.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the sin of their mouth, and for the words of their lips, they shall be taken in their pride:\r\n            *  and why? their talk is of cursing and lies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Consume them in thy wrath, consume them, that they may perish;\r\n            *  and know that it is God that ruleth in Jacob, and unto the ends of the world.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And in the evening they will return,\r\n            *  grin like a dog, and will go about the city.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They will run here and there for meat,\r\n            *  and grudge if they be not satisfied.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for me, I will sing of thy power, and will praise thy mercy betimes in the morning;\r\n            *  for thou hast been my defence and refuge in the day of my trouble.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Unto thee, O my strength, will I sing;\r\n            *  for thou, O God, art my refuge, and my merciful God.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 60\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixtieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, repulisti nos.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O GOD, thou hast cast us out, and scattered us abroad;\r\n            *  thou hast also been displeased: O turn thee unto us again.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast moved the land, and divided it:\r\n            *  heal the sores thereof, for it shaketh.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast showed thy people heavy things;\r\n            *  thou hast given us a drink of deadly wine.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast given a token for such as fear thee,\r\n            *  that they may triumph because of the truth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore were thy beloved delivered;\r\n            *  help me with thy right hand, and hear me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God hath spoken in his holiness, I will rejoice, and divide Shechem,\r\n            *  and mete out the valley of Succoth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Gilead is mine, and Manasseh is mine;\r\n            *  Ephraim also is the strength of my head; Judah is my law-giver;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Moab is my wash-pot; over Edom will I cast out my shoe;\r\n            *  Philistia, be thou glad of me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who will lead me into the strong city?\r\n            *  who will bring me into Edom?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hast not thou cast us out, O God?\r\n            *  wilt not thou, O God, go out with our hosts?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O be thou our help in trouble;\r\n            *  for vain is the help of man.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Through God will we do great acts;\r\n            *  for it is he that shall tread down our enemies.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 61\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixty-First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Exaudi, Deus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HEAR my crying, O God,\r\n            * give ear unto my prayer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            From the ends of the earth will I call upon thee,\r\n            * when my heart is in heaviness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O set me up upon the rock that is higher than I;\r\n            * for thou hast been my hope, and a strong tower for me against the enemy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will dwell in thy tabernacle for ever,\r\n            * and my trust shall be under the covering of thy wings.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou, O Lord, hast heard my desires,\r\n            * and hast given an heritage unto those that fear thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt grant the King a long life,\r\n            * that his years may endure throughout all generations.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall dwell before God for ever:\r\n            * O prepare thy loving mercy and faithfulness, that they may preserve him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So will I alway sing praise unto thy Name,\r\n            * that I may daily perform my vows.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(42, '12', '\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 12, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 65\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixty-Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Te decet hymnus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THOU, O God, art praised in Sion;\r\n            * and unto thee shall the vow be performed in Jerusalem.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou that hearest the prayer,\r\n            * unto thee shall all flesh come.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My misdeeds prevail against me:\r\n            * O be thou merciful unto our sins.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed is the man whom thou choosest, and receivest\r\n            unto thee:\r\n            * he shall dwell in thy court, and shall be satisfied with the\r\n            pleasures of thy house, even of thy holy temple.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt show us wonderful things in thy\r\n            righteousness, O God of our salvation;\r\n            * thou that art the hope of all the ends of the earth,\r\n            and of them that remain in the broad sea.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who in his strength setteth fast the mountains,\r\n            * and is girded about with power.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who stilleth the raging of the sea,\r\n            * and the noise of his waves, and the madness of\r\n            the peoples.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They also that dwell in the uttermost parts of the earth\r\n            shall be afraid at thy tokens,\r\n            * thou that makest the out-goings of the morning and\r\n            evening to praise thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou visitest the earth, and blessest it;\r\n            * thou makest it very plenteous.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The river of God is full of water:\r\n            * thou preparest their corn, for so thou providest\r\n            for the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou waterest her furrows; thou sendest rain into the\r\n            little valleys thereof;\r\n            * thou makest it soft with the drops of rain, and blessest\r\n            the increase of it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou crownest the year with thy goodness;\r\n            * and thy clouds drop fatness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They shall drop upon the dwellings of the wilderness;\r\n            * and the little hills shall rejoice on every side.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The folds shall be full of sheep;\r\n            * the valleys also shall stand so thick with corn, that they\r\n            shall laugh and sing.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 66\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixty-Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Jubilate Deo.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O BE joyful in God, all ye lands;\r\n            * sing praises unto the honour of his Name; make his\r\n            praise to be glorious.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Say unto God, O how wonderful art thou in thy works!\r\n            * through the greatness of thy power shall thine enemies\r\n            bow down unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For all the world shall worship thee,\r\n            * sing of thee, and praise thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O come hither, and behold the works of God;\r\n            * how wonderful he is in his doing toward the\r\n            children of men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He turned the sea into dry land,\r\n            * so that they went through the water on foot; there did\r\n            we rejoice thereof.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He ruleth with his power for ever; his eyes behold\r\n            the nations:\r\n            * and such as will not believe shall not be able to\r\n            exalt themselves.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O praise our God, ye peoples,\r\n            * and make the voice of his praise to be heard;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who holdeth our soul in life;\r\n            * and suffereth not our feet to slip.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou, O God, hast proved us;\r\n            * thou also hast tried us, like as silver is tried.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou broughtest us into the snare;\r\n            * and laidest trouble upon our loins.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou sufferedst men to ride over our heads;\r\n            * we went through fire and water, and thou broughtest\r\n            us out into a wealthy place.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will go into thine house with burnt-offerings, and will\r\n            pay thee my vows,\r\n            * which I promised with my lips, and spake with my\r\n            mouth, when I was in trouble.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will offer unto thee fat burnt-sacrifices, with the\r\n            incense of rams;\r\n            * I will offer bullocks and goats.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O come hither, and hearken, all ye that fear God;\r\n            * and I will tell you what he hath done for my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I called unto him with my mouth,\r\n            * and gave him praises with my tongue.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If I incline unto wickedness with mine heart,\r\n            * the Lord will not hear me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But God hath heard me;\r\n            * and considered the voice of my prayer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praised be God, who hath not cast out my prayer,\r\n            * nor turned his mercy from me.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 67\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixty-Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus misereatur.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            God be merciful unto us, and bless us,\r\n            * and show us the light of his countenance, and be\r\n            merciful unto us;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That thy way may be known upon earth,\r\n            * thy saving health among all nations.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the peoples praise thee, O God;\r\n            * yea, let all the peoples praise thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let the nations rejoice and be glad;\r\n            * for thou shalt judge the folk righteously, and govern\r\n            the nations upon earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the peoples praise thee, O God;\r\n            * yea, let all the peoples praise thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then shall the earth bring forth her increase;\r\n            * and God, even our own God, shall give us his blessing.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            God shall bless us;\r\n            * and all the ends of the world shall fear him.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(43, '13', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 13, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 69\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Sixty-Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Salvum me fac.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        SAVE me, O God;\r\n        * for the waters are come in, even unto my soul.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I stick fast in the deep mire, where no ground is;\r\n        * I am come into deep waters, so that the floods run over me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I am weary of crying; my throat is dry;\r\n        * my sight faileth me for waiting so long upon my God.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They that hate me without a cause are more than the hairs of my head;\r\n        * they that are mine enemies, and would destroy me guiltless, are mighty.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I paid them the things that I never took:\r\n        * God, thou knowest my simpleness, and my faults are not hid from thee.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let not them that trust in thee, O Lord GOD of hosts, be ashamed for my cause;\r\n        * let not those that seek thee be confounded through me, O Lord God of Israel.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And why? for thy sake have I suffered reproof;\r\n        * shame hath covered my face.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I am become a stranger unto my brethren,\r\n        * even an alien unto my mother\'s children.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For the zeal of thine house hath even eaten me;\r\n        * and the rebukes of them that rebuked thee are fallen upon me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I wept, and chastened myself with fasting,\r\n        * and that was turned to my reproof.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I put on sackcloth also,\r\n        * and they jested upon me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They that sit in the gate speak against me,\r\n        * and the drunkards make songs upon me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But, LORD, I make my prayer unto thee\r\n        * in an acceptable time.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Hear me, O God, in the multitude of thy mercy,\r\n        * even in the truth of thy salvation.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Take me out of the mire, that I sink not;\r\n        * O let me be delivered from them that hate me, and out of the deep waters.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let not the water-flood drown me, neither let the deep swallow me up;\r\n        * and let not the pit shut her mouth upon me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Hear me, O LORD, for thy loving-kindness is comfortable;\r\n        * turn thee unto me according to the multitude of thy mercies.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And hide not thy face from thy servant; for I am in trouble:\r\n        * O haste thee, and hear me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Draw nigh unto my soul, and save it;\r\n        * O deliver me, because of mine enemies.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast known my reproach, my shame, and my dishonour:\r\n        * mine adversaries are all in thy sight.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Reproach hath broken my heart; I am full of heaviness:\r\n        * I looked for some to have pity on me, but there was no man, neither found I any to comfort me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They gave me gall to eat;\r\n        * and when I was thirsty they gave me vinegar to drink.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 23 </i>Let their table be made a snare to take themselves withal;\r\n        * and let the things that should have been for their wealth be unto them an occasion of falling.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let their eyes be blinded, that they see not;\r\n        * and ever bow thou down their backs.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Pour out thine indignation upon them,\r\n        * and let thy wrathful displeasure take hold of them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let their habitation be void,\r\n        * and no man to dwell in their tents.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For they persecute him whom thou hast smitten;\r\n        * and they talk how they may vex them whom thou hast wounded.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let them fall from one wickedness to another,\r\n        * and not come into thy righteousness.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let them be wiped out of the book of the living,\r\n        * and not be written among the righteous.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 30 </i>As for me, when I am poor and in heaviness,\r\n        * thy help, O God, shall lift me up.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will praise the Name of God with a song,\r\n        * and magnify it with thanksgiving.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        This also shall please the LORD\r\n        * better than a bullock that hath horns and hoofs.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The humble shall consider this, and be glad:\r\n        * seek ye after God, and your soul shall live.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For the LORD heareth the poor,\r\n        * and despiseth not his prisoners.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let heaven and earth praise him:\r\n        * the sea, and all that moveth therein.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For God will save Sion, and build the cities of Judah,\r\n        * that men may dwell there, and have it in possession.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The posterity also of his servants shall inherit it;\r\n        * and they that love his Name shall dwell therein.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 70\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, in adjutorium.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HASTE thee, O God, to deliver me;\r\n            *  make haste to help me, O LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them be ashamed and confounded that seek after my soul;\r\n            *  let them be turned backward and put to confusion that wish me evil.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them for their reward be soon brought to shame,\r\n            *  that cry over me, There! there!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But let all those that seek thee be joyful and glad in thee:\r\n            *  and let all such as delight in thy salvation say alway, The Lord be praised.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for me, I am poor and in misery:\r\n            *  haste thee unto me, O God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou art my helper, and my redeemer:\r\n            *  O LORD, make no long tarrying.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(44, '14', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 14, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 73\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventy-Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Quam bonus Israel!</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            TRULY God is loving unto Israel: * even unto such as are of a clean heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Nevertheless, my feet were almost gone, * my treadings had well-nigh slipt.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And why? I was grieved at the wicked: * I do also see the ungodly in such prosperity.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For they are in no peril of death; * but are lusty and strong.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They come in no misfortune like other folk; * neither are they plagued like other men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And this is the cause that they are so holden with pride, * and cruelty covereth them as a garment.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their eyes swell with fatness, * and they do even what they lust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They corrupt other, and speak of wicked blasphemy; * their talking is against the Most High.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For they stretch forth their mouth unto the heaven, * and their tongue goeth through the world.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore fall the people unto them, * and thereout suck they no small advantage.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Tush, say they, how should God perceive it? * is there knowledge in the Most High?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lo, these are the ungodly, * these prosper in the world, and these have riches in possession:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And I said, Then have I cleansed my heart in vain, * and washed my hands in innocency.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All the day long have I been punished, * and chastened every morning.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, and I had almost said even as they; * but lo, then I should have condemned the generation of thy\r\n            children.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then thought I to understand this; * but it was too hard for me,<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Until I went into the sanctuary of God: * then understood I the end of these men;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Namely, how thou dost set them in slippery places, * and castest them down, and destroyest them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O how suddenly do they consume, * perish, and come to a fearful end!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, even like as a dream when one awaketh; * so shalt thou make their image to vanish out of the\r\n            city.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thus my heart was grieved, * and it went even through my reins.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So foolish was I, and ignorant, * even as it were a beast before thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Nevertheless, I am alway by thee; * for thou hast holden me by my right hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou shalt guide me with thy counsel, * and after that receive me with glory.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whom have I in heaven but thee? * and there is none upon earth that I desire in comparison of thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My flesh and my heart faileth; * but God is the strength of my heart, and my portion for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For lo, they that forsake thee shall perish; * thou hast destroyed all them that are unfaithful unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But it is good for me to hold me fast by God, to put my trust in the Lord GOD, * and to speak of all thy\r\n            works in the gates of the daughter of Sion.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 74\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventy-Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Ut quid, Deus?</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O GOD, wherefore art thou absent from us so long?\r\n            * why is thy wrath so hot against the sheep of thy pasture?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O think upon thy congregation,\r\n            *  whom thou hast purchased, and redeemed of old.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Think upon the tribe of thine inheritance,\r\n            *  and Mount Sion, wherein thou hast dwelt.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lift up thy feet, that thou mayest utterly destroy every enemy,\r\n            *  which hath done evil in thy sanctuary.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thine adversaries roar in the midst of thy congregations,\r\n            *  and set up their banners for tokens.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He that hewed timber afore out of the thick trees,\r\n            *  was known to bring it to an excellent work.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But now they break down all the carved work thereof\r\n            *  with axes and hammers.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have set fire upon thy holy places,\r\n            *  and have defiled the dwelling-place of thy Name, even unto the ground.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, they said in their hearts, Let us make havoc of them altogether:\r\n            *  thus have they burnt up all the houses of God in the land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            We see not our tokens; there is not one prophet more;\r\n            *  no, not one is there among us, that understandeth any more.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O God, how long shall the adversary do this dishonour?\r\n            *  shall the enemy blaspheme thy Name for ever?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Why withdrawest thou thy hand?\r\n            *  why pluckest thou not thy right hand out of thy bosom to consume the enemy?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For God is my King of old;\r\n            *  the help that is done upon earth, he doeth it himself.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou didst divide the sea through thy power;\r\n            *  thou brakest the heads of the dragons in the waters.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou smotest the heads of leviathan in pieces,\r\n            *  and gavest him to be meat for the people of the wilderness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou broughtest out fountains and waters out of the hard rocks;\r\n            *  thou driedst up mighty waters.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The day is thine, and the night is thine;\r\n            *  thou hast prepared the light and the sun.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast set all the borders of the earth;\r\n            *  thou hast made summer and winter.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Remember this, O LORD, how the enemy hath rebuked;\r\n            *  and how the foolish people hath blasphemed thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O deliver not the soul of thy turtle-dove unto the multitude of the enemies;\r\n            *  and forget not the congregation of the poor for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Look upon the covenant;\r\n            *  for all the earth is full of darkness and cruel habitations.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let not the simple go away ashamed;\r\n            *  but let the poor and needy give praise unto thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Arise, O God, maintain thine own cause;\r\n            *  remember how the foolish man blasphemeth thee daily.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Forget not the voice of thine enemies:\r\n            *  the presumption of them that hate thee increaseth ever more and more.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(45, '15', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 15, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 78\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Seventy-Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Attendite, popule.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HEAR my law, O my people;\r\n            * incline your ears unto the words of my mouth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will open my mouth in a parable;\r\n            * I will declare hard sentences of old;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Which we have heard and known,\r\n            * and such as our fathers have told us;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That we should not hide them from the children of the generations to come;\r\n            * but to show the honour of the LORD, his mighty and wonderful works that he hath done.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He made a covenant with Jacob, and gave Israel a law,\r\n            * which he commanded our forefathers to teach their children;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That their posterity might know it,\r\n            * and the children which were yet unborn;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            To the intent that when they came up,\r\n            * they might show their children the same;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That they might put their trust in God;\r\n            * and not to forget the works of God, but to keep his commandments;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And not to be as their forefathers, a faithless and stubborn generation;\r\n            * a generation that set not their heart aright, and whose spirit clave not stedfastly unto God;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Like as the children of Ephraim;\r\n            * who being harnessed, and carrying bows, turned themselves back in the day of battle.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They kept not the covenant of God,\r\n            * and would not walk in his law;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But forgat what he had done,\r\n            * and the wonderful works that he had showed for them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Marvellous things did he in the sight of our forefathers, in the land of Egypt,\r\n            * even in the field of Zoan.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He divided the sea, and let them go through;\r\n            * he made the waters to stand on an heap.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            In the day-time also he led them with a cloud,\r\n            * and all the night through with a light of fire.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He clave the hard rocks in the wilderness,\r\n            * and gave them drink thereof, as it had been out of the great depth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He brought waters out of the stony rock,\r\n            * so that it gushed out like the rivers.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yet for all this they sinned more against him,\r\n            * and provoked the Most Highest in the wilderness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They tempted God in their hearts,\r\n            * and required meat for their lust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They spake against God also, saying,\r\n            * Shall God prepare a table in the wilderness?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He smote the stony rock indeed, that the water gushed out, and the streams flowed withal;\r\n            * but can he give bread also, or provide flesh for his people?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When the LORD heard this, he was wroth;\r\n            * so the fire was kindled in Jacob, and there came up heavy displeasure against Israel;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Because they believed not in God,\r\n            * and put not their trust in his help.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So he commanded the clouds above,\r\n            * and opened the doors of heaven.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He rained down manna also upon them for to eat,\r\n            * and gave them food from heaven.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So man did eat angels\' food;\r\n            * for he sent them meat enough.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He caused the east-wind to blow under heaven;\r\n            * and through his power he brought in the southwestwind.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He rained flesh upon them as thick as dust,\r\n            * and feathered fowls like as the sand of the sea.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He let it fall among their tents,\r\n            * even round about their habitation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So they did eat, and were well filled; for he gave them their own desire:\r\n            * they were not disappointed of their lust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But while the meat was yet in their mouths, the heavy wrath of God came upon them, and slew the wealthiest of them;\r\n            * yea, and smote down the chosen men that were in Israel.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But for all this they sinned yet more,\r\n            * and believed not his wondrous works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore their days did he consume in vanity,\r\n            * and their years in trouble.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When he slew them, they sought him,\r\n            * and turned them early, and inquired after God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And they remembered that God was their strength,\r\n            * and that the High God was their redeemer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Nevertheless, they did but flatter him with their mouth,\r\n            * and dissembled with him in their tongue.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For their heart was not whole with him,\r\n            * neither continued they stedfast in his covenant.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But he was so merciful, that he forgave their misdeeds,\r\n            * and destroyed them not.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, many a time turned he his wrath away,\r\n            * and would not suffer his whole displeasure to arise.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he considered that they were but flesh,\r\n            * and that they were even a wind that passeth away, and cometh not again.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Many a time did they provoke him in the wilderness,\r\n            * and grieved him in the desert.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They turned back, and tempted God,\r\n            * and provoked the Holy One in Israel.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They thought not of his hand,\r\n            * and of the day when he delivered them from the hand of the enemy;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            How he had wrought his miracles in Egypt,\r\n            * and his wonders in the field of Zoan.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He turned their waters into blood,\r\n            * so that they might not drink of the rivers.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He sent flies among them, and devoured them up;\r\n            * and frogs to destroy them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He gave their fruit unto the caterpillar,\r\n            * and their labour unto the grasshopper.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He destroyed their vines with hailstones,\r\n            * and their mulberry-trees with the frost.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He smote their cattle also with hailstones,\r\n            * and their flocks with hot thunderbolts.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He cast upon them the furiousness of his wrath, anger, displeasure, and trouble:\r\n            * and sent evil angels among them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He made a way to his indignation, and spared not their soul from death;\r\n            * but gave their life over to the pestilence;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And smote all the firstborn in Egypt,\r\n            * the most principal and mightiest in the dwellings of Ham.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But as for his own people, he led them forth like sheep,\r\n            * and carried them in the wilderness like a flock.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He brought them out safely, that they should not fear,\r\n            * and overwhelmed their enemies with the sea.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And brought them within the borders of his sanctuary,\r\n            * even to this mountain, which he purchased with his right hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He cast out the heathen also before them,\r\n            * caused their land to be divided among them for an heritage, and made the tribes of Israel to dwell in their tents.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yet they tempted and displeased the Most High God,\r\n            * and kept not his testimonies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They turned their backs, and fell away like their forefathers;\r\n            * starting aside like a broken bow.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For they grieved him with their hill-altars,\r\n            * and provoked him to displeasure with their images.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When God heard this, he was wroth,\r\n            * and took sore displeasure at Israel;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So that he forsook the tabernacle in Shiloh,\r\n            * even the tent that he had pitched among men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He delivered their power into captivity,\r\n            * and their beauty into the enemy\'s hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He gave his people over also unto the sword,\r\n            * and was wroth with his inheritance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The fire consumed their young men,\r\n            * and their maidens were not given in marriage.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their priests were slain with the sword,\r\n            * and there were no widows to make lamentation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So the Lord awaked as one out of sleep,\r\n            * and like a giant refreshed with wine.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He drave his enemies backward,\r\n            * and put them to a perpetual shame.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He refused the tabernacle of Joseph,\r\n            * and chose not the tribe of Ephraim;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But chose the tribe of Judah,\r\n            * even the hill of Sion which he loved.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And there he built his temple on high,\r\n            * and laid the foundation of it like the ground which he hath made continually.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He chose David also his servant,\r\n            * and took him away from the sheep-folds:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As he was following the ewes with their young he took him,\r\n            * that he might feed Jacob his people, and Israel his inheritance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So he fed them with a faithful and true heart,\r\n            * and ruled them prudently with all his power.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(46, '16', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 16, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 82\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighty-Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus stetit.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            GOD standeth in the congregation of princes;\r\n            * he is a Judge among gods.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            How long will ye give wrong judgment,\r\n            * and accept the persons of the ungodly?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Defend the poor and fatherless;\r\n            * see that such as are in need and necessity have right.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Deliver the outcast and poor;\r\n            * save them from the hand of the ungodly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They know not, neither do they understand, but walk on still in darkness:\r\n            * all the foundations of the earth are out of course.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I have said, Ye are gods,\r\n            * and ye are all the children of the Most Highest.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But ye shall die like men,\r\n            * and fall like one of the princes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Arise, O God, and judge thou the earth;\r\n            * for thou shalt take all nations to thine inheritance.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 83\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighty-Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, quis similis?</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HOLD not thy tongue, O God, keep not still silence:\r\n            *  refrain not thyself, O God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For lo, thine enemies make a murmuring;\r\n            *  and they that hate thee have lift up their head.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have imagined craftily against thy people,\r\n            *  and taken counsel against thy secret ones.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have said, Come, and let us root them out, that they be no more a people,\r\n            *  and that the name of Israel may be no more in remembrance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For they have cast their heads together with one consent,\r\n            *  and are confederate against thee:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The tabernacles of the Edomites, and the Ishmaelites;\r\n            *  the Moabites, and Hagarenes;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Gebal, and Ammon, and Amalek;\r\n            *  the Philistines, with them that dwell at Tyre.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Assyria also is joined with them;\r\n            *  they have holpen the children of Lot.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But do thou to them as unto the Midianites;\r\n            *  unto Sisera, and unto Jabin at the brook of Kishon;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who perished at Endor,\r\n            *  and became as the dung of the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Make them and their princes like Oreb and Zeeb;\r\n            *  yea, make all their princes like as Zebah and Zalmunna;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who say, Let us take to ourselves\r\n            *  the houses of God in possession.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O my God, make them like unto the whirling dust,\r\n            *  and as the stubble before the wind;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Like as the fire that burneth up the forest,\r\n            *  and as the flame that consumeth the mountains;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Pursue them even so with thy tempest,\r\n            *  and make them afraid with thy storm.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Make their faces ashamed, O LORD,\r\n            *  that they may seek thy Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them be confounded and vexed ever more and more;\r\n            *  let them be put to shame, and perish.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And they shall know that thou, whose Name is JEHOVAH,\r\n            *  art only the Most Highest over all the earth.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 84\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighty-Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Quam dilecta!</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O HOW amiable are thy dwellings,\r\n            * thou LORD of hosts!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul hath a desire and longing to enter into the courts\r\n            of the LORD;\r\n            * my heart and my flesh rejoice in the living God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, the sparrow hath found her an house, and the swallow\r\n            a nest, where she may lay her young;\r\n            * even thy altars, O LORD of hosts, my King and my God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed are they that dwell in thy house;\r\n            * they will be alway praising thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed is the man whose strength is in thee;\r\n            * in whose heart are thy ways.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who going through the vale of misery use it for a well;\r\n            * and the pools are filled with water.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They will go from strength to strength,\r\n            * and unto the God of gods appeareth every one of them in Sion.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O LORD God of hosts, hear my prayer;\r\n            * hearken, O God of Jacob.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Behold, O God our defender,\r\n            * and look upon the face of thine anointed.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For one day in thy courts\r\n            * is better than a thousand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I had rather be a door-keeper in the house of my God,\r\n            * than to dwell in the tents of ungodliness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the LORD God is a light and defence;\r\n            * the LORD will give grace and worship; and no good thing\r\n            shall he withhold from them that live a godly life.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O LORD God of hosts,\r\n            * blessed is the man that putteth his trust in thee.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 85\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighty-Fifth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Benedixisti, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            LORD, thou art become gracious unto thy land;\r\n            * thou hast turned away the captivity of Jacob.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast forgiven the offence of thy people,\r\n            * and covered all their sins.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast taken away all thy displeasure,\r\n            * and turned thyself from thy wrathful indignation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn us then, O God our Saviour,\r\n            * and let thine anger cease from us.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wilt thou be displeased at us for ever?\r\n            * and wilt thou stretch out thy wrath from one\r\n            generation to another?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wilt thou not turn again, and quicken us,\r\n            * that thy people may rejoice in thee?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Show us thy mercy, O LORD,\r\n            * and grant us thy salvation.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will hearken what the LORD God will say;\r\n            * for he shall speak peace unto his people, and to his\r\n            saints, that they turn not again unto foolishness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For his salvation is nigh them that fear him;\r\n            * that glory may dwell in our land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Mercy and truth are met together:\r\n            * righteousness and peace have kissed each other.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Truth shall flourish out of the earth,\r\n            * and righteousness hath looked down from heaven.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, the LORD shall show loving-kindness;\r\n            * and our land shall give her increase.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Righteousness shall go before him,\r\n            * and shall direct his going in the way.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n', 'Evening', '0000-00-00 00:00:00'),
(47, '17', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 17, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 89\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Eighty-Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Misericordias Domini.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        MY song shall be alway of the loving-kindness\r\n        of the LORD;\r\n        * with my mouth will I ever be showing thy truth from\r\n        one generation to another.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For I have said, Mercy shall be set up for ever;\r\n        * thy truth shalt thou stablish in the heavens.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I have made a covenant with my chosen;\r\n        * I have sworn unto David my servant:<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thy seed will I stablish for ever,\r\n        * and set up thy throne from one generation to another.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O LORD, the very heavens shall praise thy\r\n        wondrous works;\r\n        * and thy truth in the congregation of the saints.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For who is he among the clouds,\r\n        * that shall be compared unto the LORD?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And what is he among the gods,\r\n        * that shall be like unto the LORD?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        God is very greatly to be feared in the council\r\n        of the saints,\r\n        * and to be had in reverence of all them that are round\r\n        about him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O Lord God of hosts, who is like unto thee?\r\n        * thy truth, most mighty LORD, is on every side.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou rulest the raging of the sea;\r\n        * thou stillest the waves thereof when they arise.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast subdued Egypt, and destroyed it;\r\n        * thou hast scattered thine enemies abroad with thy\r\n        mighty arm.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The heavens are thine, the earth also is thine;\r\n        * thou hast laid the foundation of the round world,\r\n        and all that therein is.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast made the north and the south;\r\n        * Tabor and Hermon shall rejoice in thy Name.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast a mighty arm;\r\n        * strong is thy hand, and high is thy right hand.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Righteousness and equity are the habitation of thy seat;\r\n        * mercy and truth shall go before thy face.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Blessed is the people, O LORD, that can rejoice in thee;\r\n        * they shall walk in the light of thy countenance.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Their delight shall be daily in thy Name;\r\n        * and in thy righteousness shall they make their boast.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For thou art the glory of their strength,\r\n        * and in thy loving-kindness thou shalt lift up our horns.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For the LORD is our defence;\r\n        * the Holy One of Israel is our King.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 20 </i>Thou spakest sometime in visions unto thy saints,\r\n        and saidst,\r\n        * I have laid help upon one that is mighty, I have exalted\r\n        one chosen out of the people.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I have found David my servant;\r\n        * with my holy oil have I anointed him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My hand shall hold him fast,\r\n        * and my arm shall strengthen him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The enemy shall not be able to do him violence;\r\n        * the son of wickedness shall not hurt him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will smite down his foes before his face,\r\n        * and plague them that hate him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My truth also and my mercy shall be with him;\r\n        * and in my Name shall his horn be exalted.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will set his dominion also in the sea,\r\n        * and his right hand in the floods.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He shall call me, Thou art my Father,\r\n        * my God, and my strong salvation.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And I will make him my firstborn,\r\n        * higher than the kings of the earth.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My mercy will I keep for him for evermore,\r\n        * and my covenant shall stand fast with him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        His seed also will I make to endure for ever,\r\n        * and his throne as the days of heaven.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 31 </i>But if his children forsake my law,\r\n        * and walk not in my judgments;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        If they break my statutes, and keep not my\r\n        commandments;\r\n        * I will visit their offences with the rod, and their\r\n        sin with scourges.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Nevertheless, my loving-kindness will I not utterly\r\n        take from him,\r\n        * nor suffer my truth to fail.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        My covenant will I not break, nor alter the thing that\r\n        is gone out of my lips:\r\n        * I have sworn once by my holiness, that I will\r\n        not fail David.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        His seed shall endure for ever,\r\n        * and his throne is like as the sun before me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He shall stand fast for evermore as the moon,\r\n        * and as the faithful witness in heaven.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But thou hast abhorred and forsaken thine anointed,\r\n        * and art displeased at him.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast broken the covenant of thy servant,\r\n        * and cast his crown to the ground.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast overthrown all his hedges,\r\n        * and broken down his strongholds.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        All they that go by spoil him,\r\n        * and he is become a reproach to his neighbours.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast set up the right hand of his enemies,\r\n        * and made all his adversaries to rejoice.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast taken away the edge of his sword,\r\n        * and givest him not victory in the battle.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Thou hast put out his glory,\r\n        * and cast his throne down to the ground.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The days of his youth hast thou shortened,\r\n        * and covered him with dishonour.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        LORD, how long wilt thou hide thyself? for ever?\r\n        * and shall thy wrath burn like fire?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O remember how short my time is;\r\n        * wherefore hast thou made all men for nought?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        What man is he that liveth, and shall not see death?\r\n        * and shall he deliver his soul from the power of the grave?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Lord, where are thy old loving-kindnesses,\r\n        * which thou swarest unto David in thy truth?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Remember, Lord, the rebuke that thy servants have,\r\n        * and how I do bear in my bosom the rebukes of\r\n        many people;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Wherewith thine enemies have blasphemed thee,\r\n        * and slandered the footsteps of thine anointed.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Praised be the LORD for evermore.\r\n        * Amen, and Amen.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(48, '18', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 18, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 93\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninety-Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dominus regnavit.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE LORD is King, and hath put on glorious apparel;\r\n            * the LORD hath put on his apparel, and girded himself\r\n            with strength.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath made the round world so sure,\r\n            * that it cannot be moved.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ever since the world began, hath thy seat been prepared:\r\n            * thou art from everlasting.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The floods are risen, O LORD, the floods have lift up\r\n            their voice;\r\n            * the floods lift up their waves.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The waves of the sea are mighty, and rage horribly;\r\n            * but yet the LORD, who dwelleth on high, is mightier.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy testimonies, O LORD, are very sure:\r\n            * holiness becometh thine house for ever.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 94\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninety- Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus ultionum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        O LORD God, to whom vengeance belongeth,\r\n        *  thou God, to whom vengeance belongeth, show thyself.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Arise, thou Judge of the world,\r\n        * and reward the proud after their deserving.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        LORD, how long shall the ungodly,\r\n        * how long shall the ungodly triumph?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        How long shall all wicked doers speak so disdainfully,\r\n        *  and make such proud boasting?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They smite down thy people, O LORD,\r\n        *  and trouble thine heritage.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They murder the widow and the stranger,\r\n        *  and put the fatherless to death.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        And yet they say, Tush, the LORD shall not see,\r\n        * neither shall the God of Jacob regard it.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Take heed, ye unwise among the people:\r\n        * O ye fools, when will ye understand?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        He that planted the ear, shall he not hear?\r\n        * or he that made the eye, shall he not see?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Or he that instructeth the heathen,\r\n        * it is he that teacheth man knowledge; shall not he punish?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        The LORD knoweth the thoughts of man,\r\n        *  that they are but vain.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Blessed is the man whom thou chastenest, O LORD,\r\n        *  and teachest him in thy law;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        That thou mayest give him patience in time of adversity,\r\n        *  until the pit be digged up for the ungodly.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For the LORD will not fail his people;\r\n        *  neither will he forsake his inheritance;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Until righteousness turn again unto judgment:\r\n        *  all such as are true in heart shall follow it.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Who will rise up with me against the wicked?\r\n        *  or who will take my part against the evil doers?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        If the LORD had not helped me,\r\n        *  it had not failed, but my soul had been put to silence.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But when I said, My foot hath slipt;\r\n        *  thy mercy, O LORD, held me up.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        In the multitude of the sorrows that I had in my heart,\r\n        *  thy comforts have refreshed my soul.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Wilt thou have any thing to do with the throne of wickedness,\r\n        *  which imagineth mischief as a law?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        They gather them together against the soul of the righteous,\r\n        *  and condemn the innocent blood.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But the LORD is my refuge,\r\n        *  and my God is the strength of my confidence.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 23 </i>He shall recompense them their wickedness, and destroy them in their own malice;\r\n        *  yea, the LORD our God shall destroy them.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(49, '19', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 19, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 98\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninety-Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Cantate Domino.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O SING unto the LORD a new song;\r\n            * for he hath done marvellous things.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            With his own right hand, and with his holy arm,\r\n            * hath he gotten himself the victory.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD declared his salvation;\r\n            * his righteousness hath he openly showed in the\r\n            sight of the heathen.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath remembered his mercy and truth toward\r\n            the house of Israel;\r\n            * and all the ends of the world have seen the salvation\r\n            of our God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Show yourselves joyful unto the LORD, all ye lands;\r\n            * sing, rejoice, and give thanks.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise the LORD upon the harp;\r\n            * sing to the harp with a psalm of thanksgiving.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            With trumpets also and shawms,\r\n            * O show yourselves joyful before the LORD, the King.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the sea make a noise, and all that therein is;\r\n            * the round world, and they that dwell therein.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the floods clap their hands, and let the hills be\r\n            joyful together before the LORD;\r\n            * for he is come to judge the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            With righteousness shall he judge the world,\r\n            * and the peoples with equity.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 99\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The Ninety-Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Dominus regnavit.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            THE LORD is King, be the people never so impatient;\r\n            * he sitteth between the Cherubim, be the earth never\r\n            so unquiet.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD is great in Sion,\r\n            * and high above all people.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They shall give thanks unto thy Name,\r\n            * which is great, wonderful, and holy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The King\'s power loveth judgment; thou hast prepared equity,\r\n            * thou hast executed judgment and righteousness in Jacob.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O magnify the LORD our God, and fall down\r\n            before his footstool;\r\n            * for he is holy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Moses and Aaron among his priests, and Samuel among\r\n            such as call upon his Name:\r\n            * these called upon the LORD, and he heard them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He spake unto them out of the cloudy pillar;\r\n            * for they kept his testimonies, and the law that he gave them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou heardest them, O LORD our God;\r\n            * thou forgavest them, O God, though thou didst\r\n            punish their wicked doings.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O magnify the LORD our God, and worship him upon\r\n            his holy hill;\r\n            * for the LORD our God is holy.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 100\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundredth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Jubilate Deo.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O BE joyful in the LORD, all ye lands:\r\n            * serve the LORD with gladness, and come before\r\n            his presence with a song.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Be ye sure that the LORD he is God; it is he\r\n            that hath made us, and not we ourselves;\r\n            * we are his people, and the sheep of his pasture.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O go your way into his gates with thanksgiving,\r\n            and into his courts with praise;\r\n            * be thankful unto him, and speak good of his Name.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the LORD is gracious, his mercy is everlasting;\r\n            * and his truth endureth from generation to generation.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 101\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Misericordiam et judicium.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            MY song shall be of mercy and judgment;\r\n            * unto thee, O LORD, will I sing.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let me have understanding\r\n            * in the way of godliness!<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When wilt thou come unto me?\r\n            * I will walk in my house with a perfect heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will take no wicked thing in hand; I hate the sins of unfaithfulness;\r\n            * there shall no such cleave unto me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            A froward heart shall depart from me;\r\n            * I will not know a wicked person.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whoso privily slandereth his neighbour,\r\n            * him will I destroy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whoso hath also a haughty look and a proud heart,\r\n            * I will not suffer him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Mine eyes look upon such as are faithful in the land,\r\n            * that they may dwell with me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whoso leadeth a godly life,\r\n            * he shall be my servant.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There shall no deceitful person dwell in my house;\r\n            * he that telleth lies shall not tarry in my sight.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I shall soon destroy all the ungodly that are in the land;\r\n            * that I may root out all wicked doers from the city of the LORD.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(50, '20', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 20, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 104\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Fourth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Benedic, anima mea.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            PRAISE the LORD, O my soul:\r\n            * O LORD my God, thou art become exceeding glorious; thou art clothed with majesty and honour.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou deckest thyself with light as it were with a garment,\r\n            * and spreadest out the heavens like a curtain.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who layeth the beams of his chambers in the waters,\r\n            * and maketh the clouds his chariot, and walketh upon the wings of the wind.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He maketh his angels winds,\r\n            * and his ministers a flaming fire.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He laid the foundations of the earth,\r\n            * that it never should move at any time.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou coveredst it with the deep like as with a garment;\r\n            * the waters stand above the hills.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            At thy rebuke they flee;\r\n            * at the voice of thy thunder they haste away.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They go up as high as the hills, and down to the valleys beneath;\r\n            * even unto the place which thou hast appointed for them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou hast set them their bounds, which they shall not pass,\r\n            * neither turn again to cover the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He sendeth the springs into the rivers,\r\n            * which run among the hills.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All beasts of the field drink thereof,\r\n            * and the wild asses quench their thirst.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Beside them shall the fowls of the air have their habitation,\r\n            * and sing among the branches.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He watereth the hills from above;\r\n            * the earth is filled with the fruit of thy works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He bringeth forth grass for the cattle,\r\n            * and green herb for the service of men;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That he may bring food out of the earth, and wine that maketh glad the heart of man;\r\n            * and oil to make him a cheerful countenance, and bread to strengthen man\'s heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The trees of the LORD also are full of sap;\r\n            * even the cedars of Lebanon which he hath planted;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wherein the birds make their nests;\r\n            * and the firtrees are a dwelling for the stork.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The high hills are a refuge for the wild goats;\r\n            * and so are the stony rocks for the conies.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He appointed the moon for certain seasons,\r\n            * and the sun knoweth his going down.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thou makest darkness that it may be night;\r\n            * wherein all the beasts of the forest do move.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The lions, roaring after their prey,\r\n            * do seek their meat from God.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The sun ariseth, and they get them away together,\r\n            * and lay them down in their dens.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Man goeth forth to his work, and to his labour,\r\n            * until the evening.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O LORD, how manifold are thy works!\r\n            * in wisdom hast thou made them all; the earth is full of thy riches.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So is the great and wide sea also;\r\n            * wherein are things creeping innumerable, both small and great beasts.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            There go the ships, and there is that leviathan,\r\n            * whom thou hast made to take his pastime therein.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            These wait all upon thee,\r\n            * that thou mayest give them meat in due season.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When thou givest it them, they gather it;\r\n            * and when thou openest thy hand, they are filled with good.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When thou hidest thy face, they are troubled:\r\n            * when thou takest away their breath, they die, and are turned again to their dust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When thou lettest thy breath go forth, they shall be made;\r\n            * and thou shalt renew the face of the earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The glorious majesty of the LORD shall endure for ever;\r\n            * the LORD shall rejoice in his works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The earth shall tremble at the look of him;\r\n            * if he do but touch the hills, they shall smoke.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will sing unto the LORD as long as I live;\r\n            * I will praise my God while I have my being.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And so shall my words please him:\r\n            * my joy shall be in the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for sinners, they shall be consumed out of the earth,\r\n            * and the ungodly shall come to an end.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise thou the LORD, O my soul.\r\n            * Praise the LORD.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(51, '21', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 21, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 106\r\n    </h3>\r\n</div>\r\n\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Confitemini Domino.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O GIVE thanks unto the LORD; for he is gracious,\r\n            * and his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who can express the noble acts of the LORD,\r\n            * or show forth all his praise?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed are they that alway keep judgment,\r\n            * and do righteousness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Remember me, O LORD, according to the favour that thou bearest unto thy people;\r\n            * O visit me with thy salvation;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            That I may see the felicity of thy chosen,\r\n            * and rejoice in the gladness of thy people, and give thanks with thine inheritance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            We have sinned with our fathers;\r\n            * we have done amiss, and dealt wickedly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Our fathers regarded not thy wonders in Egypt, neither kept they thy great goodness in remembrance;\r\n            * but were disobedient at the sea, even at the Red Sea.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Nevertheless, he helped them for his Name\'s sake,\r\n            * that he might make his power to be known.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He rebuked the Red Sea also, and it was dried up;\r\n            * so he led them through the deep, as through a wilderness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And he saved them from the adversary\'s hand,\r\n            * and delivered them from the hand of the enemy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for those that troubled them, the waters overwhelmed them;\r\n            * there was not one of them left.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then believed they his words,\r\n            * and sang praise unto him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But within a while they forgat his works,\r\n            * and would not abide his counsel.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But lust came upon them in the wilderness,\r\n            * and they tempted God in the desert.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And he gave them their desire,\r\n            * and sent leanness withal into their soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They angered Moses also in the tents,\r\n            * and Aaron the saint of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So the earth opened, and swallowed up Dathan,\r\n            * and covered the congregation of Abiram.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And the fire was kindled in their company;\r\n            * the flame burnt up the ungodly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They made a calf in Horeb,\r\n            * and worshipped the molten image.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thus they turned their glory\r\n            * into the similitude of a calf that eateth hay.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And they forgat God their Saviour,\r\n            * who had done so great things in Egypt;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wondrous works in the land of Ham;\r\n            * and fearful things by the Red Sea.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So he said he would have destroyed them, had not Moses his chosen stood before him in the gap,\r\n            * to turn away his wrathful indignation, lest he should destroy them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, they thought scorn of that pleasant land,\r\n            * and gave no credence unto his word;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But murmured in their tents,\r\n            * and hearkened not unto the voice of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then lift he up his hand against them,\r\n            * to overthrow them in the wilderness;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            To cast out their seed among the nations,\r\n            * and to scatter them in the lands.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They joined themselves unto Baal-peor,\r\n            * and ate the offerings of the dead.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thus they provoked him to anger with their own inventions;\r\n            * and the plague was great among them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then stood up Phinehas, and interposed;\r\n            * and so the plague ceased.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And that was counted unto him for righteousness,\r\n            * among all posterities for evermore.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They angered him also at the waters of strife,\r\n            * so that he punished Moses for their sakes;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Because they provoked his spirit,\r\n            * so that he spake unadvisedly with his lips.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Neither destroyed they the heathen,\r\n            * as the LORD commanded them;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But were mingled among the heathen,\r\n            * and learned their works.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Insomuch that they worshipped their idols, which became a snare unto them;\r\n            * yea, they offered their sons and their daughters unto devils;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And shed innocent blood, even the blood of their sons and of their daughters,\r\n            * whom they offered unto the idols of Canaan; and the land was defiled with blood.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thus were they stained with their own works,\r\n            * and went a whoring with their own inventions.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore was the wrath of the LORD kindled against his people,\r\n            * insomuch that he abhorred his own inheritance.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And he gave them over into the hand of the heathen;\r\n            * and they that hated them were lords over them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their enemies oppressed them,\r\n            * and had them in subjection.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Many a time did he deliver them;\r\n            * but they rebelled against him with their own inventions, and were brought down in their\r\n            wickedness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Nevertheless, when he saw their adversity,\r\n            * he heard their complaint.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He thought upon his covenant, and pitied them, according unto the multitude of his mercies;\r\n            * yea, he made all those that led them away captive to pity them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Deliver us, O LORD our God, and gather us from among the heathen;\r\n            * that we may give thanks unto thy holy Name, and make our boast of thy praise.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Blessed be the LORD God of Israel, from everlasting, and world without end;\r\n            * And let all the people say, Amen.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(52, '22', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 22, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 108\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Paratum cor meum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        O GOD, my heart is ready, my heart is ready;\r\n        *  I will sing, and give praise with the best member that I have.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Awake, thou lute and harp;\r\n        *  I myself will awake right early.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        I will give thanks unto thee, O LORD, among the peoples;\r\n        *  I will sing praises unto thee among the nations.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For thy mercy is greater than the heavens,\r\n        *  and thy truth reacheth unto the clouds.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Set up thyself, O God, above the heavens,\r\n        *  and thy glory above all the earth;<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 6 </i>That thy beloved may be delivered:\r\n        *  let thy right hand save them, and hear thou me.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i> vs 7 </i>God hath spoken in his holiness;\r\n        *  I will rejoice therefore, and divide Shechem, and mete out the valley of Succoth.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Gilead is mine, and Manasseh is mine;\r\n        *  Ephraim also is the strength of my head; Judah is my lawgiver;<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Moab is my wash-pot; over Edom will I cast out my shoe;\r\n        *  upon Philistia will I triumph.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Who will lead me into the strong city?\r\n        *  and who will bring me into Edom?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Hast not thou forsaken us, O God?\r\n        *  and wilt not thou, O God, go forth with our hosts?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O help us against the enemy:\r\n        *  for vain is the help of man.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Through God we shall do great acts;\r\n        *  and it is he that shall tread down our enemies.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 109\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Deus, laudem.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HOLD not thy tongue, O God of my praise;\r\n            *  for the mouth of the ungodly, yea, the mouth of the deceitful is opened upon me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And they have spoken against me with false tongues;\r\n            *  they compassed me about also with words of hatred, and fought against me without a cause.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the love that I had unto them, lo, they take now my contrary part;\r\n            *  but I give myself unto prayer.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thus have they rewarded me evil for good,\r\n            *  and hatred for my good will.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Set thou an ungodly man to be ruler over him,\r\n            *  and let an adversary stand at his right hand.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When sentence is given upon him, let him be condemned;\r\n            *  and let his prayer be turned into sin.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let his days be few;\r\n            *  and let another take his office.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let his children be fatherless,\r\n            *  and his wife a widow.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let his children be vagabonds, and beg their bread;\r\n            *  let them seek it also out of desolate places.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the extortioner consume all that he hath;\r\n            *  and let the stranger spoil his labour.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let there be no man to pity him,\r\n            *  nor to have compassion upon his fatherless children.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let his posterity be destroyed;\r\n            *  and in the next generation let his name be clean put out.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the wickedness of his fathers be had in remembrance in the sight of the LORD;\r\n            *  and let not the sin of his mother be done away.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them alway be before the LORD,\r\n            *  that he may root out the memorial of them from off the earth;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And that, because his mind was not to do good;\r\n            *  but persecuted the poor helpless man, that he might slay him that was vexed at the heart.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            His delight was in cursing, and it shall happen unto him;\r\n            *  he loved not blessing, therefore shall it be far from him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He clothed himself with cursing like as with a raiment,\r\n            *  and it shall come into his bowels like water, and like oil into his bones.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let it be unto him as the cloak that he hath upon him,\r\n            *  and as the girdle that he is alway girded withal.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let it thus happen from the LORD unto mine enemies,\r\n            *  and to those that speak evil against my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But deal thou with me, O LORD God, according unto thy Name;\r\n            *  for sweet is thy mercy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O deliver me, for I am helpless and poor,\r\n            *  and my heart is wounded within me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I go hence like the shadow that departeth,\r\n            *  and am driven away as the grasshopper.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My knees are weak through fasting;\r\n            *  my flesh is dried up for want of fatness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I am become also a reproach unto them:\r\n            *  they that look upon me shake their heads.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Help me, O LORD my God;\r\n            *  O save me according to thy mercy;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And they shall know how that this is thy hand,\r\n            *  and that thou, LORD, hast done it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Though they curse, yet bless thou;\r\n            *  and let them be confounded that rise up against me; but let thy servant rejoice.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let mine adversaries be clothed with shame;\r\n            *  and let them cover themselves with their own confusion, as with a cloak.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for me, I will give great thanks unto the LORD with my mouth,\r\n            *  and praise him among the multitude;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he shall stand at the right hand of the poor,\r\n            *  to save his soul from unrighteous judges.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(53, '23', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 23, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 114\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Fourteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>In exitu Israel.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            WHEN Israel came out of Egypt,\r\n            * and the house of Jacob from among the strange people,<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Judah was his sanctuary,\r\n            * and Israel his dominion.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The sea saw that, and fled;\r\n            * Jordan was driven back.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The mountains skipped like rams,\r\n            * and the little hills like young sheep.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            What aileth thee, O thou sea, that thou fleddest?\r\n            * and thou Jordan, that thou wast driven back?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ye mountains, that ye skipped like rams?\r\n            * and ye little hills, like young sheep?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Tremble, thou earth, at the presence of the Lord:\r\n            * at the presence of the God of Jacob;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who turned the hard rock into a standing water,\r\n            * and the flint-stone into a springing well.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 115\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Fifteenth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Non nobis, Domine.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            NOT unto us, O LORD, not unto us, but unto thy Name give the praise;\r\n            * for thy loving mercy, and for thy truth\'s sake.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Wherefore shall the heathen say,\r\n            * Where is now their God?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            As for our God, he is in heaven:\r\n            * he hath done whatsoever pleased him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Their idols are silver and gold,\r\n            * even the work of men\'s hands.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have mouths, and speak not;\r\n            * eyes have they, and see not.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have ears, and hear not;\r\n            * noses have they, and smell not.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They have hands, and handle not; feet have they, and walk not;\r\n            * neither speak they through their throat.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They that make them are like unto them;\r\n            * and so are all such as put their trust in them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But thou, house of Israel, trust thou in the LORD;\r\n            * he is their helper and defender.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ye house of Aaron, put your trust in the LORD;\r\n            * he is their helper and defender.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ye that fear the LORD, put your trust in the LORD;\r\n            * he is their helper and defender.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD hath been mindful of us, and he shall bless us;\r\n            * even he shall bless the house of Israel, he shall bless the house of Aaron.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall bless them that fear the LORD,\r\n            * both small and great.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD shall increase you more and more,\r\n            * you and your children.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Ye are the blessed of the LORD,\r\n            * who made heaven and earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All the whole heavens are the LORD\'S;\r\n            * the earth hath he given to the children of men.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The dead praise not thee, O LORD,\r\n            * neither all they that go down into silence.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But we will praise the LORD,\r\n            * from this time forth for evermore. Praise the LORD.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n', 'Evening', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(54, '24', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 24, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 119 (Section I - IV)\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Nineteenth Psalm</h3></div>\r\n\r\n<div class=\"text-center\">\r\n    <table width=\"90%\">\r\n\r\n        <tbody><tr>\r\n            <td> <h4><a href=\"#section1\">\r\n                I. Beati immaculati</a><br/>\r\n                <i>(vs 1-8)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section9\">\r\n                IX. Bonitatem fecisti</a><br/>\r\n                <i>(vs 65-72)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section17\">\r\n                XVII. Mirabilia</a><br/>\r\n                <i>(vs 129-136)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section2\">\r\n                II. In quo corrigit?</a><br/>\r\n                <i>(vs 9-16)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section10\">\r\n                X. Manus tuae fecerunt me</a><br/>\r\n                <i>(vs 73-80)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section18\">\r\n                XVIII. Justus es, Domine</a><br/>\r\n                <i>(vs 137-144)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section3\">\r\n                III. Retribue servo tuo</a><br/>\r\n                <i>(vs 17-24)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section11\">\r\n                XI. Defecit anima mea</a><br/>\r\n                <i>(vs 81-88)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section19\">\r\n                XIX. Clamavi in toto corde meo</a><br/>\r\n                <i>(vs 145-152)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section4\">\r\n                IV. Adhaesit pavimento</a><br/>\r\n                <i>(vs 25-32)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section12\">\r\n                XII. In aeternum, Domine</a><br/>\r\n                <i>(vs 89-96)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section20\">\r\n                XX. Vide humilitatem</a><br/>\r\n                <i>(vs 153-160)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section5\">\r\n                V. Legem pone</a><br/>\r\n                <i>(vs 33-40)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section13\">\r\n                XIII. Quomodo dilexi!</a><br/>\r\n                <i>(vs 97-104)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section21\">\r\n                XXI. Principes persecuti sunt</a><br/>\r\n                <i>(vs 161-168)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section6\">\r\n                VI. Et veniat super me</a><br/>\r\n                <i>(vs 41-48)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section14\">\r\n                XIV. Lucerna pedibus meus</a><br/>\r\n                <i>(vs 105-112)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section22\">\r\n                XXII. Appropinquet deprecatio</a><br/>\r\n                <i>(vs 169-176)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section7\">\r\n                VII. Memor esto verbi tui</a><br/>\r\n                <i>(vs 49-56)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section15\">\r\n                XV. Iniquos odio habui</a><br/>\r\n                <i>(vs 113-120)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section8\">\r\n                VIII. Portio mea, Domine</a><br/>\r\n                <i>(vs 57-64)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section16\">\r\n                XVI. Feci judicium</a><br/>\r\n                <i>(vs 121-128)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n        </tbody></table>\r\n</div>\r\n\r\n\r\n<a name=\"section1\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    I. Beati immaculati <i>(vs 1-8)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    BLESSED are those that are undefiled in the way, * and walk in the law of the LORD.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed are they that keep his testimonies, * and seek him with their whole heart;<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Even they who do no wickedness, * and walk in his ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast charged * that we shall diligently keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O that my ways were made so direct, * that I might keep thy statutes!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I not be confounded, * while I have respect unto all thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will thank thee with an unfeigned heart, * when I shall have learned the judgments of thy\r\n    righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will keep thy statutes; * O forsake me not utterly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section2\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    II. In quo corrigit? <i>(vs 9-16)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    WHEREWITHAL shall a young man cleanse his way? * even by ruling himself after thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my whole heart have I sought thee; * O let me not go wrong out of thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word have I hid within my heart, * that I should not sin against thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed art Thou, O LORD; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my lips have I been telling * of all the judgments of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have had as great delight in the way of thy testimonies, * as in all manner of riches.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will talk of thy commandments, * and have respect unto thy ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My delight shall be in thy statutes, * and I will not forget thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section3\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    III. Retribue servo tuo <i>(vs 17-24)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O DO well unto thy servant; * that I may live, and keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Open thou mine eyes; * that I may see the wondrous things of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a stranger upon earth; * O hide not thy commandments from me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul breaketh out for the very fervent desire * that it hath alway unto thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast rebuked the proud; * and cursed are they that do err from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn from me shame and rebuke; * for I have kept thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Princes also did sit and speak against me; * but thy servant is occupied in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For thy testimonies are my delight, * and my counsellors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section4\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IV. Adhaesit pavimento <i>(vs 25-32)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul cleaveth to the dust; * O quicken thou me, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have acknowledged my ways, and thou heardest me: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to understand the way of thy commandments; * and so shall I talk of thy wondrous works.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul melteth away for very heaviness; * comfort thou me according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take from me the way of lying, * and cause thou me to make much of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have chosen the way of truth, * and thy judgments have I laid before me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have stuck unto thy testimonies; * O LORD, confound me not.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will run the way of thy commandments, * when thou hast set my heart at liberty.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section5\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    V. Legem pone <i>(vs 33-40)</i><br/>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    <small> &nbsp; <br/></small>\r\n    TEACH me, O LORD, the way of thy statutes, * and I shall keep it unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Give me understanding, and I shall keep thy law; * yea, I shall keep it with my whole heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to go in the path of thy commandments; * for therein is my desire.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Incline my heart unto thy testimonies, * and not to covetousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn away mine eyes, lest they behold vanity; * and quicken thou me in thy way.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish thy word in thy servant, * that I may fear thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take away the rebuke that I am afraid of; * for thy judgments are good.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Behold, my delight is in thy commandments; * O quicken me in thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section6\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VI. Et veniat super me <i>(vs 41-48)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET thy loving mercy come also unto me, O LORD, * even thy salvation, according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I make answer unto my blasphemers; * for my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O take not the word of thy truth utterly out of my mouth; * for my hope is in thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I alway keep thy law; * yea, for ever and ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And I will walk at liberty; * for I seek thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will speak of thy testimonies also, even before kings, * and will not be ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And my delight shall be in thy commandments, * which I have loved.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My hands also will I lift up unto thy commandments, which I have loved; * and my study shall be in thy\r\n    statutes.<br/>\r\n</b></blockquote>\r\n<small> &nbsp; <br/></small>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section7\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VII. Memor esto verbi tui <i>(vs 49-56)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O THINK upon thy servant, as concerning thy word, * wherein thou hast caused me to put my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The same is my comfort in my trouble; * for thy word hath quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have had me exceedingly in derision; * yet have I not shrinked from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I remembered thine everlasting judgments, O LORD, * and received comfort.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am horribly afraid, * for the ungodly that forsake thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy statutes have been my songs, * in the house of my pilgrimage.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have thought upon thy Name, O LORD, in the night season, * and have kept thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    This I had, * because I kept thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section8\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VIII. Portio mea, Domine <i>(vs 57-64)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THOU art my portion, O LORD; * I have promised to keep thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made my humble petition in thy presence with my whole heart; * O be merciful unto me, according to\r\n    thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I called mine own ways to remembrance, * and turned my feet unto thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made haste, and prolonged not the time, * to keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The snares of the ungodly have compassed me about; * but I have not forgotten thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    At midnight I will rise to give thanks unto thee, * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a companion of all them that fear thee, * and keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The earth, O LORD, is full of thy mercy: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section9\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IX. Bonitatem fecisti <i>(vs 65-72)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thou hast dealt graciously with thy servant, * according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O teach me true understanding and knowledge; * for I have believed thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Before I was troubled, I went wrong; * but now have I kept thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art good and gracious; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have imagined a lie against me; * but I will keep thy commandments with my whole\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Their heart is as fat as brawn; * but my delight hath been in thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is good for me that I have been in trouble; * that I may learn thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The law of thy mouth is dearer unto me * than thousands of gold and silver.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section10\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    X. Manus tuae fecerunt me <i>(vs 73-80)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY hands have made me and fashioned me: * O give me understanding, that I may learn  thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They that fear thee will be glad when they see me; * because I have put my trust in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I know, O LORD, that thy judgments are right, * and that thou of very faithfulness hast caused me to be\r\n    troubled.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy merciful kindness be my comfort, * according to thy word unto thy servant.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy loving mercies come unto me, that I may live; * for thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the proud be confounded, for they go wickedly about to destroy me; * but I will be occupied in thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let such as fear thee, and have known thy testimonies, * be turned unto me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my heart be sound in thy statutes, * that I be not ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section11\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XI. Defecit anima mea <i>(vs 81-88)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul hath longed for thy salvation, * and I have a good hope because of thy word.<br/>\n\n    <small> &nbsp; <br/></small>\r\n    Mine eyes-long sore for thy word; * saying, O when wilt thou comfort me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I am become like a bottle in the smoke; * yet do I not forget thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    How many are the days of thy servant? * when wilt thou be avenged of them that persecute me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have digged pits for me, * which are not after thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    All thy commandments are true: * they persecute me falsely; O be thou my help.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They had almost made an end of me upon earth; * but I forsook not thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O quicken me after thy loving-kindness; * and so shall I keep the testimonies of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section12\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XII. In aeternum, Domine <i>(vs 89-96)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thy word * endureth for ever in heaven.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy truth also remaineth from one generation to another; * thou hast laid the foundation of the earth, and\r\n    it abideth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They continue this day according to thine ordinance; * for all things serve thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    If my delight had not been in thy law, * I should have perished in my trouble.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will never forget thy commandments; * for with them thou hast quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thine: O save me, * for I have sought thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly laid wait for me, to destroy me; * but I will consider thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I see that all things come to an end; * but thy commandment is exceeding broad.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section13\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIII. Quomodo dilexi! <i>(vs 97-104)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LORD, what love have I unto thy law! * all the day long is my study in it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou, through thy commandments, hast made me wiser than mine enemies; * for they are ever with\r\n    me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have more understanding than my teachers; * for thy testimonies are my study.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am wiser than the aged; * because I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have refrained my feet from every evil way, * that I may keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have not shrunk from thy judgments; * for thou teachest me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O how sweet are thy words unto my throat; * yea, sweeter than honey unto my mouth!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Through thy commandments I get understanding: * therefore I hate all evil ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section14\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIV. Lucerna pedibus meus <i>(vs 105-112)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY word is a lantern unto my feet, * and a light unto my paths.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have sworn, and am stedfastly purposed, * to keep thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am troubled above measure: * quicken me, O LORD, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the free-will offerings of my mouth please thee, O LORD; * and teach me thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul is alway in my hand; * yet do I not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly have laid a snare for me; * but yet I swerved not from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy testimonies have I claimed as mine heritage for ever; * and why? they are the very joy of my\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have applied my heart to fulfil thy statutes alway, * even unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section15\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XV. Iniquos odio habui <i>(vs 113-120)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I HATE them that imagine evil things; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art my defence and shield; * and my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Away from me, ye wicked; * I will keep the commandments of my God.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish me according to thy word, that I may live; * and let me not be disappointed of my hope.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hold thou me up, and I shall be safe; * yea, my delight shall be ever in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast trodden down all them that depart  from thy statutes; * for they imagine but deceit.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou puttest away all the ungodly of the earth like dross; * therefore I love thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My flesh trembleth for fear of thee; * and I am afraid of thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section16\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVI. Feci judicium <i>(vs 121-128)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I DEAL with the thing that is lawful and right; * O give me not over unto mine oppressors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make thou thy servant to delight in that which is good, * that the proud do me no wrong.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes are wasted away with looking for thy health, * and for the word of thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deal with thy servant according unto thy loving mercy, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thy servant; O grant me understanding, * that I may know thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is time for thee, LORD, to lay to thine hand; * for they have destroyed thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I love thy commandments * above gold and precious stones.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Therefore hold I straight all thy commandments; * and all false ways I utterly abhor.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section17\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVII. Mirabilia <i>(vs 129-136)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY testimonies are wonderful; * therefore doth my soul keep them.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    When thy word goeth forth, * it giveth light and understanding unto the simple.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I opened my mouth, and drew in my breath; * for my delight was in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O look thou upon me, and be merciful unto me, * as thou usest to do unto those that love thy Name.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Order my steps in thy word; * and so shall no wickedness have dominion over me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deliver me from the wrongful dealings of men; * and so shall I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Show the light of thy countenance upon thy servant, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes gush out with water, * because men keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section18\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVIII. Justus es, Domine <i>(vs 137-144)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    RIGHTEOUS art thou, O LORD; * and true are thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The testimonies that thou hast commanded * are exceeding righteous and true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My zeal hath even consumed me; * because mine enemies have forgotten thy words.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is tried to the uttermost, * and thy servant loveth it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am small and of no reputation; * yet do I not forget thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy righteousness is an everlasting righteousness, * and thy law is the truth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Trouble and heaviness have taken hold upon me; * yet is my delight in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The righteousness of thy testimonies is everlasting: * O grant me understanding, and I shall live.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section19\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIX. Clamavi in toto corde meo <i>(vs 145-152)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I CALL with my whole heart; * hear me, O LORD; I will keep thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, even unto thee do I call; * help me, and I shall keep thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Early in the morning do I cry unto thee; * for in thy word is my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes prevent the night watches; * that I might be occupied in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hear my voice, O LORD, according unto thy loving-kindness; * quicken me, according to thy\r\n    judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They draw nigh that of malice persecute me, * and are far from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Be thou nigh at hand, O LORD; * for all thy commandments are true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As concerning thy testimonies, I have known long since, * that thou hast grounded them for ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section20\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XX. Vide humilitatem <i>(vs 153-160)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O CONSIDER mine adversity, and deliver me, * for I do not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Avenge thou my cause, and deliver me; * quicken me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Health is far from the ungodly; * for they regard not thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is thy mercy, O LORD; * quicken me, as thou art wont.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Many there are that trouble me, and persecute me; * yet do I not swerve from thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It grieveth me when I see the transgressors; * because they keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Consider, O LORD, how I love thy commandments; * O quicken me, according to thy loving-\r\n    kindness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is true from everlasting; * all the judgments of thy righteousness endure for evermore.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section21\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXI. Principes persecuti sunt <i>(vs 161-168)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    PRINCES have persecuted me without a cause; * but my heart standeth in awe of thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am as glad of thy word, * as one that findeth great spoils.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As for lies, I hate and abhor them; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Seven times a day do I praise thee; * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is the peace that they have who love thy law; * and they have none occasion of stumbling.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    LORD, I have looked for thy saving health, * and done after thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul hath kept thy testimonies, * and loved them exceedingly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have kept thy commandments and testimonies; * for all my ways are before thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section22\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXII. Appropinquet deprecatio <i>(vs 169-176)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET my complaint come before thee, O LORD; * give me understanding according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let my supplication come before thee; * deliver me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My lips shall speak of thy praise, * when thou hast taught me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, my tongue shall sing of thy word; * for all thy commandments are righteous.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let thine hand help me; * for I have chosen thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have longed for thy saving health, O LORD; * and in thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my soul live, and it shall praise thee; * and thy judgments shall help me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have gone astray like a sheep that is lost; * O seek thy servant, for I do not forget thy\r\n    commandments.<br/>\r\n</b></blockquote>\r\n\r\n\r\n<a name=\"finish\"> &nbsp;</a>\r\n\r\n\r\n<br/>\r\n<br/>\r\n\r\n\r\n\r\n\r\n\r\n\r\n', 'Evening', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(55, '25', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 25, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 119 (Section X - XIII)\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Nineteenth Psalm</h3></div>\r\n\r\n<div class=\"text-center\">\r\n    <table width=\"90%\">\r\n\r\n        <tbody><tr>\r\n            <td> <h4><a href=\"#section1\">\r\n                I. Beati immaculati</a><br/>\r\n                <i>(vs 1-8)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section9\">\r\n                IX. Bonitatem fecisti</a><br/>\r\n                <i>(vs 65-72)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section17\">\r\n                XVII. Mirabilia</a><br/>\r\n                <i>(vs 129-136)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section2\">\r\n                II. In quo corrigit?</a><br/>\r\n                <i>(vs 9-16)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section10\">\r\n                X. Manus tuae fecerunt me</a><br/>\r\n                <i>(vs 73-80)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section18\">\r\n                XVIII. Justus es, Domine</a><br/>\r\n                <i>(vs 137-144)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section3\">\r\n                III. Retribue servo tuo</a><br/>\r\n                <i>(vs 17-24)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section11\">\r\n                XI. Defecit anima mea</a><br/>\r\n                <i>(vs 81-88)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section19\">\r\n                XIX. Clamavi in toto corde meo</a><br/>\r\n                <i>(vs 145-152)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section4\">\r\n                IV. Adhaesit pavimento</a><br/>\r\n                <i>(vs 25-32)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section12\">\r\n                XII. In aeternum, Domine</a><br/>\r\n                <i>(vs 89-96)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section20\">\r\n                XX. Vide humilitatem</a><br/>\r\n                <i>(vs 153-160)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section5\">\r\n                V. Legem pone</a><br/>\r\n                <i>(vs 33-40)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section13\">\r\n                XIII. Quomodo dilexi!</a><br/>\r\n                <i>(vs 97-104)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section21\">\r\n                XXI. Principes persecuti sunt</a><br/>\r\n                <i>(vs 161-168)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section6\">\r\n                VI. Et veniat super me</a><br/>\r\n                <i>(vs 41-48)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section14\">\r\n                XIV. Lucerna pedibus meus</a><br/>\r\n                <i>(vs 105-112)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section22\">\r\n                XXII. Appropinquet deprecatio</a><br/>\r\n                <i>(vs 169-176)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section7\">\r\n                VII. Memor esto verbi tui</a><br/>\r\n                <i>(vs 49-56)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section15\">\r\n                XV. Iniquos odio habui</a><br/>\r\n                <i>(vs 113-120)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section8\">\r\n                VIII. Portio mea, Domine</a><br/>\r\n                <i>(vs 57-64)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section16\">\r\n                XVI. Feci judicium</a><br/>\r\n                <i>(vs 121-128)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n        </tbody></table>\r\n</div>\r\n\r\n\r\n<a name=\"section1\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    I. Beati immaculati <i>(vs 1-8)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    BLESSED are those that are undefiled in the way, * and walk in the law of the LORD.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed are they that keep his testimonies, * and seek him with their whole heart;<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Even they who do no wickedness, * and walk in his ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast charged * that we shall diligently keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O that my ways were made so direct, * that I might keep thy statutes!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I not be confounded, * while I have respect unto all thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will thank thee with an unfeigned heart, * when I shall have learned the judgments of thy\r\n    righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will keep thy statutes; * O forsake me not utterly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section2\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    II. In quo corrigit? <i>(vs 9-16)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    WHEREWITHAL shall a young man cleanse his way? * even by ruling himself after thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my whole heart have I sought thee; * O let me not go wrong out of thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word have I hid within my heart, * that I should not sin against thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed art Thou, O LORD; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my lips have I been telling * of all the judgments of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have had as great delight in the way of thy testimonies, * as in all manner of riches.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will talk of thy commandments, * and have respect unto thy ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My delight shall be in thy statutes, * and I will not forget thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section3\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    III. Retribue servo tuo <i>(vs 17-24)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O DO well unto thy servant; * that I may live, and keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Open thou mine eyes; * that I may see the wondrous things of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a stranger upon earth; * O hide not thy commandments from me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul breaketh out for the very fervent desire * that it hath alway unto thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast rebuked the proud; * and cursed are they that do err from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn from me shame and rebuke; * for I have kept thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Princes also did sit and speak against me; * but thy servant is occupied in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For thy testimonies are my delight, * and my counsellors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section4\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IV. Adhaesit pavimento <i>(vs 25-32)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul cleaveth to the dust; * O quicken thou me, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have acknowledged my ways, and thou heardest me: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to understand the way of thy commandments; * and so shall I talk of thy wondrous works.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul melteth away for very heaviness; * comfort thou me according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take from me the way of lying, * and cause thou me to make much of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have chosen the way of truth, * and thy judgments have I laid before me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have stuck unto thy testimonies; * O LORD, confound me not.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will run the way of thy commandments, * when thou hast set my heart at liberty.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section5\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    V. Legem pone <i>(vs 33-40)</i><br/>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    <small> &nbsp; <br/></small>\r\n    TEACH me, O LORD, the way of thy statutes, * and I shall keep it unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Give me understanding, and I shall keep thy law; * yea, I shall keep it with my whole heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to go in the path of thy commandments; * for therein is my desire.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Incline my heart unto thy testimonies, * and not to covetousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn away mine eyes, lest they behold vanity; * and quicken thou me in thy way.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish thy word in thy servant, * that I may fear thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take away the rebuke that I am afraid of; * for thy judgments are good.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Behold, my delight is in thy commandments; * O quicken me in thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section6\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VI. Et veniat super me <i>(vs 41-48)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET thy loving mercy come also unto me, O LORD, * even thy salvation, according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I make answer unto my blasphemers; * for my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O take not the word of thy truth utterly out of my mouth; * for my hope is in thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I alway keep thy law; * yea, for ever and ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And I will walk at liberty; * for I seek thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will speak of thy testimonies also, even before kings, * and will not be ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And my delight shall be in thy commandments, * which I have loved.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My hands also will I lift up unto thy commandments, which I have loved; * and my study shall be in thy\r\n    statutes.<br/>\r\n</b></blockquote>\r\n<small> &nbsp; <br/></small>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section7\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VII. Memor esto verbi tui <i>(vs 49-56)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O THINK upon thy servant, as concerning thy word, * wherein thou hast caused me to put my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The same is my comfort in my trouble; * for thy word hath quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have had me exceedingly in derision; * yet have I not shrinked from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I remembered thine everlasting judgments, O LORD, * and received comfort.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am horribly afraid, * for the ungodly that forsake thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy statutes have been my songs, * in the house of my pilgrimage.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have thought upon thy Name, O LORD, in the night season, * and have kept thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    This I had, * because I kept thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section8\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VIII. Portio mea, Domine <i>(vs 57-64)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THOU art my portion, O LORD; * I have promised to keep thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made my humble petition in thy presence with my whole heart; * O be merciful unto me, according to\r\n    thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I called mine own ways to remembrance, * and turned my feet unto thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made haste, and prolonged not the time, * to keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The snares of the ungodly have compassed me about; * but I have not forgotten thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    At midnight I will rise to give thanks unto thee, * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a companion of all them that fear thee, * and keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The earth, O LORD, is full of thy mercy: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section9\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IX. Bonitatem fecisti <i>(vs 65-72)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thou hast dealt graciously with thy servant, * according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O teach me true understanding and knowledge; * for I have believed thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Before I was troubled, I went wrong; * but now have I kept thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art good and gracious; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have imagined a lie against me; * but I will keep thy commandments with my whole\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Their heart is as fat as brawn; * but my delight hath been in thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is good for me that I have been in trouble; * that I may learn thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The law of thy mouth is dearer unto me * than thousands of gold and silver.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section10\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    X. Manus tuae fecerunt me <i>(vs 73-80)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY hands have made me and fashioned me: * O give me understanding, that I may learn  thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They that fear thee will be glad when they see me; * because I have put my trust in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I know, O LORD, that thy judgments are right, * and that thou of very faithfulness hast caused me to be\r\n    troubled.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy merciful kindness be my comfort, * according to thy word unto thy servant.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy loving mercies come unto me, that I may live; * for thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the proud be confounded, for they go wickedly about to destroy me; * but I will be occupied in thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let such as fear thee, and have known thy testimonies, * be turned unto me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my heart be sound in thy statutes, * that I be not ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section11\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XI. Defecit anima mea <i>(vs 81-88)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul hath longed for thy salvation, * and I have a good hope because of thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes-long sore for thy word; * saying, O when wilt thou comfort me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I am become like a bottle in the smoke; * yet do I not forget thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    How many are the days of thy servant? * when wilt thou be avenged of them that persecute me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have digged pits for me, * which are not after thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    All thy commandments are true: * they persecute me falsely; O be thou my help.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They had almost made an end of me upon earth; * but I forsook not thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O quicken me after thy loving-kindness; * and so shall I keep the testimonies of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section12\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XII. In aeternum, Domine <i>(vs 89-96)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thy word * endureth for ever in heaven.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy truth also remaineth from one generation to another; * thou hast laid the foundation of the earth, and\r\n    it abideth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They continue this day according to thine ordinance; * for all things serve thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    If my delight had not been in thy law, * I should have perished in my trouble.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will never forget thy commandments; * for with them thou hast quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thine: O save me, * for I have sought thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly laid wait for me, to destroy me; * but I will consider thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I see that all things come to an end; * but thy commandment is exceeding broad.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section13\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIII. Quomodo dilexi! <i>(vs 97-104)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LORD, what love have I unto thy law! * all the day long is my study in it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou, through thy commandments, hast made me wiser than mine enemies; * for they are ever with\r\n    me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have more understanding than my teachers; * for thy testimonies are my study.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am wiser than the aged; * because I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have refrained my feet from every evil way, * that I may keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have not shrunk from thy judgments; * for thou teachest me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O how sweet are thy words unto my throat; * yea, sweeter than honey unto my mouth!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Through thy commandments I get understanding: * therefore I hate all evil ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section14\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIV. Lucerna pedibus meus <i>(vs 105-112)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY word is a lantern unto my feet, * and a light unto my paths.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have sworn, and am stedfastly purposed, * to keep thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am troubled above measure: * quicken me, O LORD, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the free-will offerings of my mouth please thee, O LORD; * and teach me thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul is alway in my hand; * yet do I not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly have laid a snare for me; * but yet I swerved not from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy testimonies have I claimed as mine heritage for ever; * and why? they are the very joy of my\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have applied my heart to fulfil thy statutes alway, * even unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section15\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XV. Iniquos odio habui <i>(vs 113-120)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I HATE them that imagine evil things; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art my defence and shield; * and my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Away from me, ye wicked; * I will keep the commandments of my God.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish me according to thy word, that I may live; * and let me not be disappointed of my hope.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hold thou me up, and I shall be safe; * yea, my delight shall be ever in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast trodden down all them that depart  from thy statutes; * for they imagine but deceit.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou puttest away all the ungodly of the earth like dross; * therefore I love thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My flesh trembleth for fear of thee; * and I am afraid of thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section16\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVI. Feci judicium <i>(vs 121-128)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I DEAL with the thing that is lawful and right; * O give me not over unto mine oppressors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make thou thy servant to delight in that which is good, * that the proud do me no wrong.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes are wasted away with looking for thy health, * and for the word of thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deal with thy servant according unto thy loving mercy, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thy servant; O grant me understanding, * that I may know thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is time for thee, LORD, to lay to thine hand; * for they have destroyed thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I love thy commandments * above gold and precious stones.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Therefore hold I straight all thy commandments; * and all false ways I utterly abhor.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section17\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVII. Mirabilia <i>(vs 129-136)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY testimonies are wonderful; * therefore doth my soul keep them.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    When thy word goeth forth, * it giveth light and understanding unto the simple.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I opened my mouth, and drew in my breath; * for my delight was in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O look thou upon me, and be merciful unto me, * as thou usest to do unto those that love thy Name.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Order my steps in thy word; * and so shall no wickedness have dominion over me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deliver me from the wrongful dealings of men; * and so shall I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Show the light of thy countenance upon thy servant, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes gush out with water, * because men keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section18\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVIII. Justus es, Domine <i>(vs 137-144)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    RIGHTEOUS art thou, O LORD; * and true are thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The testimonies that thou hast commanded * are exceeding righteous and true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My zeal hath even consumed me; * because mine enemies have forgotten thy words.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is tried to the uttermost, * and thy servant loveth it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am small and of no reputation; * yet do I not forget thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy righteousness is an everlasting righteousness, * and thy law is the truth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Trouble and heaviness have taken hold upon me; * yet is my delight in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The righteousness of thy testimonies is everlasting: * O grant me understanding, and I shall live.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section19\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIX. Clamavi in toto corde meo <i>(vs 145-152)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I CALL with my whole heart; * hear me, O LORD; I will keep thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, even unto thee do I call; * help me, and I shall keep thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Early in the morning do I cry unto thee; * for in thy word is my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes prevent the night watches; * that I might be occupied in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hear my voice, O LORD, according unto thy loving-kindness; * quicken me, according to thy\r\n    judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They draw nigh that of malice persecute me, * and are far from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Be thou nigh at hand, O LORD; * for all thy commandments are true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As concerning thy testimonies, I have known long since, * that thou hast grounded them for ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section20\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XX. Vide humilitatem <i>(vs 153-160)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O CONSIDER mine adversity, and deliver me, * for I do not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Avenge thou my cause, and deliver me; * quicken me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Health is far from the ungodly; * for they regard not thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is thy mercy, O LORD; * quicken me, as thou art wont.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Many there are that trouble me, and persecute me; * yet do I not swerve from thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It grieveth me when I see the transgressors; * because they keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Consider, O LORD, how I love thy commandments; * O quicken me, according to thy loving-\r\n    kindness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is true from everlasting; * all the judgments of thy righteousness endure for evermore.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section21\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXI. Principes persecuti sunt <i>(vs 161-168)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    PRINCES have persecuted me without a cause; * but my heart standeth in awe of thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am as glad of thy word, * as one that findeth great spoils.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As for lies, I hate and abhor them; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Seven times a day do I praise thee; * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is the peace that they have who love thy law; * and they have none occasion of stumbling.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    LORD, I have looked for thy saving health, * and done after thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul hath kept thy testimonies, * and loved them exceedingly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have kept thy commandments and testimonies; * for all my ways are before thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section22\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXII. Appropinquet deprecatio <i>(vs 169-176)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET my complaint come before thee, O LORD; * give me understanding according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let my supplication come before thee; * deliver me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My lips shall speak of thy praise, * when thou hast taught me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, my tongue shall sing of thy word; * for all thy commandments are righteous.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let thine hand help me; * for I have chosen thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have longed for thy saving health, O LORD; * and in thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my soul live, and it shall praise thee; * and thy judgments shall help me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have gone astray like a sheep that is lost; * O seek thy servant, for I do not forget thy\r\n    commandments.<br/>\r\n</b></blockquote>\r\n\r\n\r\n<a name=\"finish\"> &nbsp;</a>\r\n\r\n\r\n<br/>\r\n<br/>\r\n\r\n\r\n\r\n\r\n\r\n\r\n', 'Evening', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(56, '26', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 26, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 119 (Section XIX - XXII)\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Nineteenth Psalm</h3></div>\r\n\r\n<div class=\"text-center\">\r\n    <table width=\"90%\">\r\n\r\n        <tbody><tr>\r\n            <td> <h4><a href=\"#section1\">\r\n                I. Beati immaculati</a><br/>\r\n                <i>(vs 1-8)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section9\">\r\n                IX. Bonitatem fecisti</a><br/>\r\n                <i>(vs 65-72)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section17\">\r\n                XVII. Mirabilia</a><br/>\r\n                <i>(vs 129-136)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section2\">\r\n                II. In quo corrigit?</a><br/>\r\n                <i>(vs 9-16)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section10\">\r\n                X. Manus tuae fecerunt me</a><br/>\r\n                <i>(vs 73-80)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section18\">\r\n                XVIII. Justus es, Domine</a><br/>\r\n                <i>(vs 137-144)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section3\">\r\n                III. Retribue servo tuo</a><br/>\r\n                <i>(vs 17-24)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section11\">\r\n                XI. Defecit anima mea</a><br/>\r\n                <i>(vs 81-88)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section19\">\r\n                XIX. Clamavi in toto corde meo</a><br/>\r\n                <i>(vs 145-152)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section4\">\r\n                IV. Adhaesit pavimento</a><br/>\r\n                <i>(vs 25-32)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section12\">\r\n                XII. In aeternum, Domine</a><br/>\r\n                <i>(vs 89-96)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section20\">\r\n                XX. Vide humilitatem</a><br/>\r\n                <i>(vs 153-160)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section5\">\r\n                V. Legem pone</a><br/>\r\n                <i>(vs 33-40)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section13\">\r\n                XIII. Quomodo dilexi!</a><br/>\r\n                <i>(vs 97-104)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section21\">\r\n                XXI. Principes persecuti sunt</a><br/>\r\n                <i>(vs 161-168)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section6\">\r\n                VI. Et veniat super me</a><br/>\r\n                <i>(vs 41-48)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section14\">\r\n                XIV. Lucerna pedibus meus</a><br/>\r\n                <i>(vs 105-112)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section22\">\r\n                XXII. Appropinquet deprecatio</a><br/>\r\n                <i>(vs 169-176)</i>\r\n            </h4>\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section7\">\r\n                VII. Memor esto verbi tui</a><br/>\r\n                <i>(vs 49-56)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section15\">\r\n                XV. Iniquos odio habui</a><br/>\r\n                <i>(vs 113-120)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td> <h4><a href=\"#section8\">\r\n                VIII. Portio mea, Domine</a><br/>\r\n                <i>(vs 57-64)</i>\r\n            </h4>\r\n            </td>\r\n            <td> <h4><a href=\"#section16\">\r\n                XVI. Feci judicium</a><br/>\r\n                <i>(vs 121-128)</i>\r\n            </h4>\r\n            </td>\r\n            <td>\r\n                &nbsp;\r\n            </td>\r\n        </tr>\r\n        </tbody></table>\r\n</div>\r\n\r\n\r\n<a name=\"section1\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    I. Beati immaculati <i>(vs 1-8)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    BLESSED are those that are undefiled in the way, * and walk in the law of the LORD.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed are they that keep his testimonies, * and seek him with their whole heart;<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Even they who do no wickedness, * and walk in his ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast charged * that we shall diligently keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O that my ways were made so direct, * that I might keep thy statutes!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I not be confounded, * while I have respect unto all thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will thank thee with an unfeigned heart, * when I shall have learned the judgments of thy\r\n    righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will keep thy statutes; * O forsake me not utterly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section2\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    II. In quo corrigit? <i>(vs 9-16)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    WHEREWITHAL shall a young man cleanse his way? * even by ruling himself after thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my whole heart have I sought thee; * O let me not go wrong out of thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word have I hid within my heart, * that I should not sin against thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Blessed art Thou, O LORD; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    With my lips have I been telling * of all the judgments of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have had as great delight in the way of thy testimonies, * as in all manner of riches.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will talk of thy commandments, * and have respect unto thy ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My delight shall be in thy statutes, * and I will not forget thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section3\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    III. Retribue servo tuo <i>(vs 17-24)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O DO well unto thy servant; * that I may live, and keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Open thou mine eyes; * that I may see the wondrous things of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a stranger upon earth; * O hide not thy commandments from me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul breaketh out for the very fervent desire * that it hath alway unto thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast rebuked the proud; * and cursed are they that do err from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn from me shame and rebuke; * for I have kept thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Princes also did sit and speak against me; * but thy servant is occupied in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For thy testimonies are my delight, * and my counsellors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section4\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IV. Adhaesit pavimento <i>(vs 25-32)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul cleaveth to the dust; * O quicken thou me, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have acknowledged my ways, and thou heardest me: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to understand the way of thy commandments; * and so shall I talk of thy wondrous works.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul melteth away for very heaviness; * comfort thou me according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take from me the way of lying, * and cause thou me to make much of thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have chosen the way of truth, * and thy judgments have I laid before me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have stuck unto thy testimonies; * O LORD, confound me not.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will run the way of thy commandments, * when thou hast set my heart at liberty.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section5\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    V. Legem pone <i>(vs 33-40)</i><br/>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    <small> &nbsp; <br/></small>\r\n    TEACH me, O LORD, the way of thy statutes, * and I shall keep it unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Give me understanding, and I shall keep thy law; * yea, I shall keep it with my whole heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make me to go in the path of thy commandments; * for therein is my desire.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Incline my heart unto thy testimonies, * and not to covetousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O turn away mine eyes, lest they behold vanity; * and quicken thou me in thy way.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish thy word in thy servant, * that I may fear thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Take away the rebuke that I am afraid of; * for thy judgments are good.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Behold, my delight is in thy commandments; * O quicken me in thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section6\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VI. Et veniat super me <i>(vs 41-48)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET thy loving mercy come also unto me, O LORD, * even thy salvation, according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I make answer unto my blasphemers; * for my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O take not the word of thy truth utterly out of my mouth; * for my hope is in thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    So shall I alway keep thy law; * yea, for ever and ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And I will walk at liberty; * for I seek thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will speak of thy testimonies also, even before kings, * and will not be ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    And my delight shall be in thy commandments, * which I have loved.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My hands also will I lift up unto thy commandments, which I have loved; * and my study shall be in thy\r\n    statutes.<br/>\r\n</b></blockquote>\r\n<small> &nbsp; <br/></small>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section7\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VII. Memor esto verbi tui <i>(vs 49-56)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O THINK upon thy servant, as concerning thy word, * wherein thou hast caused me to put my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The same is my comfort in my trouble; * for thy word hath quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have had me exceedingly in derision; * yet have I not shrinked from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I remembered thine everlasting judgments, O LORD, * and received comfort.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am horribly afraid, * for the ungodly that forsake thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy statutes have been my songs, * in the house of my pilgrimage.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have thought upon thy Name, O LORD, in the night season, * and have kept thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    This I had, * because I kept thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section8\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    VIII. Portio mea, Domine <i>(vs 57-64)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THOU art my portion, O LORD; * I have promised to keep thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made my humble petition in thy presence with my whole heart; * O be merciful unto me, according to\r\n    thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I called mine own ways to remembrance, * and turned my feet unto thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I made haste, and prolonged not the time, * to keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The snares of the ungodly have compassed me about; * but I have not forgotten thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    At midnight I will rise to give thanks unto thee, * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am a companion of all them that fear thee, * and keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The earth, O LORD, is full of thy mercy: * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section9\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    IX. Bonitatem fecisti <i>(vs 65-72)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thou hast dealt graciously with thy servant, * according unto thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O teach me true understanding and knowledge; * for I have believed thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Before I was troubled, I went wrong; * but now have I kept thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art good and gracious; * O teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have imagined a lie against me; * but I will keep thy commandments with my whole\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Their heart is as fat as brawn; * but my delight hath been in thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is good for me that I have been in trouble; * that I may learn thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The law of thy mouth is dearer unto me * than thousands of gold and silver.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section10\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    X. Manus tuae fecerunt me <i>(vs 73-80)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY hands have made me and fashioned me: * O give me understanding, that I may learn  thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They that fear thee will be glad when they see me; * because I have put my trust in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I know, O LORD, that thy judgments are right, * and that thou of very faithfulness hast caused me to be\r\n    troubled.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy merciful kindness be my comfort, * according to thy word unto thy servant.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let thy loving mercies come unto me, that I may live; * for thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the proud be confounded, for they go wickedly about to destroy me; * but I will be occupied in thy\r\n    commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let such as fear thee, and have known thy testimonies, * be turned unto me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my heart be sound in thy statutes, * that I be not ashamed.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section11\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XI. Defecit anima mea <i>(vs 81-88)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    MY soul hath longed for thy salvation, * and I have a good hope because of thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes-long sore for thy word; * saying, O when wilt thou comfort me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I am become like a bottle in the smoke; * yet do I not forget thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    How many are the days of thy servant? * when wilt thou be avenged of them that persecute me?<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The proud have digged pits for me, * which are not after thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    All thy commandments are true: * they persecute me falsely; O be thou my help.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They had almost made an end of me upon earth; * but I forsook not thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O quicken me after thy loving-kindness; * and so shall I keep the testimonies of thy mouth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section12\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XII. In aeternum, Domine <i>(vs 89-96)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O LORD, thy word * endureth for ever in heaven.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy truth also remaineth from one generation to another; * thou hast laid the foundation of the earth, and\r\n    it abideth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They continue this day according to thine ordinance; * for all things serve thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    If my delight had not been in thy law, * I should have perished in my trouble.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I will never forget thy commandments; * for with them thou hast quickened me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thine: O save me, * for I have sought thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly laid wait for me, to destroy me; * but I will consider thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I see that all things come to an end; * but thy commandment is exceeding broad.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section13\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIII. Quomodo dilexi! <i>(vs 97-104)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LORD, what love have I unto thy law! * all the day long is my study in it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou, through thy commandments, hast made me wiser than mine enemies; * for they are ever with\r\n    me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have more understanding than my teachers; * for thy testimonies are my study.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am wiser than the aged; * because I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have refrained my feet from every evil way, * that I may keep thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have not shrunk from thy judgments; * for thou teachest me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O how sweet are thy words unto my throat; * yea, sweeter than honey unto my mouth!<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Through thy commandments I get understanding: * therefore I hate all evil ways.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section14\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIV. Lucerna pedibus meus <i>(vs 105-112)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY word is a lantern unto my feet, * and a light unto my paths.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have sworn, and am stedfastly purposed, * to keep thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am troubled above measure: * quicken me, O LORD, according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let the free-will offerings of my mouth please thee, O LORD; * and teach me thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul is alway in my hand; * yet do I not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The ungodly have laid a snare for me; * but yet I swerved not from thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy testimonies have I claimed as mine heritage for ever; * and why? they are the very joy of my\r\n    heart.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have applied my heart to fulfil thy statutes alway, * even unto the end.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section15\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XV. Iniquos odio habui <i>(vs 113-120)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I HATE them that imagine evil things; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou art my defence and shield; * and my trust is in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Away from me, ye wicked; * I will keep the commandments of my God.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O stablish me according to thy word, that I may live; * and let me not be disappointed of my hope.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hold thou me up, and I shall be safe; * yea, my delight shall be ever in thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou hast trodden down all them that depart  from thy statutes; * for they imagine but deceit.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thou puttest away all the ungodly of the earth like dross; * therefore I love thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My flesh trembleth for fear of thee; * and I am afraid of thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section16\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVI. Feci judicium <i>(vs 121-128)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I DEAL with the thing that is lawful and right; * O give me not over unto mine oppressors.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Make thou thy servant to delight in that which is good, * that the proud do me no wrong.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes are wasted away with looking for thy health, * and for the word of thy righteousness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deal with thy servant according unto thy loving mercy, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am thy servant; O grant me understanding, * that I may know thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It is time for thee, LORD, to lay to thine hand; * for they have destroyed thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    For I love thy commandments * above gold and precious stones.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Therefore hold I straight all thy commandments; * and all false ways I utterly abhor.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section17\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVII. Mirabilia <i>(vs 129-136)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    THY testimonies are wonderful; * therefore doth my soul keep them.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    When thy word goeth forth, * it giveth light and understanding unto the simple.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I opened my mouth, and drew in my breath; * for my delight was in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O look thou upon me, and be merciful unto me, * as thou usest to do unto those that love thy Name.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Order my steps in thy word; * and so shall no wickedness have dominion over me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O deliver me from the wrongful dealings of men; * and so shall I keep thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Show the light of thy countenance upon thy servant, * and teach me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes gush out with water, * because men keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section18\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XVIII. Justus es, Domine <i>(vs 137-144)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    RIGHTEOUS art thou, O LORD; * and true are thy judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The testimonies that thou hast commanded * are exceeding righteous and true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My zeal hath even consumed me; * because mine enemies have forgotten thy words.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is tried to the uttermost, * and thy servant loveth it.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am small and of no reputation; * yet do I not forget thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy righteousness is an everlasting righteousness, * and thy law is the truth.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Trouble and heaviness have taken hold upon me; * yet is my delight in thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    The righteousness of thy testimonies is everlasting: * O grant me understanding, and I shall live.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section19\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XIX. Clamavi in toto corde meo <i>(vs 145-152)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    I CALL with my whole heart; * hear me, O LORD; I will keep thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, even unto thee do I call; * help me, and I shall keep thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Early in the morning do I cry unto thee; * for in thy word is my trust.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Mine eyes prevent the night watches; * that I might be occupied in thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Hear my voice, O LORD, according unto thy loving-kindness; * quicken me, according to thy\r\n    judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    They draw nigh that of malice persecute me, * and are far from thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Be thou nigh at hand, O LORD; * for all thy commandments are true.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As concerning thy testimonies, I have known long since, * that thou hast grounded them for ever.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section20\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XX. Vide humilitatem <i>(vs 153-160)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    O CONSIDER mine adversity, and deliver me, * for I do not forget thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Avenge thou my cause, and deliver me; * quicken me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Health is far from the ungodly; * for they regard not thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is thy mercy, O LORD; * quicken me, as thou art wont.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Many there are that trouble me, and persecute me; * yet do I not swerve from thy testimonies.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    It grieveth me when I see the transgressors; * because they keep not thy law.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Consider, O LORD, how I love thy commandments; * O quicken me, according to thy loving-\r\n    kindness.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Thy word is true from everlasting; * all the judgments of thy righteousness endure for evermore.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section21\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXI. Principes persecuti sunt <i>(vs 161-168)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    PRINCES have persecuted me without a cause; * but my heart standeth in awe of thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I am as glad of thy word, * as one that findeth great spoils.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    As for lies, I hate and abhor them; * but thy law do I love.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Seven times a day do I praise thee; * because of thy righteous judgments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Great is the peace that they have who love thy law; * and they have none occasion of stumbling.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    LORD, I have looked for thy saving health, * and done after thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My soul hath kept thy testimonies, * and loved them exceedingly.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have kept thy commandments and testimonies; * for all my ways are before thee.<br/>\r\n    <small> &nbsp; <br/></small>\r\n</b></blockquote>\r\n<div class=\"text-center\"><a href=\"#finish\">Go to the end of Psalm 119</a></div>\r\n<small> &nbsp; <br/></small>\r\n<small> &nbsp; <br/></small>\r\n\r\n\r\n<a name=\"section22\">&nbsp;</a><div class=\"text-center\"><h4>\r\n    XXII. Appropinquet deprecatio <i>(vs 169-176)</i><br/>\r\n    <small> &nbsp; <br/></small>\r\n</h4>\r\n</div>\r\n\r\n<blockquote><b>\r\n    LET my complaint come before thee, O LORD; * give me understanding according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let my supplication come before thee; * deliver me according to thy word.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    My lips shall speak of thy praise, * when thou hast taught me thy statutes.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Yea, my tongue shall sing of thy word; * for all thy commandments are righteous.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    Let thine hand help me; * for I have chosen thy commandments.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have longed for thy saving health, O LORD; * and in thy law is my delight.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    O let my soul live, and it shall praise thee; * and thy judgments shall help me.<br/>\r\n    <small> &nbsp; <br/></small>\r\n    I have gone astray like a sheep that is lost; * O seek thy servant, for I do not forget thy\r\n    commandments.<br/>\r\n</b></blockquote>\r\n\r\n\r\n<a name=\"finish\"> &nbsp;</a>\r\n\r\n\r\n<br/>\r\n<br/>\r\n\r\n\r\n\r\n\r\n\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(57, '27', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 27, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        <a href=\"psalm126.cfm\"> Psalm 126 </a>&nbsp; &nbsp;\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twenty Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>In convertendo.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            WHEN the LORD turned again the captivity of Sion,\r\n            * then were we like unto them that dream.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then was our mouth filled with laughter,\r\n            * and our tongue with joy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Then said they among the heathen,\r\n            * The LORD hath done great things for them.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, the LORD hath done great things for us already;\r\n            * whereof we rejoice.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Turn our captivity, O LORD,\r\n            * as the rivers in the south.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            They that sow in tears\r\n            * shall reap in joy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He that now goeth on his way weeping,\r\n            and beareth forth good seed,\r\n            * shall doubtless come again with joy,\r\n            and bring his sheaves with him.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        <a href=\"psalm127.cfm\"> Psalm 127 </a>&nbsp; &nbsp;\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twenty Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Nisi Dominus.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            EXCEPT the LORD build the house,\r\n            * their labour is but lost that build it.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Except the LORD keep the city,\r\n            * the watchman waketh but in vain.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            It is but lost labour that ye haste to rise up early, and\r\n            so late take rest, and eat the bread of carefulness;\r\n            * for so he giveth his beloved sleep.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lo, children, and the fruit of the womb,\r\n            * are an heritage and gift that cometh of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Like as the arrows in the hand of the giant,\r\n            * even so are the young children.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Happy is the man that hath his quiver full of them;\r\n            * they shall not be ashamed when they speak with their\r\n            enemies in the gate.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        <a href=\"psalm128.cfm\"> Psalm 128 </a>&nbsp; &nbsp;\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twenty Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Beates omnes.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            BLESSED are all they that fear the LORD,\r\n            * and walk in his ways.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For thou shalt eat the labours of thine hands:\r\n            * O well is thee, and happy shalt thou be.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy wife shall be as the fruitful vine\r\n            * upon the walls of thine house;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Thy children like the olive-branches\r\n            * round about thy table.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Lo, thus shall the man be blessed\r\n            * that feareth the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD from out of Sion shall so bless thee,\r\n            * that thou shalt see Jerusalem in prosperity all thy life long;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, that thou shalt see thy children\'s children,\r\n            * and peace upon Israel.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        <a href=\"psalm129.cfm\"> Psalm 129 </a>&nbsp; &nbsp;\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Twenty Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Saepe expugnaverunt.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            MANY a time have they fought against me from my youth up,\r\n            * may Israel now say;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, many a time have they vexed me from my youth up;\r\n            * but they have not prevailed against me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The plowers plowed upon my back,\r\n            * and made long furrows.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But the righteous LORD\r\n            * hath hewn the snares of the ungodly in pieces.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them be confounded and turned backward,\r\n            * as many as have evil will at Sion.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them be even as the grass upon the housetops,\r\n            * which withereth afore it be grown up;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Whereof the mower filleth not his hand,\r\n            * neither he that bindeth up the sheaves his bosom.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            So that they who go by say not so much as, The LORD prosper you;\r\n            * we wish you good luck in the Name of the LORD.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        <a href=\"psalm130.cfm\"> Psalm 130 </a>&nbsp; &nbsp;\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirtieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>De profundis.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            OUT of the deep have I called unto thee, O LORD;\r\n            * Lord, hear my voice.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let thine ears consider well\r\n            * the voice of my complaint.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            If thou, LORD, wilt be extreme to mark what is done amiss,\r\n            * O Lord, who may abide it?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For there is mercy with thee;\r\n            * therefore shalt thou be feared.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I look for the LORD; my soul doth wait for him;\r\n            * in his word is my trust.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            My soul fleeth unto the Lord before the morning watch;\r\n            * I say, before the morning watch.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O Israel, trust in the LORD; for with the LORD  there is mercy,\r\n            * and with him is plenteous redemption.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And he shall redeem Israel\r\n            * from all his sins.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        <a href=\"psalm131.cfm\"> Psalm 131 </a>&nbsp; &nbsp;\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirty First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, non est.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            LORD, I am not high-minded;\r\n            * I have no proud looks.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I do not exercise myself in great matters\r\n            * which are too high for me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But I refrain my soul, and keep it low, like as a child that is weaned from his mother:\r\n            * yea, my soul is even as a weaned child.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O Israel, trust in the LORD\r\n            * from this time forth for evermore.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00');
INSERT INTO `daily_psalter` (`id`, `day_name`, `text`, `day_type`, `inserted_at`) VALUES
(58, '28', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 28, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 136\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirty Sixth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Confitemini.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O GIVE thanks unto the LORD, for he is gracious:\r\n            * and his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O give thanks unto the God of all gods:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O thank the Lord of all lords:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who only doeth great wonders:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who by his excellent wisdom made the heavens:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who laid out the earth above the waters:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who hath made great lights:\r\n            * for his mercy endureth for ever:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The sun to rule the day:\r\n            * for his mercy endureth for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The moon and the stars to govern the night:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who smote Egypt, with their firstborn:\r\n            * for his mercy endureth for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And brought out Israel from among them:\r\n            * for his mercy endureth for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            With a mighty hand and stretched-out arm:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who divided the Red Sea in two parts:\r\n            * for his mercy endureth for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And made Israel to go through the midst of it:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But as for Pharaoh and his host, he overthrew them in the Red Sea:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who led his people through the wilderness:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who smote great kings:\r\n            * for his mercy endureth for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, and slew mighty kings:\r\n            * for his mercy endureth for ever:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Sihon, king of the Amorites:\r\n            * for his mercy endureth for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And Og, the king of Bashan:\r\n            * for his mercy endureth for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And gave away their land for an heritage:\r\n            * for his mercy endureth for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Even for an heritage unto Israel his servant:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who remembered us when we were in trouble:\r\n            * for his mercy endureth for ever;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And hath delivered us from our enemies:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who giveth food to all flesh:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O give thanks unto the God of heaven:\r\n            * for his mercy endureth for ever.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O give thanks unto the Lord of lords:\r\n            * for his mercy endureth for ever.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 137\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirty Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Super flumina.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        BY the waters of Babylon we sat down and wept,\r\n        * when we remembered thee, O Sion.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        As for our harps, we hanged them up\r\n        * upon the trees that are therein.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        For they that led us away captive, required of us then a song, and melody in our heaviness:\r\n        * Sing us one of the songs of Sion.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        How shall we sing the LORD\'S song\r\n        * in a strange land?<br/>\r\n        <small> &nbsp; <br/></small>\r\n        If I forget thee, O Jerusalem,\r\n        * let my right hand forget her cunning.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        If I do not remember thee, let my tongue cleave to the roof of my mouth;\r\n        * yea, if I prefer not Jerusalem above my chief joy.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 7 </i>Remember the children of Edom, O LORD, in the day of Jerusalem;\r\n        * how they said, Down with it, down with it, even to the ground.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O daughter of Babylon, wasted with misery;\r\n        * yea, happy shall he be that rewardeth thee as thou hast served us.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Blessed shall he be that taketh thy children,\r\n        * and throweth them against the stones.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 138\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Thirty Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Confitebor tibi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I WILL give thanks unto thee, O Lord, with my whole heart;\r\n            * even before the gods will I sing praise unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I will worship toward thy holy temple, and praise thy\r\n            Name, because of thy loving-kindness and truth;\r\n            * for thou hast magnified thy Name, and thy word,\r\n            above all things.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When I called upon thee, thou heardest me;\r\n            * and enduedst my soul with much strength.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            All the kings of the earth shall praise thee, O LORD;\r\n            * for they have heard the words of thy mouth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yea, they shall sing of the ways of the LORD,\r\n            * that great is the glory of the LORD.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For though the LORD be high, yet hath he respect\r\n            unto the lowly;\r\n            * as for the proud, he beholdeth them afar off.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Though I walk in the midst of trouble, yet shalt thou\r\n            refresh me;\r\n            * thou shalt stretch forth thy hand upon the furiousness of\r\n            mine enemies, and thy right hand shall save me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD shall make good his loving-kindness\r\n            toward me;\r\n            * yea, thy mercy, O LORD, endureth for ever; despise not then\r\n            the works of thine own hands.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(59, '29', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 29, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 141\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Forty First Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, clamavi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <b>\r\n        LORD, I call upon thee; haste thee unto me,\r\n        * and consider my voice, when I cry unto thee.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let my prayer be set forth in thy sight as the incense;\r\n        * and let the lifting up of my hands be an evening sacrifice.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Set a watch, O LORD, before my mouth,\r\n        * and keep the door of my lips.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        O let not mine heart be inclined to any evil thing;\r\n        * let me not be occupied in ungodly works with the men that work wickedness, neither let me eat of such things as please them.<br/>\r\n        <small> &nbsp; <br/></small>\r\n    </b>\r\n</blockquote>\r\n\r\n<hr/>\r\n\r\n<blockquote>\r\n    <b>\r\n        <small> &nbsp; <br/></small>\r\n        <i>vs 5 </i>Let the righteous rather smite me friendly, and reprove me;\r\n        * yea, let not my head refuse their precious balms.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        As for the ungodly,\r\n        * I will pray yet against their wickedness.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let their judges be overthrown in stony places,\r\n        * that they may hear my words; for they are sweet.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Our bones lie scattered before the pit,\r\n        * like as when one breaketh and heweth wood upon the earth.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        But mine eyes look unto thee, O LORD God;\r\n        * in thee is my trust; O cast not out my soul.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Keep me from the snare that they have laid for me,\r\n        * and from the traps of the wicked doers.<br/>\r\n        <small> &nbsp; <br/></small>\r\n        Let the ungodly fall into their own nets together,\r\n        * and let me ever escape them.\r\n    </b>\r\n    <br/>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 142\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Forty Second Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Voce mea ad Dominum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            I CRIED unto the LORD with my voice;\r\n            *  yea, even unto the LORD did I make my supplication.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I poured out my complaints before him,\r\n            *  and showed him of my trouble.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            When my spirit was in heaviness, thou knewest my path;\r\n            *  in the way wherein I walked, have they privily laid a snare for me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I looked also upon my right hand,\r\n            *  and saw there was no man that would know me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I had no place to flee unto,\r\n            *  and no man cared for my soul.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I cried unto thee, O LORD, and said,\r\n            *  Thou art my hope, and my portion in the land of the living.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Consider my complaint;\r\n            *  for I am brought very low.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O deliver me from my persecutors;\r\n            *  for they are too strong for me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Bring my soul out of prison, that I may give thanks unto thy Name;\r\n            *  which thing if thou wilt grant me, then shall the righteous resort unto my company.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 143\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Forty Third Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Domine, exaudi.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            HEAR my prayer, O LORD, and consider my desire;\r\n            *  hearken unto me for thy truth and righteousness\' sake.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And enter not into judgment with thy servant;\r\n            *  for in thy sight shall no man living be justified.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the enemy hath persecuted my soul; he hath smitten my life down to the ground;\r\n            *  he hath laid me in the darkness, as the men that have been long dead.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Therefore is my spirit vexed within me,\r\n            *  and my heart within me is desolate.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Yet do I remember the time past; I muse upon all thy works;\r\n            *  yea, I exercise myself in the works of thy hands.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            I stretch forth my hands unto thee;\r\n            *  my soul gaspeth unto thee as a thirsty land.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Hear me, O LORD, and that soon; for my spirit waxeth faint:\r\n            *  hide not thy face from me, lest I be like unto them that go down into the pit.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O let me hear thy loving-kindness betimes in the morning; for in thee is my trust:\r\n            *  show thou me the way that I should walk in; for I lift up my soul unto thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Deliver me, O LORD, from mine enemies;\r\n            *  for I flee unto thee to hide me.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Teach me to do the thing that pleaseth thee; for thou art my God:\r\n            *  let thy loving Spirit lead me forth into the land of righteousness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Quicken me, O LORD, for thy Name\'s sake;\r\n            *  and for thy righteousness\' sake bring my soul out of trouble.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            And of thy goodness slay mine enemies,\r\n            *  and destroy all them that vex my soul; for I am thy servant.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00'),
(60, '30', '\r\n\r\n<div class=\"text-center\">\r\n    <h2><br/>The Psalter for Day 30, Evening<br/></h2>\r\n    <br/>\r\n\r\n    <h3>\r\n        Psalm 147\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Forty Seventh Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Laudate Dominum.</i></h4></div>\r\n<hr/>\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            PRAISE the LORD, for it is a good thing to sing praises\r\n            unto our God;\r\n            * yea, a joyful and pleasant thing it is to be thankful.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD doth build up Jerusalem,\r\n            * and gather together the outcasts of Israel.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He healeth those that are broken in heart,\r\n            * and giveth medicine to heal their sickness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He telleth the number of the stars,\r\n            * and calleth them all by their names.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Great is our Lord, and great is his power;\r\n            * yea, and his wisdom is infinite.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            The LORD setteth up the meek,\r\n            * and bringeth the ungodly down to the ground.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            O sing unto the LORD with thanksgiving;\r\n            * sing praises upon the harp unto our God:<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who covereth the heaven with clouds, and prepareth rain\r\n            for the earth;\r\n            * and maketh the grass to grow upon the mountains,\r\n            and herb for the use of men;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Who giveth fodder unto the cattle,\r\n            * and feedeth the young ravens that call upon him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath no pleasure in the strength of an horse;\r\n            * neither delighteth he in any man\'s legs.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            But the LORD\'S delight is in them that fear him,\r\n            * and put their trust in his mercy.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise the LORD, O Jerusalem;\r\n            * praise thy God, O Sion.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For he hath made fast the bars of thy gates,\r\n            * and hath blessed thy children within thee.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He maketh peace in thy borders,\r\n            * and filleth thee with the flour of wheat.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He sendeth forth his commandment upon earth,\r\n            * and his word runneth very swiftly.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He giveth snow like wool,\r\n            * and scattereth the hoarfrost like ashes.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He casteth forth his ice like morsels:\r\n            * who is able to abide his frost?<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He sendeth out his word, and melteth them:\r\n            * he bloweth with his wind, and the waters flow.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He showeth his word unto Jacob,\r\n            * his statutes and ordinances unto Israel.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath not dealt so with any nation;\r\n            * neither have the heathen knowledge of his laws.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 148\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Forty Eighth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Laudate Dominum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O PRAISE the LORD from the heavens:\r\n            * praise him in the heights.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise him, all ye angels of his:\r\n            * praise him, all his host.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise him, sun and moon:\r\n            * praise him, all ye stars and light.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise him, all ye heavens,\r\n            * and ye waters that are above the heavens.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them praise the Name of the LORD:\r\n            * for he spake the word, and they were made;\r\n            he commanded, and they were created.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He hath made them fast for ever and ever:\r\n            * he hath given them a law which shall not be broken.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise the LORD from the earth,\r\n            * ye dragons and all deeps;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Fire and hail, snow and vapours,\r\n            * wind and storm, fulfilling his word;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Mountains and all hills;\r\n            * fruitful trees and all cedars;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Beasts and all cattle;\r\n            * creeping things and flying fowls;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Kings of the earth, and all peoples;\r\n            * princes, and all judges of the world;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Young men and maidens, old men and children,\r\n            praise the Name of the LORD:\r\n            * for his Name only is excellent, and his praise above\r\n            heaven and earth.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            He shall exalt the horn of his people: all his saints\r\n            shall praise him;\r\n            * even the children of Israel, even the people that\r\n            serveth him.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 149\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Forty Ninth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Cantate Domino.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O SING unto the LORD a new song;\r\n            *  let the congregation of saints praise him.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let Israel rejoice in him that made him,\r\n            *  and let the children of Sion be joyful in their King.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let them praise his Name in the dance:\r\n            *  let them sing praises unto him with tabret and harp.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            For the LORD hath pleasure in his people,\r\n            *  and helpeth the meek-hearted.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the saints be joyful with glory;\r\n            *  let them rejoice in their beds.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let the praises of God be in their mouth;\r\n            *  and a two-edged sword in their hands;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            To be avenged of the nations,\r\n            *  and to rebuke the peoples;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            To bind their kings in chains,\r\n            *  and their nobles with links of iron;<br/>\r\n            <small> &nbsp; <br/></small>\r\n            To execute judgment upon them; as it is written,\r\n            *  Such honour have all his saints.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n\r\n\r\n\r\n<div class=\"text-center\">\r\n    <h3>\r\n        Psalm 150\r\n    </h3>\r\n</div>\r\n\r\n\r\n<div class=\"text-center\"><h3>The One Hundred Fiftieth Psalm</h3></div>\r\n<div class=\"text-center\"><h4><i>Laudate Dominum.</i></h4></div>\r\n<hr/>\r\n\r\n\r\n<blockquote>\r\n    <p>\r\n        <b>\r\n            O PRAISE God in his sanctuary:\r\n            * praise him in the firmament of his power.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise him in his noble acts:\r\n            * praise him according to his excellent greatness.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise him in the sound of the trumpet:\r\n            * praise him upon the lute and harp.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise him in the timbrels and dances:\r\n            * praise him upon the strings and pipe.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Praise him upon the well-tuned cymbals:\r\n            * praise him upon the loud cymbals.<br/>\r\n            <small> &nbsp; <br/></small>\r\n            Let every thing that hath breath\r\n            * praise the LORD.\r\n        </b>\r\n    </p>\r\n</blockquote>\r\n\r\n', 'Evening', '0000-00-00 00:00:00');
INSERT INTO `psalm` (`id`, `description`, `inserted_at`, `updated_at`) VALUES
(1, '<div class=\"text-center\"><h4><i>Beatus vir qui non abiit.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nBLESSED is the man that hath not walked in the \r\ncounsel of the ungodly, nor stood in the way of sinners, \r\n* and hath not sat in the seat of the scornful.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut his delight is in the law of the LORD; \r\n* and in his law will he exercise himself day and night.<br/> \r\n<small> &nbsp; <br/></small>\r\nAnd he shall be like a tree planted by the water-side, \r\n* that will bring forth his fruit in due season.<br/> \r\n<small> &nbsp; <br/></small>\r\nHis leaf also shall not wither; \r\n* and look, whatsoever he doeth, it shall prosper.<br/> \r\n<small> &nbsp; <br/></small>\r\nAs for the ungodly, it is not so with them; \r\n* but they are like the chaff, which the wind scattereth \r\naway from the face of the earth.<br/> \r\n<small> &nbsp; <br/></small>\r\nTherefore the ungodly shall not be able to stand \r\nin the judgment, \r\n* neither the sinners in the congregation of the righteous.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut the LORD knoweth the way of the righteous; \r\n* and the way of the ungodly shall perish.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(2, '<div class=\"text-center\"><h4><i>Quare fremuerent gentes?</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nWHY do the heathen so furiously rage together? \r\n* and why do the people imagine a vain thing?<br/> \r\n<small> &nbsp; <br/></small>\r\nThe kings of the earth stand up, and the rulers \r\ntake counsel together \r\n* against the LORD, and against his Anointed:<br/> \r\n<small> &nbsp; <br/></small>\r\nLet us break their bonds asunder, \r\n* and cast away their cords from us.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe that dwelleth in heaven shall laugh them to scorn: \r\n* the Lord shall have them in derision.<br/> \r\n<small> &nbsp; <br/></small>\r\nThen shall he speak unto them in his wrath, \r\n* and vex them in his sore displeasure:<br/>\r\n<small> &nbsp; <br/></small>\r\nYet have I set my King \r\n* upon my holy hill of Sion.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will rehearse the decree; \r\n* the LORD hath said unto me, Thou art my Son, \r\nthis day have I begotten thee.<br/> \r\n<small> &nbsp; <br/></small>\r\nDesire of me, and I shall give thee the nations \r\nfor thine inheritance, \r\n* and the utmost parts of the earth for thy possession.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou shalt bruise them with a rod of iron, \r\n* and break them in pieces like a potter\'s vessel.<br/> \r\n<small> &nbsp; <br/></small>\r\nBe wise now therefore, O ye kings; \r\n* be instructed, ye that are judges of the earth.<br/> \r\n<small> &nbsp; <br/></small>\r\nServe the LORD in fear, \r\n* and rejoice unto him with reverence.<br/> \r\n<small> &nbsp; <br/></small>\r\nKiss the Son, lest he be angry, and so ye perish from \r\nthe right way, if his wrath be kindled, yea but a little.  \r\n* Blessed are all they that put their trust in him.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(3, '<div class=\"text-center\"><h4><i>Domine, quid multiplicati?</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nLORD, how are they increased that trouble me! \r\n* many are they that rise against me.<br/> \r\n<small> &nbsp; <br/></small>\r\nMany one there be that say of my soul, \r\n* There is no help for him in his God.<br/> \r\n<small> &nbsp; <br/></small>\r\n But thou, O LORD, art my defender; \r\n* thou art my worship, and the lifter up of my head.<br/> \r\n<small> &nbsp; <br/></small>\r\nI did call upon the LORD with my voice, \r\n* and he heard me out of his holy hill.<br/> \r\n<small> &nbsp; <br/></small>\r\nI laid me down and slept, and rose up again; \r\n* for the LORD sustained me.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will not be afraid for ten thousands of the people; \r\n* that have set themselves against me round about.<br/>\r\n<small> &nbsp; <br/></small>\r\nUp, LORD, and help me, O my God, \r\n* for thou smitest all mine enemies upon the cheek-bone; \r\nthou hast broken the teeth of the ungodly.<br/> \r\n<small> &nbsp; <br/></small>\r\nSalvation belongeth unto the LORD; \r\n* and thy blessing is upon thy people.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(4, '<div class=\"text-center\"><h4><i>Cum invocarem.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nHEAR me when I call, O God of my righteousness: \r\n* thou hast set me at liberty when I was in trouble; have \r\nmercy upon me, and hearken unto my prayer.<br/> \r\n<small> &nbsp; <br/></small>\r\nO ye sons of men, how long will ye blaspheme mine honour, \r\n* and have such pleasure in vanity, and seek after falsehood?<br/> \r\n<small> &nbsp; <br/></small>\r\nKnow this also, that the LORD hath chosen to himself the \r\nman that is godly; \r\n* when I call upon the LORD he will hear me.<br/> \r\n<small> &nbsp; <br/></small>\r\nStand in awe, and sin not; \r\n* commune with your own heart, and in your chamber, \r\nand be still.<br/> \r\n<small> &nbsp; <br/></small>\r\nOffer the sacrifice of righteousness, \r\n* and put your trust in the LORD.<br/> \r\n<small> &nbsp; <br/></small>\r\nThere be many that say, \r\n* Who will show us any good? <br/> \r\n<small> &nbsp; <br/></small>\r\nLORD, lift thou up \r\n* the light of thy countenance upon us.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou hast put gladness in my heart; \r\n* yea, more than when their corn and wine and oil increase.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will lay me down in peace, and take my rest; \r\n* for it is thou, LORD, only, that makest me dwell in safety.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(5, '<div class=\"text-center\"><h4><i>Verba mea auribus.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nPONDER my words, O LORD,  \r\n*  consider my meditation.<br/> \r\n<small> &nbsp; <br/></small>\r\nO hearken thou unto the voice of my calling, my King \r\n and my God:  \r\n*  for unto thee will I make my prayer.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy voice shalt thou hear betimes, O LORD;  \r\n*  early in the morning will I direct my prayer unto thee, \r\nand will look up.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor thou art the God that hast no pleasure in wickedness;  \r\n*  neither shall any evil dwell with thee.<br/> \r\n<small> &nbsp; <br/></small>\r\nSuch as be foolish shall not stand in thy sight;  \r\n*  for thou hatest all them that work iniquity.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou shalt destroy them that speak lies:  \r\n*  the LORD will abhor both the blood-thirsty and \r\ndeceitful man.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut as for me, in the multitude of thy mercy I will come \r\ninto thine house;  \r\n*  and in thy fear will I worship toward thy holy temple.<br/> \r\n<small> &nbsp; <br/></small>\r\nLead me, O LORD, in thy righteousness, because of \r\nmine enemies;  \r\n*  make thy way plain before my face.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor there is no faithfulness in their mouth;  \r\n*  their inward parts are very wickedness.<br/> \r\n<small> &nbsp; <br/></small>\r\nTheir throat is an open sepulchre;  \r\n*  they flatter with their tongue.<br/> \r\n<small> &nbsp; <br/></small>\r\nDestroy thou them, O God; let them perish through \r\ntheir own imaginations;  \r\n*  cast them out in the multitude of their ungodliness; \r\nfor they have rebelled against thee.<br/> \r\n<small> &nbsp; <br/></small>\r\nAnd let all them that put their trust in thee rejoice:  \r\n*  they shall ever be giving of thanks, because thou \r\ndefendest them; they that love thy Name shall \r\nbe joyful in thee; <br/>\r\n<small> &nbsp; <br/></small>\r\nFor thou, LORD, wilt give thy blessing unto \r\nthe righteous,  \r\n*  and with thy favourable kindness wilt thou defend \r\nhim as with a shield.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(6, '<div class=\"text-center\"><h4><i>Domine, ne in furore.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nO LORD, rebuke me not in thine indignation,  \r\n*  neither chasten me in thy displeasure.<br/> \r\n<small> &nbsp; <br/></small>\r\nHave mercy upon me, O LORD, for I am weak;  \r\n*  O LORD, heal me, for my bones are vexed.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy soul also is sore troubled:  \r\n*  but, LORD, how long wilt thou punish me? <br/>\r\n<small> &nbsp; <br/></small>\r\nTurn thee, O LORD, and deliver my soul;  \r\n*  O save me, for thy mercy\'s sake.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor in death no man remembereth thee;  \r\n*  and who will give thee thanks in the pit? <br/> \r\n<small> &nbsp; <br/></small>\r\n I am weary of my groaning;  \r\n*  every night wash I my bed, and water my couch \r\nwith my tears.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy beauty is gone for very trouble,  \r\n*  and worn away because of all mine enemies.<br/> \r\n<small> &nbsp; <br/></small>\r\nAway from me, all ye that work iniquity;  \r\n*  for the LORD hath heard the voice of my weeping.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD hath heard my petition;  \r\n*  the LORD will receive my prayer.<br/> \r\n<small> &nbsp; <br/></small>\r\nAll mine enemies shall be confounded, and sore vexed;  \r\n*  they shall be turned back, and put to shame suddenly.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(7, '<div class=\"text-center\"><h4><i>Domine, Deus meus.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nO LORD my God, in thee have I put my trust: \r\n* save me from all them that persecute me, and deliver me;<br/>\r\n<small> &nbsp; <br/></small>\r\nLest he devour my soul like a lion, and tear it in pieces, \r\n* while there is none to help.<br/> \r\n<small> &nbsp; <br/></small>\r\nO LORD my God, if I have done any such thing; \r\n* or if there be any wickedness in my hands; <br/> \r\n<small> &nbsp; <br/></small>\r\nIf I have rewarded evil unto him that dealt \r\nfriendly with me; \r\n*  (yea, I have delivered him that without any cause \r\nis mine enemy;) <br/>\r\n<small> &nbsp; <br/></small>\r\nThen let mine enemy persecute my soul, and take me; \r\n*  yea, let him tread my life down upon the earth, \r\nand lay mine honour in the dust.<br/> \r\n<small> &nbsp; <br/></small>\r\nStand up, O LORD, in thy wrath, and lift up thyself, \r\nbecause of the indignation of mine enemies;  \r\n*  arise up for me in the judgment that thou hast \r\ncommanded.<br/> \r\n<small> &nbsp; <br/></small>\r\nAnd so shall the congregation of the peoples \r\ncome about thee: \r\n*  for their sakes therefore lift up thyself again.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD shall judge the peoples: give sentence \r\nwith me, O LORD, \r\n* according to my righteousness, and according to \r\nthe innocency that is in me.<br/> \r\n<small> &nbsp; <br/></small>\r\nO let the wickedness of the ungodly come to an end; \r\n* but guide thou the just.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor the righteous God \r\n* trieth the very hearts and reins.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy help cometh of God, \r\n* who preserveth them that are true of heart.<br/> \r\n<small> &nbsp; <br/></small>\r\nGod is a righteous Judge, strong, and patient; \r\n* and God is provoked every day.<br/> \r\n<small> &nbsp; <br/></small>\r\nIf a man will not turn, he will whet his sword; \r\n* he hath bent his bow, and made it ready.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe hath prepared for him the instruments of death; \r\n* he ordaineth his arrows against the persecutors.<br/> \r\n<small> &nbsp; <br/></small>\r\nBehold, the ungodly travaileth with iniquity; \r\n* he hath conceived mischief, and brought forth falsehood.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe hath graven and digged up a pit, \r\n* and is fallen himself into the destruction that he \r\nmade for other.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor his travail shall come upon his own head, \r\n* and his wickedness shall fall on his own pate.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will give thanks unto the LORD, according \r\nto his righteousness; \r\n* and I will praise the Name of the LORD Most High.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(8, '<div class=\"text-center\"><h4><i>Domine, Dominus noster.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nO LORD our Governor, how excellent is thy Name \r\nin all the world; \r\n* thou that hast set thy glory above the heavens!<br/>\r\n<small> &nbsp; <br/></small>\r\nOut of the mouth of very babes and sucklings hast \r\nthou ordained strength, because of thine enemies, \r\n* that thou mightest still the enemy and the avenger.<br/> \r\n<small> &nbsp; <br/></small>\r\nWhen I consider thy heavens, even the work \r\nof thy fingers; \r\n* the moon and the stars which thou hast ordained;<br/> \r\n<small> &nbsp; <br/></small>\r\nWhat is man, that thou art mindful of him? \r\n* and the son of man, that thou visitest him?<br/>\r\n<small> &nbsp; <br/></small>\r\nThou madest him lower than the angels, \r\n* to crown him with glory and worship.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou makest him to have dominion of the \r\nworks of thy hands; \r\n* and thou hast put all things in subjection under his feet:<br/> \r\n<small> &nbsp; <br/></small>\r\nAll sheep and oxen; \r\n* yea, and the beasts of the field;<br/> \r\n<small> &nbsp; <br/></small>\r\nThe fowls of the air, and the fishes of the sea; \r\n* and whatsoever walketh through the paths of the seas.<br/> \r\n<small> &nbsp; <br/></small>\r\nO LORD our Governor, \r\n* how excellent is thy Name in all the world!\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(9, '<div class=\"text-center\"><h4><i>Confitebor tibi.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nI WILL give thanks unto thee, O LORD, with my whole heart; \r\n*  I will speak of all thy marvellous works.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will be glad and rejoice in thee; \r\n*  yea, my songs will I make of thy Name, O thou Most Highest.<br/> \r\n<small> &nbsp; <br/></small>\r\nWhile mine enemies are driven back, \r\n*  they shall fall and perish at thy presence.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor thou hast maintained my right and my cause; \r\n*  thou art set in the throne that judgest right.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou hast rebuked the heathen, and destroyed the ungodly; \r\n*  thou hast put out their name for ever and ever.<br/> \r\n<small> &nbsp; <br/></small>\r\nO thou enemy, thy destructions are come to a perpetual end; \r\n*  even as the cities which thou hast destroyed, \r\nwhose memorial is perished with them.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut the LORD shall endure for ever; \r\n*  he hath also prepared his seat for judgment.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor he shall judge the world in righteousness, \r\n*  and minister true judgment unto the people.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD also will be a defence for the oppressed, \r\n*  even a refuge in due time of trouble.<br/> \r\n<small> &nbsp; <br/></small>\r\nAnd they that know thy Name will put their trust in thee; \r\n*  for thou, LORD, hast never failed them that seek thee.<br/>\r\n<small> &nbsp; <br/></small>\r\nO praise the LORD which dwelleth in Sion; \r\n*  show the people of his doings.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor when he maketh inquisition for blood, \r\nhe remembereth them, \r\n*  and forgetteth not the complaint of the poor.<br/> \r\n<small> &nbsp; <br/></small>\r\nHave mercy upon me, O LORD; consider the trouble \r\nwhich I suffer of them that hate me, \r\n*  thou that liftest me up from the gates of death; <br/>\r\n<small> &nbsp; <br/></small>\r\nThat I may show all thy praises within the gates of \r\nthe daughter of Sion: \r\n*  I will rejoice in thy salvation.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe heathen are sunk down in the pit that they made; \r\n*  in the same net which they hid privily is their foot taken.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD is known to execute judgment; \r\n*  the ungodly is trapped in the work of his own hands.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe wicked shall be turned to destruction, \r\n*  and all the people that forget God.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor the poor shall not alway be forgotten; \r\n*  the patient abiding of the meek shall not perish for ever.<br/> \r\n<small> &nbsp; <br/></small>\r\nUp, LORD, and let not man have the upper hand; \r\n*  let the heathen be judged in thy sight.<br/> \r\n<small> &nbsp; <br/></small>\r\nPut them in fear, O LORD, \r\n*  that the heathen may know themselves to be but men.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(10, '<div class=\"text-center\"><h4><i>Ut quid, Domine?</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nWHY standest thou so far off, O LORD,  \r\n*  and hidest thy face in the needful time of trouble? <br/> \r\n <br/>\r\nThe ungodly, for his own lust, doth persecute the poor:  \r\n*  let them be taken in the crafty wiliness that they \r\nhave imagined.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor the ungodly hath made boast of his own \r\nheart\'s desire,  \r\n*  and speaketh good of the covetous, whom \r\nthe LORD abhorreth.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe ungodly is so proud, that he careth not for God,  \r\n*  neither is God in all his thoughts.<br/> \r\n<small> &nbsp; <br/></small>\r\nHis ways are alway grievous;  \r\n*  thy judgments are far above out of his sight, and \r\ntherefore defieth he all his enemies.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor he hath said in his heart, Tush, I shall never \r\nbe cast down,  \r\n*  there shall no harm happen unto me.<br/> \r\n<small> &nbsp; <br/></small>\r\nHis mouth is full of cursing, deceit, and fraud;  \r\n*  under his tongue is ungodliness and vanity.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe sitteth lurking in the thievish corners of the streets,  \r\n*  and privily in his lurking dens doth he murder the \r\ninnocent; his eyes are set against the poor.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor he lieth waiting secretly; even as a lion lurketh \r\nhe in his den,  \r\n*  that he may ravish the poor.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe doth ravish the poor,  \r\n*  when he getteth him into his net.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe falleth down, and humbleth himself,  \r\n*  that the congregation of the poor may fall into \r\nthe hands of his captains.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe hath said in his heart, Tush, God hath forgotten;  \r\n*  he hideth away his face, and he will never see it.<br/> \r\n<small> &nbsp; <br/></small>\r\nArise, O LORD God, and lift up thine hand;  \r\n*  forget not the poor.<br/> \r\n<small> &nbsp; <br/></small>\r\nWherefore should the wicked blaspheme God,  \r\n*  while he doth say in his heart, Tush, thou God carest \r\nnot for it? <br/>\r\n<small> &nbsp; <br/></small>\r\nSurely thou hast seen it;  \r\n*  for thou beholdest ungodliness and wrong, that thou \r\nmayest take the matter into thy hand.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe poor committeth himself unto thee;  \r\n*  for thou art the helper of the friendless.<br/> \r\n<small> &nbsp; <br/></small>\r\nBreak thou the power of the ungodly and malicious;  \r\n*  search out his ungodliness, until thou find none.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD is King for ever and ever,  \r\n*  and the heathen are perished out of the land.<br/> \r\n<small> &nbsp; <br/></small>\r\nLORD, thou hast heard the desire of the poor;  \r\n*  thou preparest their heart, and thine ear hearkeneth; <br/>\r\n<small> &nbsp; <br/></small>\r\nTo help the fatherless and poor unto their right,  \r\n*  that the man of the earth be no more exalted \r\nagainst them.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(11, '<div class=\"text-center\"><h4><i>In Domino confido.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nIN the LORD put I my trust;  \r\n*  how say ye then to my soul, that she should flee \r\nas a bird unto the hill? <br/>\r\n<small> &nbsp; <br/></small>\r\nFor lo, the ungodly bend their bow, and make ready \r\ntheir arrows within the quiver,  \r\n*  that they may privily shoot at them which are \r\ntrue of heart.<br/> \r\n<small> &nbsp; <br/></small>\r\nIf the foundations be destroyed,  \r\n*  what can the righteous do? <br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD is in his holy temple;  \r\n*  the LORD\'S seat is in heaven.<br/> \r\n<small> &nbsp; <br/></small>\r\nHis eyes consider the poor,  \r\n*  and his eyelids try the children of men.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD approveth the righteous:  \r\n*  but the ungodly, and him that delighteth in wickedness, \r\ndoth his soul abhor.<br/> \r\n<small> &nbsp; <br/></small>\r\nUpon the ungodly he shall rain snares, fire and \r\nbrimstone, storm and tempest:  \r\n*  this shall be their portion to drink.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor the righteous LORD loveth righteousness;  \r\n*  his countenance will behold the thing that is just.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(12, '<div class=\"text-center\"><h4><i>Salvum me fac.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nHELP me, LORD, for there is not one godly man left;  \r\n*  for the faithful are minished from among the \r\nchildren of men.<br/> \r\n<small> &nbsp; <br/></small>\r\nThey talk of vanity every one with his neighbour;  \r\n*  they do but flatter with their lips, and dissemble in \r\ntheir double heart.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD shall root out all deceitful lips,  \r\n*  and the tongue that speaketh proud things;<br/> \r\n<small> &nbsp; <br/></small>\r\nWhich have said, With our tongue will we prevail;  \r\n*  we are they that ought to speak; who is lord over us?<br/> \r\n <br/>\r\nNow, for the comfortless troubles\' sake of the needy,  \r\n*  and because of the deep sighing of the poor,<br/> \r\n<small> &nbsp; <br/></small>\r\nI will up, saith the LORD;  \r\n*  and will help every one from him that swelleth against \r\nhim, and will set him at rest.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe words of the LORD are pure words;  \r\n*  even as the silver which from the earth is tried, \r\nand purified seven times in the fire.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou shalt keep them, O LORD;  \r\n*  thou shalt preserve them from this generation for ever.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe ungodly walk on every side:  \r\n*  when they are exalted, the children of men are \r\nput to rebuke.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(13, '<div class=\"text-center\"><h4><i>Usquequo, Domine?</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nHOW long wilt thou forget me, O LORD; for ever? \r\n* how long wilt thou hide thy face from me?<br/> \r\n<small> &nbsp; <br/></small>\r\nHow long shall I seek counsel in my soul, and be so \r\nvexed in my heart? \r\n* how long shall mine enemy triumph over me? <br/> \r\n<small> &nbsp; <br/></small>\r\nConsider, and hear me, O LORD my God; \r\n* lighten mine eyes, that I sleep not in death; <br/>\r\n<small> &nbsp; <br/></small>\r\nLest mine enemy say, I have prevailed against him: \r\n* for if I be cast down, they that trouble me will \r\nrejoice at it.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut my trust is in thy mercy, \r\n* and my heart is joyful in thy salvation.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will sing of the LORD, because he hath dealt so \r\nlovingly with me; \r\n* yea, I will praise the Name of the Lord Most Highest.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(14, '<div class=\"text-center\"><h4><i>Dixit insipiens.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nTHE fool hath said in his heart, \r\n* There is no God.<br/> \r\n<small> &nbsp; <br/></small>\r\nThey are corrupt, and become abominable in \r\ntheir doings; \r\n* there is none that doeth good, no not one.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD looked down from heaven upon the \r\nchildren of men, \r\n* to see if there were any that would understand, \r\nand seek after God.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut they are all gone out of the way, they are altogether \r\nbecome abominable; \r\n* there is none that doeth good, no not one.<br/> \r\n<small> &nbsp; <br/></small>\r\nHave they no knowledge, that they are all such workers \r\nof mischief, \r\n* eating up my people as it were bread, and call not \r\nupon the LORD?<br/> \r\n<small> &nbsp; <br/></small>\r\nThere were they brought in great fear, even where no \r\nfear was; \r\n* for God is in the generation of the righteous.<br/> \r\n<small> &nbsp; <br/></small>\r\nAs for you, ye have made a mock at the counsel \r\nof the poor; \r\n* because he putteth his trust in the LORD.<br/> \r\n<small> &nbsp; <br/></small>\r\nWho shall give salvation unto Israel out of Sion? \r\n* When the LORD turneth the captivity of his people, then \r\nshall Jacob rejoice, and Israel shall be glad.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(15, '<div class=\"text-center\"><h4><i>Domine, quis habitabit?</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nLORD, who shall dwell in thy tabernacle? \r\n* or who shall rest upon thy holy hill?<br/> \r\n<small> &nbsp; <br/></small>\r\nEven he that leadeth an uncorrupt life, \r\n* and doeth the thing which is right, and speaketh the \r\ntruth from his heart.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe that hath used no deceit in his tongue, \r\nnor done evil to his neighbour, \r\n* and hath not slandered his neighbour.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe that setteth not by himself, but is \r\nlowly in his own eyes, \r\n* and maketh much of them that fear the LORD.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe that sweareth unto his neighbour, \r\nand disappointeth him not, \r\n* though it were to his own hindrance.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe that hath not given his money upon usury, \r\n* nor taken reward against the innocent.<br/> \r\n<small> &nbsp; <br/></small>\r\nWhoso doeth these things \r\n* shall never fall.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(16, '<div class=\"text-center\"><h4><i>Conserva me, Domine.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nPRESERVE me, O God;\r\n* for in thee have I put my trust.<br/>\r\n<small> &nbsp; <br/></small>\r\nO my soul, thou hast said unto the LORD,\r\n* Thou art my God; I have no good like unto thee.<br/>\r\n<small> &nbsp; <br/></small>\r\nAll my delight is upon the saints that are in the earth,\r\n* and upon such as excel in virtue.<br/>\r\n<small> &nbsp; <br/></small>\r\nBut they that run after another god\r\n* shall have great trouble.<br/>\r\n<small> &nbsp; <br/></small>\r\nTheir drink-offerings of blood will I not offer,\r\n* neither make mention of their names within my lips.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe LORD himself is the portion of mine inheritance, and of my cup;\r\n* thou shalt maintain my lot.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe lot is fallen unto me in a fair ground;\r\n* yea, I have a goodly heritage.<br/>\r\n<small> &nbsp; <br/></small>\r\nI will thank the LORD for giving me warning;\r\n* my reins also chasten me in the night season.<br/>\r\n<small> &nbsp; <br/></small>\r\nI have set the LORD alway before me;\r\n* for he is on my right hand, therefore I shall not fall.<br/>\r\n<small> &nbsp; <br/></small>\r\n Wherefore my heart is glad, and my glory rejoiceth:\r\n* my flesh also shall rest in hope.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor why? thou shalt not leave my soul in hell;\r\n* neither shalt thou suffer thy Holy One to see corruption.<br/>\r\n<small> &nbsp; <br/></small>\r\nThou shalt show me the path of life: in thy presence is the fulness of joy,\r\n* and at thy right hand there is pleasure for evermore.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(17, '<div class=\"text-center\"><h4><i>Exaudi, Domine.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nHEAR the right, O LORD, consider my complaint, \r\n* and hearken unto my prayer, that goeth not out \r\nof feigned lips.<br/> \r\n<small> &nbsp; <br/></small>\r\nLet my sentence come forth from thy presence; \r\n* and let thine eyes look upon the thing that is equal.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou hast proved and visited mine heart in the \r\nnight season; thou hast tried me, and shalt find \r\nno wickedness in me; \r\n* for I am utterly purposed that my mouth shall not offend.<br/> \r\n<small> &nbsp; <br/></small>\r\nAs for the works of men, \r\n* by the word of thy lips I have kept me from the ways \r\nof the destroyer.<br/> \r\n<small> &nbsp; <br/></small>\r\nO hold thou up my goings in thy paths, \r\n* that my footsteps slip not.<br/> \r\n<small> &nbsp; <br/></small>\r\nI have called upon thee, O God, for thou shalt hear me: \r\n* incline thine ear to me, and hearken unto my words.<br/> \r\n<small> &nbsp; <br/></small>\r\nShow thy marvellous loving-kindness, thou that art the \r\nSaviour of them which put their trust in thee, \r\n* from such as resist thy right hand.<br/> \r\n<small> &nbsp; <br/></small>\r\nKeep me as the apple of an eye; \r\n* hide me under the shadow of thy wings,<br/>\r\n<small> &nbsp; <br/></small>\r\nFrom the ungodly, that trouble me; \r\n* mine enemies compass me round about, to take \r\naway my soul.<br/> \r\n<small> &nbsp; <br/></small>\r\nThey are inclosed in their own fat, \r\n* and their mouth speaketh proud things.<br/> \r\n<small> &nbsp; <br/></small>\r\nThey lie waiting in our way on every side, \r\n* watching to cast us down to the ground; <br/>\r\n<small> &nbsp; <br/></small>\r\nLike as a lion that is greedy of his prey, \r\n* and as it were a lion\'s whelp lurking in secret places.<br/> \r\n<small> &nbsp; <br/></small>\r\nUp, LORD, disappoint him, and cast him down; \r\n* deliver my soul from the ungodly, by thine own sword;<br/> \r\n<small> &nbsp; <br/></small>\r\nYea, by thy hand, O LORD; from the men of \r\nthe evil world; \r\n* which have their portion in this life, whose bellies \r\nthou fillest with thy hid treasure.<br/> \r\n<small> &nbsp; <br/></small>\r\nThey have children at their desire, \r\n* and leave the rest of their substance for their babes.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut as for me, I shall behold thy presence in  righteousness; \r\n* and when I awake up after thy likeness, I shall be satisfied.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(18, '<div class=\"text-center\"><h4><i>Diligam te, Domine.</i></h4></div><hr/><blockquote>\r\n<b>\r\nI WILL love thee, O LORD, my strength.\r\n* The LORD is my stony rock, and my defence;<br/>\r\n<small> &nbsp; <br/></small>\r\nMy Saviour, my God, and my might, in whom \r\nI will trust; \r\n* my buckler, the horn also of my salvation, \r\nand my refuge.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will call upon the LORD, which is worthy \r\nto be praised; \r\n* so shall I be safe from mine enemies.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe sorrows of death compassed me, \r\n* and the overflowings of ungodliness made me afraid.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe pains of hell came about me; \r\n* the snares of death overtook me.<br/> \r\n<small> &nbsp; <br/></small>\r\nIn my trouble I called upon the LORD, \r\n* and complained unto my God:<br/>\r\n<small> &nbsp; <br/></small>\r\nSo he heard my voice out of his holy temple, \r\n* and my complaint came before him; it entered \r\neven into his ears.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe earth trembled and quaked, \r\n* the very foundations also of the hills shook, and \r\nwere removed, because he was wroth.<br/> \r\n<small> &nbsp; <br/></small>\r\nThere went a smoke out in his presence, \r\n* and a consuming fire out of his mouth, so that \r\ncoals were kindled at it.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe bowed the heavens also, and came down, \r\n* and it was dark under his feet.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe rode upon the Cherubim, and did fly; \r\n* he came flying upon the wings of the wind.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe made darkness his secret place, \r\n* his pavilion round about him with dark water, \r\nand thick clouds to cover him.<br/> \r\n<small> &nbsp; <br/></small>\r\nAt the brightness of his presence his clouds removed; \r\n* hailstones and coals of fire.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD also thundered out of heaven, and the \r\nHighest gave his thunder; \r\n* hailstones and coals of fire.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe sent out his arrows, and scattered them; \r\n* he cast forth lightnings, and destroyed them.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe springs of waters were seen, and the foundations of \r\nthe round world were discovered, \r\n* at thy chiding, O LORD, at the blasting of the breath \r\nof thy displeasure.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe sent down from on high to fetch me, \r\n* and took me out of many waters.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe delivered me from my strongest enemy, and from \r\nthem which hate me; \r\n* for they were too mighty for me<br/>\r\n<small> &nbsp; <br/></small>\r\nThey came upon me in the day of my trouble; \r\n* but the LORD was my upholder.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe brought me forth also into a place of liberty; \r\n* he brought me forth, even because he had a \r\nfavour unto me.<br/> \r\n<small> &nbsp; <br/></small>\r\n</b>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(19, '<div class=\"text-center\"><h4><i>Caeli enarrant.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nTHE heavens declare the glory of God; \r\n* and the firmament showeth his handy-work.<br/> \r\n<small> &nbsp; <br/></small>\r\nOne day telleth another; \r\n* and one night certifieth another.<br/> \r\n<small> &nbsp; <br/></small>\r\nThere is neither speech nor language; \r\n* but their voices are heard among them.<br/> \r\n<small> &nbsp; <br/></small>\r\nTheir sound is gone out into all lands; \r\n* and their words into the ends of the world.<br/> \r\n<small> &nbsp; <br/></small>\r\nIn them hath he set a tabernacle for the sun; \r\n* which cometh forth as a bridegroom out of his chamber, \r\nand  rejoiceth as a giant to run his course.<br/> \r\n<small> &nbsp; <br/></small>\r\nIt goeth forth from the uttermost part of the heaven, and \r\nrunneth about unto the end of it again; \r\n* and there is nothing hid from the heat thereof.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe law of the LORD is an undefiled law, converting \r\nthe soul; \r\n* the testimony of the LORD is sure, and giveth wisdom \r\nunto the simple.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe statutes of the LORD are right, and rejoice the heart; \r\n* the commandment of the LORD is pure, and giveth light \r\nunto the eyes.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe fear of the LORD is clean, and endureth forever; \r\n* the judgments of the LORD are true, and righteous \r\naltogether.<br/> \r\n<small> &nbsp; <br/></small>\r\nMore to be desired are they than gold, yea, than much \r\nfine gold; \r\n* sweeter also than honey, and the honeycomb.<br/> \r\n<small> &nbsp; <br/></small>\r\nMoreover, by them is thy servant taught; \r\n* and in keeping of them there is great reward.<br/> \r\n<small> &nbsp; <br/></small>\r\nWho can tell how oft he offendeth? \r\n* O cleanse thou me from my secret faults.<br/> \r\n<small> &nbsp; <br/></small>\r\nKeep thy servant also from presumptuous sins, lest \r\nthey get the dominion over me; \r\n* so shall I be undefiled, and innocent from the \r\ngreat offence.<br/> \r\n<small> &nbsp; <br/></small>\r\nLet the words of my mouth, and the meditation of my \r\nheart, be alway acceptable in thy sight, \r\n* O LORD, my strength and my redeemer.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(20, '<div class=\"text-center\"><h4><i>Exaudiat te Dominus.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nTHE LORD hear thee in the day of trouble;\r\n* the Name of the God of Jacob defend thee:<br/>\r\n<small> &nbsp; <br/></small>\r\nSend thee help from the sanctuary, \r\n* and strengthen thee out of Sion:<br/> \r\n<small> &nbsp; <br/></small>\r\nRemember all thy offerings, \r\n* and accept thy burnt sacrifice:<br/> \r\n<small> &nbsp; <br/></small>\r\nGrant thee thy heart\'s desire, \r\n* and fulfil all thy mind.<br/> \r\n<small> &nbsp; <br/></small>\r\nWe will rejoice in thy salvation, and triumph in the \r\nName of the Lord our God: \r\n* the LORD perform all thy petitions.<br/> \r\n<small> &nbsp; <br/></small>\r\nNow know I that the LORD helpeth his anointed, and \r\nwill hear him from his holy heaven, \r\n* even with the wholesome strength of his right hand.<br/> \r\n<small> &nbsp; <br/></small>\r\nSome put their trust in chariots, and some in horses; \r\n* but we will remember the Name of the LORD our God.<br/> \r\n<small> &nbsp; <br/></small>\r\nThey are brought down and fallen; \r\n* but we are risen and stand upright.<br/> \r\n<small> &nbsp; <br/></small>\r\nSave, LORD; and hear us, O King of heaven, \r\n* when we call upon thee.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(21, '<div class=\"text-center\"><h4><i>Domine, in virtute tua.</i></h4></div><hr/><blockquote>\r\n<b>\r\nTHE King shall rejoice in thy strength, O LORD; \r\n* exceeding glad shall he be of thy salvation.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou hast given him his heart\'s desire, \r\n* and hast not denied him the request of his lips.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor thou shalt meet him with the blessings of goodness, \r\n* and shalt set a crown of pure gold upon his head.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe asked life of thee; and thou gavest him a long life, \r\n* even for ever and ever.<br/> \r\n<small> &nbsp; <br/></small>\r\nHis honour is great in thy salvation; \r\n* glory and great worship shalt thou lay upon him.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor thou shalt give him everlasting felicity, \r\n* and make him glad with the joy of thy countenance.<br/> \r\n<small> &nbsp; <br/></small>\r\n</b>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(22, '<div class=\"text-center\"><h4><i>Deus, deus meus.</i></h4></div><hr/><blockquote>\r\n<b>\r\nMY God, my God, look upon me; why hast thou forsaken me? * and art so far from my health, and from  \r\nthe words of my complaint?<br/>\r\n<small> &nbsp; <br/></small>\r\nO my God, I cry in the day-time, but thou hearest not; * and in the night season also I take no rest.<br/>\r\n<small> &nbsp; <br/></small>\r\nAnd thou continuest holy, * O thou Worship of Israel.<br/>\r\n<small> &nbsp; <br/></small>\r\nOur fathers hoped in thee; * they trusted in thee, and thou didst deliver them.<br/>\r\n<small> &nbsp; <br/></small>\r\nThey called upon thee, and were holpen; * they put their trust in thee, and were not confounded.<br/>\r\n<small> &nbsp; <br/></small>\r\nBut as for me, I am a worm, and no man; * a very scorn of men, and the outcast of the people.<br/>\r\n<small> &nbsp; <br/></small>\r\nAll they that see me laugh me to scorn; * they shoot out their lips, and shake their heads, saying,<br/>\r\n<small> &nbsp; <br/></small>\r\nHe trusted in the LORD, that he would deliver him; * let him deliver him, if he will have him.<br/>\r\n<small> &nbsp; <br/></small>\r\nBut thou art he that took me out of my mother\'s womb; * thou wast my hope, when I hanged yet upon my \r\nmother\'s breasts.<br/>\r\n<small> &nbsp; <br/></small>\r\nI have been left unto thee ever since I was born; * thou art my God even from my mother\'s womb.<br/>\r\n<small> &nbsp; <br/></small>\r\nO go not from me; for trouble is hard at hand, * and there is none to help me.<br/>\r\n<small> &nbsp; <br/></small>\r\nMany oxen are come about me; * fat bulls of Bashan close me in on every side.<br/>\r\n<small> &nbsp; <br/></small>\r\nThey gape upon me with their mouths, * as it were a ramping and a roaring lion.<br/>\r\n<small> &nbsp; <br/></small>\r\nI am poured out like water, and all my bones are out of joint; * my heart also in the midst of my body is \r\neven like melting wax.<br/>\r\n<small> &nbsp; <br/></small>\r\nMy strength is dried up like a potsherd, and my tongue cleaveth to my gums, * and thou bringest me into \r\nthe dust of death.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor many dogs are come about me, * and the council of the wicked layeth siege against me.<br/>\r\n<small> &nbsp; <br/></small>\r\nThey pierced my hands and my feet: I may tell all my bones: * they stand staring and looking upon me.<br/>\r\n<small> &nbsp; <br/></small>\r\nThey part my garments among them, * and cast lots upon my vesture.<br/>\r\n<small> &nbsp; <br/></small>\r\nBut be not thou far from me, O LORD; * thou art my succour, haste thee to help me.<br/>\r\n<small> &nbsp; <br/></small>\r\n</b>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(23, '<div class=\"text-center\"><h4><i>Dominus regit me.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nTHE LORD is my shepherd; \r\n* therefore can I lack nothing.<br/>\r\n<small> &nbsp; <br/></small>\r\nHe shall feed me in a green pasture, \r\n* and lead me forth beside the waters of comfort.<br/>\r\n<small> &nbsp; <br/></small>\r\nHe shall convert my soul, \r\n* and bring me forth in the paths of righteousness for \r\nhis Name\'s sake.<br/>\r\n<small> &nbsp; <br/></small>\r\nYea, though I walk through the valley of the shadow of \r\ndeath, I will fear no evil; \r\n* for thou art with me; thy rod and thy staff comfort me.<br/>\r\n<small> &nbsp; <br/></small>\r\nThou shalt prepare a table before me in the presence of \r\nthem that trouble me; \r\n* thou hast anointed my head with oil, and my cup shall \r\nbe full.<br/>\r\n<small> &nbsp; <br/></small>\r\nSurely thy loving-kindness and mercy shall follow \r\nme all the days of my life; \r\n* and I will dwell in the house of the LORD for ever.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(24, '<div class=\"text-center\"><h4><i>Domini est terra.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nTHE earth is the LORD\'S, and all that therein is; \r\n* the compass of the world, and they that dwell therein.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor he hath founded it upon the seas, \r\n* and stablished it upon the floods.<br/> \r\n<small> &nbsp; <br/></small>\r\nWho shall ascend into the hill of the LORD? \r\n* or who shall rise up in his holy place?<br/>\r\n<small> &nbsp; <br/></small>\r\nEven he that hath clean hands, and a pure heart;  \r\n* and that hath not lift up his mind unto vanity, nor sworn \r\nto deceive his neighbour.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe shall receive the blessing from the LORD, \r\n* and righteousness from the God of his salvation.<br/> \r\n<small> &nbsp; <br/></small>\r\nThis is the generation of them that seek him; \r\n* even of them that seek thy face, O God of Jacob.<br/> \r\n<small> &nbsp; <br/></small>\r\nLift up your heads, O ye gates; and be ye lift up,  \r\nye everlasting doors; \r\n* and the King of glory shall come in.<br/> \r\n<small> &nbsp; <br/></small>\r\nWho is this King of glory? \r\n* It is the LORD strong and mighty, even the LORD \r\nmighty in battle.<br/> \r\n<small> &nbsp; <br/></small>\r\nLift up your heads, O ye gates; and be ye lift up, \r\nye everlasting doors; \r\n* and the King of glory shall come in.<br/> \r\n<small> &nbsp; <br/></small>\r\nWho is this King of glory? \r\n* Even the LORD of hosts, he is the King of glory.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(25, '<div class=\"text-center\"><h4><i>Ad te, Domine, levavi.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nUNTO thee, O LORD, will I lift up my soul; my God, I have put my trust in thee: \r\n* O let me not be confounded, neither let mine enemies triumph over me.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor all they that hope in thee shall not be ashamed; * but such as transgress without a cause shall be put to \r\nconfusion.<br/> \r\n<small> &nbsp; <br/></small>\r\nShow me thy ways, O LORD, * and teach me thy paths.<br/> \r\n<small> &nbsp; <br/></small>\r\nLead me forth in thy truth, and learn me: * for thou art the God of my salvation; in thee hath been my \r\nhope all the day long.<br/> \r\n<small> &nbsp; <br/></small>\r\nCall to remembrance, O LORD, thy tender mercies, * and thy loving-kindnesses, which have been ever of \r\nold.<br/> \r\n<small> &nbsp; <br/></small>\r\nO remember not the sins and offences of my youth; * but according to thy mercy think thou upon me, O \r\nLORD, for thy goodness.<br/> \r\n<small> &nbsp; <br/></small>\r\nGracious and righteous is the LORD; * therefore, will he teach sinners in the way.<br/> \r\n<small> &nbsp; <br/></small>\r\nThem that are meek shall he guide in judgment; * and such as are gentle, them shall he learn his \r\nway.<br/> \r\n<small> &nbsp; <br/></small>\r\nAll the paths of the LORD are mercy and truth, * unto such as keep his covenant and his testimonies.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor thy Name\'s sake, O LORD, * be merciful unto my sin; for it is great.<br/> \r\n<small> &nbsp; <br/></small>\r\nWhat man is he that feareth the LORD? * him shall he teach in the way that he shall choose.<br/> \r\n<small> &nbsp; <br/></small>\r\nHis soul shall dwell at ease, * and his seed shall inherit the land.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe secret of the LORD is among them that fear him; * and he will show them his covenant.<br/> \r\n<small> &nbsp; <br/></small>\r\nMine eyes are ever looking unto the LORD; * for he shall pluck my feet out of the net.<br/> \r\n<small> &nbsp; <br/></small>\r\nTurn thee unto me, and have mercy upon me; * for I am desolate, and in misery.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe sorrows of my heart are enlarged: * O bring thou me out of my troubles.<br/> \r\n<small> &nbsp; <br/></small>\r\nLook upon my adversity and misery, * and forgive me all my sin.<br/> \r\n<small> &nbsp; <br/></small>\r\nConsider mine enemies, how many they are; * and they bear a tyrannous hate against me.<br/> \r\n<small> &nbsp; <br/></small>\r\nO keep my soul, and deliver me: * let me not be confounded, for I have put my trust in thee.<br/> \r\n<small> &nbsp; <br/></small>\r\nLet perfectness and righteous dealing wait upon me; * for my hope hath been in thee.<br/> \r\n<small> &nbsp; <br/></small>\r\nDeliver Israel, O God, * out of all his troubles.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(26, '<div class=\"text-center\"><h4><i>Judica me, Domine.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nBE thou my Judge, O LORD, \r\nfor I have walked innocently: \r\n* my trust hath been also in the LORD, \r\ntherefore shall I not fall.<br/> \r\n<small> &nbsp; <br/></small>\r\nExamine me, O LORD, and prove me; \r\n* try out my reins and my heart.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor thy loving-kindness is ever before mine eyes; \r\n* and I will walk in thy truth.<br/> \r\n<small> &nbsp; <br/></small>\r\nI have not dwelt with vain persons; \r\n* neither will I have fellowship with the deceitful.<br/> \r\n<small> &nbsp; <br/></small>\r\nI have hated the congregation of the wicked; \r\n* and will not sit among the ungodly.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will wash my hands in innocency, O LORD; \r\n* and so will I go to thine altar;<br/> \r\n<small> &nbsp; <br/></small>\r\nThat I may show the voice of thanksgiving, \r\n* and tell of all thy wondrous works.<br/> \r\n<small> &nbsp; <br/></small>\r\nLORD, I have loved the habitation of thy house, \r\n* and the place where thine honour dwelleth.<br/> \r\n<small> &nbsp; <br/></small>\r\nO shut not up my soul with the sinners, \r\n* nor my life with the blood-thirsty;<br/>\r\n<small> &nbsp; <br/></small>\r\nIn whose hands is wickedness, \r\n* and their right hand is full of gifts.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut as for me, I will walk innocently: \r\n* O deliver me, and be merciful unto me.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy foot standeth right: \r\n* I will praise the LORD in the congregations.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `psalm` (`id`, `description`, `inserted_at`, `updated_at`) VALUES
(27, '<div class=\"text-center\"><h4><i>Dominus illuminatio.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nTHE LORD is my light and my salvation; whom then \r\nshall I fear? \r\n* the LORD is the strength of my life; of whom then shall \r\nI be afraid?<br/>\r\n<small> &nbsp; <br/></small>\r\nWhen the wicked, even mine enemies and my foes, \r\ncame upon me to eat up my flesh, \r\n* they stumbled and fell.<br/> \r\n<small> &nbsp; <br/></small>\r\nThough an host of men were laid against me, yet shall \r\nnot my heart be afraid; \r\n* and though there rose up war against me, yet will I \r\nput my trust in him.<br/> \r\n<small> &nbsp; <br/></small>\r\nOne thing have I desired of the LORD, which \r\nI will require; \r\n* even that I may dwell in the house of the LORD all the \r\ndays of my life, to behold the fair beauty of the LORD, \r\nand to visit his temple.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor in the time of trouble he shall hide me in  \r\nhis tabernacle; \r\n* yea, in the secret place of his dwelling shall he hide me, \r\nand set me up upon a rock of stone.<br/> \r\n<small> &nbsp; <br/></small>\r\nAnd now shall he lift up mine head \r\n* above mine enemies round about me.<br/> \r\n<small> &nbsp; <br/></small>\r\nTherefore will I offer in his dwelling an oblation, \r\nwith great gladness: \r\n* I will sing and speak praises unto the LORD.<br/> \r\n<small> &nbsp; <br/></small>\r\nHearken unto my voice, O LORD, when I cry unto thee; \r\n* have mercy upon me, and hear me.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy heart hath talked of thee, Seek ye my face: \r\n* Thy face, LORD, will I seek.<br/> \r\n<small> &nbsp; <br/></small>\r\nO hide not thou thy face from me, \r\n* nor cast thy servant away in displeasure.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou hast been my succour; \r\n* leave me not, neither forsake me, O God of my salvation.<br/> \r\n<small> &nbsp; <br/></small>\r\nWhen my father and my mother forsake me,  \r\n* the LORD taketh me up.<br/> \r\n<small> &nbsp; <br/></small>\r\nTeach me thy way, O LORD, \r\n* and lead me in the right way, because of mine enemies.<br/> \r\n<small> &nbsp; <br/></small>\r\nDeliver me not over into the will of mine adversaries: \r\n* for there are false witnesses risen up against me, \r\nand such as speak wrong.<br/> \r\n<small> &nbsp; <br/></small>\r\nI should utterly have fainted, \r\n* but that I believe verily to see the goodness of the LORD \r\nin the land of the living.<br/> \r\n<small> &nbsp; <br/></small>\r\nO tarry thou the LORD\'S leisure; \r\n* be strong, and he shall comfort thine heart; and put thou \r\nthy trust in the LORD.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(28, '<div class=\"text-center\"><h4><i>Ad te, Domine.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nUNTO thee will I cry, O LORD, my strength: * think no scorn of me; lest, if thou make as though thou \r\nhearest not, I become like them that go down into the pit.<br/>\r\n<small> &nbsp; <br/></small>\r\nHear the voice of my humble petitions, when I cry unto thee; * when I hold up my hands towards the  \r\nmercy-seat of thy holy temple.<br/>\r\n<small> &nbsp; <br/></small>\r\nO pluck me not away, neither destroy me with the ungodly and wicked doers, * which speak friendly to \r\ntheir neighbours, but imagine mischief in their hearts.<br/>\r\n<small> &nbsp; <br/></small>\r\nReward them according to their deeds, * and according to the wickedness of their own inventions.<br/>\r\n<small> &nbsp; <br/></small>\r\nRecompense them after the work of their hands; * pay them that they have deserved.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor they regard not in their mind the works of the LORD, nor the operation of his hands; * therefore shall \r\nhe break them down, and not build them up.<br/>\r\n<small> &nbsp; <br/></small>\r\nPraised be the LORD; * for he hath heard the voice of my humble petitions.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe LORD is my strength, and my shield; my heart hath trusted in him, and I am helped; * therefore my \r\nheart danceth for joy, and in my song will I praise him.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe LORD is my strength, * and he is the wholesome defence of his anointed.<br/>\r\n<small> &nbsp; <br/></small>\r\nO save thy people, and give thy blessing unto thine inheritance: * feed them, and set them up for ever.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(29, '<div class=\"text-center\"><h4><i>Afferte Domino.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nASCRIBE unto the LORD, O ye mighty, * ascribe unto the LORD worship and strength.<br/>\r\n<small> &nbsp; <br/></small>\r\nAscribe unto the LORD the honour due unto his Name; * worship the LORD with holy worship.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe voice of the LORD is upon the waters; * it is the glorious God that maketh the thunder.<br/>\r\n<small> &nbsp; <br/></small>\r\nIt is the LORD that ruleth the sea; the voice of the  LORD is mighty in operation; * the voice of the LORD \r\nis a glorious voice.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe voice of the LORD breaketh the cedar-trees; * yea, the LORD breaketh the cedars of Lebanon.<br/>\r\n<small> &nbsp; <br/></small>\r\nHe maketh them also to skip like a calf; * Lebanon also, and Sirion, like a young unicorn.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe voice of the LORD divideth the flames of fire; the voice of the LORD shaketh the wilderness; * yea, \r\nthe LORD shaketh the wilderness of Kadesh.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe voice of the LORD maketh the hinds to bring  forth young, and strippeth bare the forests: * in his \r\ntemple doth every thing speak of his honour.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe LORD sitteth above the water-flood, * and the LORD remaineth a King for ever.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe LORD shall give strength unto his people; * the LORD shall give his people the blessing of peace.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(30, '<div class=\"text-center\"><h4><i>Exaltabo te, Domine.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nI WILL magnify thee, O LORD; for thou hast set me up,  \r\n* and not made my foes to triumph over me.<br/> \r\n<small> &nbsp; <br/></small>\r\nO LORD my God, I cried unto thee;  \r\n* and thou hast healed me.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou, LORD, hast brought my soul out of hell:  \r\n* thou hast kept my life, that I should not go down  \r\ninto the pit.<br/> \r\n<small> &nbsp; <br/></small>\r\nSing praises unto the LORD, O ye saints of his;  \r\n* and give thanks unto him, for a remembrance of  \r\nhis holiness.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor his wrath endureth but the twinkling of an eye,  \r\nand in his pleasure is life;  \r\n* heaviness may endure for a night, but joy cometh in  \r\nthe morning.<br/> \r\n<small> &nbsp; <br/></small>\r\nAnd in my prosperity I said, I shall never be removed:  \r\n* thou, LORD, of thy goodness, hast made my hill  \r\nso strong.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou didst turn thy face from me,  \r\n* and I was troubled.<br/> \r\n<small> &nbsp; <br/></small>\r\nThen cried I unto thee, O LORD;  \r\n* and gat me to my LORD right humbly.<br/> \r\n<small> &nbsp; <br/></small>\r\nWhat profit is there in my blood,  \r\n* when I go down into the pit?<br/> \r\n<small> &nbsp; <br/></small>\r\nShall the dust give thanks unto thee?  \r\n* or shall it declare thy truth?<br/> \r\n<small> &nbsp; <br/></small>\r\nHear, O LORD, and have mercy upon me;  \r\n* LORD, be thou my helper.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou hast turned my heaviness into joy;  \r\n* thou hast put off my sackcloth, and girded me  \r\nwith gladness:<br/> \r\n<small> &nbsp; <br/></small>\r\nTherefore shall every good man sing of thy praise \r\nwithout ceasing.  \r\n* O my God, I will give thanks unto thee for ever.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(31, '<div class=\"text-center\"><h4><i>In te, Domine, speravi.</i></h4></div><hr/><blockquote>\r\n<b>\r\nIN thee, O LORD, have I put my trust; let me never \r\nbe put to confusion; \r\n* deliver me in thy righteousness.<br/>\r\n<small> &nbsp; <br/></small>\r\nBow down thine ear to me; \r\n* make haste to deliver me.<br/>\r\n<small> &nbsp; <br/></small>\r\nAnd be thou my strong rock, and house of defence, \r\n* that thou mayest save me.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor thou art my strong rock, and my castle: \r\n* be thou also my guide, and lead me for thy \r\nName\'s sake.<br/>\r\n<small> &nbsp; <br/></small>\r\nDraw me out of the net that they have laid privily \r\nfor me; \r\n* for thou art my strength.<br/>\r\n<small> &nbsp; <br/></small>\r\nInto thy hands I commend my spirit; \r\n* for thou hast redeemed me, O LORD, thou God \r\nof truth.<br/>\r\n<small> &nbsp; <br/></small>\r\n</b>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(32, '<div class=\"text-center\"><h4><i>Beati quorum.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nBLESSED is he whose unrighteousness is forgiven, \r\n* and whose sin is covered.<br/> \r\n<small> &nbsp; <br/></small>\r\nBlessed is the man unto whom the LORD \r\nimputeth no sin, \r\n* and in whose spirit there is no guile.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor whilst I held my tongue, \r\n* my bones consumed away through my daily complaining.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor thy hand was heavy upon me day and night, \r\n* and my moisture was like the drought in summer.<br/> \r\n<small> &nbsp; <br/></small>\r\nI acknowledged my sin unto thee; \r\n* and mine unrighteousness have I not hid.<br/> \r\n<small> &nbsp; <br/></small>\r\nI said, I will confess my sins unto the LORD; \r\n* and so thou forgavest the wickedness of my sin.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor this shall every one that is godly make his prayer \r\nunto thee, in a time when thou mayest be found; \r\n* surely the great water-floods shall not come nigh him.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou art a place to hide me in; thou shalt preserve \r\nme from trouble; \r\n* thou shalt compass me about with songs of deliverance.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will inform thee, and teach thee in the way wherein \r\nthou shalt go; \r\n* and I will guide thee with mine eye.<br/> \r\n<small> &nbsp; <br/></small>\r\nBe ye not like to horse and mule, which have \r\nno understanding; \r\n* whose mouths must be held with bit and bridle, \r\nelse they will not obey thee.<br/> \r\n<small> &nbsp; <br/></small>\r\nGreat plagues remain for the ungodly; \r\n* but whoso putteth his trust in the LORD, mercy \r\nembraceth him on every side.<br/> \r\n<small> &nbsp; <br/></small>\r\nBe glad, O ye righteous, and rejoice in the LORD; \r\n* and be joyful, all ye that are true of heart.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(33, '<div class=\"text-center\"><h4><i>Exultate, justi.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nREJOICE in the LORD, O ye righteous; * for it be-cometh well the just to be thankful.<br/>\r\n<small> &nbsp; <br/></small>\r\nPraise the LORD with harp; * sing praises unto him with the lute, and instrument of ten strings.<br/> \r\n<small> &nbsp; <br/></small>\r\nSing unto the Lord a new song; * sing praises lustily unto him with a good courage.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor the word of the LORD is true; * and all his works are faithful.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe loveth righteousness and judgment; * the earth is full of the goodness of the LORD.<br/> \r\n<small> &nbsp; <br/></small>\r\nBy the word of the LORD were the heavens made; * and all the host of them by the breath of his \r\nmouth.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe gathereth the waters of the sea together, as it were upon an heap; * and layeth up the deep, as in a \r\ntreasure-house.<br/> \r\n<small> &nbsp; <br/></small>\r\nLet all the earth fear the LORD: * stand in awe of him, all ye that dwell in the world.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor he spake, and it was done; * he commanded, and it stood fast.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe LORD bringeth the counsel of the heathen to nought, \r\n* and maketh the devices of the people to be of none effect, and casteth out the counsels of princes.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe counsel of the LORD shall endure for ever, * and the thoughts of his heart from generation to \r\ngeneration.<br/> \r\n<small> &nbsp; <br/></small>\r\nBlessed are the people whose God is the Lord JEHOVAH; * and blessed are the folk that he hath chosen \r\nto him, to be his inheritance.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD looketh down from heaven, and beholdeth all the children of men; * from the habitation of his \r\ndwelling, he considereth all them that dwell on the earth.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe fashioneth all the hearts of them, * and understandeth all their works.<br/> \r\n<small> &nbsp; <br/></small>\r\nThere is no king that can be saved by the multitude of an host; * neither is any mighty man delivered by \r\nmuch strength.<br/> \r\n<small> &nbsp; <br/></small>\r\nA horse is counted but a vain thing to save a man; \r\n* neither shall he deliver any man by his great strength.<br/> \r\n<small> &nbsp; <br/></small>\r\nBehold, the eye of the LORD is upon them that fear him, \r\n* and upon them that put their trust in his mercy;<br/> \r\n<small> &nbsp; <br/></small>\r\nTo deliver their soul from death, * and to feed them in the time of dearth.<br/> \r\n<small> &nbsp; <br/></small>\r\nOur soul hath patiently tarried for the LORD; * for he is our help and our shield.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor our heart shall rejoice in him; * because we have hoped in his holy Name.<br/> \r\n<small> &nbsp; <br/></small>\r\nLet thy merciful kindness, O LORD, be upon us, * like as we do put our trust in thee.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(34, '<div class=\"text-center\"><h4><i>Benedicam Dominum.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nI WILL alway give thanks unto the LORD;  \r\n* his praise shall ever be in my mouth.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy soul shall make her boast in the LORD; \r\n* the humble shall hear thereof, and be glad.<br/> \r\n<small> &nbsp; <br/></small>\r\nO praise the LORD with me, \r\n* and let us magnify his Name together.<br/> \r\n<small> &nbsp; <br/></small>\r\nI sought the LORD, and he heard me; \r\n* yea, he delivered me out of all my fear.<br/> \r\n<small> &nbsp; <br/></small>\r\nThey had an eye unto him, and were lightened; \r\n* and their faces were not ashamed.<br/> \r\n<small> &nbsp; <br/></small>\r\nLo, the poor crieth, and the LORD heareth him; \r\n* yea, and saveth him out of all his troubles.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe angel of the LORD tarrieth round about them  \r\nthat fear him;  \r\n* and delivereth them.<br/> \r\n<small> &nbsp; <br/></small>\r\nO taste, and see, how gracious the LORD is: \r\n* blessed is the man that trusteth in him.<br/> \r\n<small> &nbsp; <br/></small>\r\nO fear the LORD, ye that are his saints; \r\n* for they that fear him lack nothing.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe lions do lack, and suffer hunger; \r\n* but they who seek the LORD shall want no manner of \r\nthing that is good.<br/> \r\n<small> &nbsp; <br/></small>\r\nCome, ye children, and hearken unto me; \r\n* I will teach you the fear of the LORD.<br/> \r\n<small> &nbsp; <br/></small>\r\nWhat man is he that lusteth to live, \r\n* and would fain see good days?<br/> \r\n<small> &nbsp; <br/></small>\r\nKeep thy tongue from evil, \r\n* and thy lips, that they speak no guile.<br/> \r\n<small> &nbsp; <br/></small>\r\nEschew evil, and do good; \r\n* seek peace, and ensue it.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe eyes of the LORD are over the righteous, \r\n* and his ears are open unto their prayers.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe countenance of the LORD is against them that \r\ndo evil, \r\n* to root out the remembrance of them from the earth.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe righteous cry, and the LORD heareth them, \r\n* and delivereth them out of all their troubles.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD is nigh unto them that are of a contrite heart, * and will save such as be of an humble \r\nspirit.<br/> \r\n<small> &nbsp; <br/></small>\r\nGreat are the troubles of the righteous; \r\n* but the LORD delivereth him out of all.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe keepeth all his bones, \r\n* so that not one of them is broken.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut misfortune shall slay the ungodly; \r\n* and they that hate the righteous shall be desolate.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD delivereth the souls of his servants; \r\n* and all they that put their trust in him shall not \r\nbe destitute.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(35, '<div class=\"text-center\"><h4><i>Judica, Domine.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nPLEAD thou my cause, O LORD, with them that strive with me,\r\n*  and fight thou against them that fight against me.<br/>\r\n<small> &nbsp; <br/></small>\r\nLay hand upon the shield and buckler,\r\n*  and stand up to help me.<br/>\r\n<small> &nbsp; <br/></small>\r\nBring forth the spear, and stop the way against them that pursue me:\r\n*  say unto my soul, I am thy salvation.<br/>\r\n<small> &nbsp; <br/></small>\r\nLet them be confounded, and put to shame, that seek after my soul;\r\n*  let them be turned back, and brought to confusion, that imagine mischief for me.<br/>\r\n<small> &nbsp; <br/></small>\r\nLet them be as the dust before the wind,\r\n*  and the angel of the LORD scattering them.<br/>\r\n<small> &nbsp; <br/></small>\r\nLet their way be dark and slippery,\r\n*  and let the angel of the LORD pursue them.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor they have privily laid their net to destroy me without a cause;\r\n*  yea, even without a cause have they made a pit for my soul.<br/>\r\n<small> &nbsp; <br/></small>\r\nLet a sudden destruction come upon him unawares, and his net that he hath laid privily catch himself;\r\n*  that he may fall into his own mischief.<br/>\r\n<small> &nbsp; <br/></small>\r\nAnd my soul shall be joyful in the LORD;\r\n*  it shall rejoice in his salvation.<br/>\r\n<small> &nbsp; <br/></small>\r\nAll my bones shall say, LORD, who is like unto thee, who deliverest the poor from him that is too strong for him;\r\n*  yea, the poor, and him that is in misery, from him that spoileth him?<br/>\r\n<small> &nbsp; <br/></small>\r\nFalse witnesses did rise up:\r\n*  they laid to my charge things that I knew not.<br/>\r\n<small> &nbsp; <br/></small>\r\nThey rewarded me evil for good,\r\n*  to the great discomfort of my soul.<br/>\r\n<small> &nbsp; <br/></small>\r\nNevertheless, when they were sick, I put on sackcloth, and humbled my soul with fasting;\r\n*  and my prayer shall turn into mine own bosom.<br/>\r\n<small> &nbsp; <br/></small>\r\nI behaved myself as though it had been my friend or my brother;\r\n*  I went heavily, as one that mourneth for his mother.<br/>\r\n<small> &nbsp; <br/></small>\r\nBut in mine adversity they rejoiced, and gathered themselves together;\r\n*  yea, the very abjects came together against me  unawares, making mouths at me, and ceased not.<br/>\r\n<small> &nbsp; <br/></small>\r\nWith the flatterers were busy mockers,\r\n*  who gnashed upon me with their teeth.<br/>\r\n<small> &nbsp; <br/></small>\r\nLord, how long wilt thou look upon this? \r\n*  O deliver my soul from the calamities which they bring on me, and  my darling from the lions.<br/>\r\n<small> &nbsp; <br/></small>\r\nSo will I give thee thanks in the great congregation;\r\n*  I will praise thee among much people.<br/>\r\n<small> &nbsp; <br/></small>\r\nO let not them that are mine enemies triumph over me ungodly;\r\n*  neither let them wink with their eyes, that hate me without a cause.<br/>\r\n<small> &nbsp; <br/></small>\r\nAnd why? their communing is not for peace;\r\n*  but they imagine deceitful words against them that are quiet in the land.<br/>\r\n<small> &nbsp; <br/></small>\r\nThey gaped upon me with their mouths, and said,\r\n*  Fie on thee! fie on thee! we saw it with our eyes.<br/>\r\n<small> &nbsp; <br/></small>\r\nThis thou hast seen, O LORD;\r\n*  hold not thy tongue then; go not far from me, O Lord.<br/>\r\n<small> &nbsp; <br/></small>\r\nAwake, and stand up to judge my quarrel;\r\n*  avenge thou my cause, my God and my Lord.<br/>\r\n<small> &nbsp; <br/></small>\r\nJudge me, O LORD my God, according to thy righteousness;\r\n*  and let them not triumph over me.<br/>\r\n<small> &nbsp; <br/></small>\r\nLet them not say in their hearts, There! there! so would we have it;\r\n*  neither let them say, We have devoured him.<br/>\r\n<small> &nbsp; <br/></small>\r\nLet them be put to confusion and shame together, that rejoice at my trouble;\r\n*  let them be clothed with rebuke and dishonour, that boast themselves against me.<br/>\r\n<small> &nbsp; <br/></small>\r\nLet them be glad and rejoice, that favour my righteous dealing;\r\n*  yea, let them say alway, Blessed be the LORD, who hath pleasure in the prosperity of his servant.<br/>\r\n<small> &nbsp; <br/></small>\r\nAnd as for my tongue, it shall be talking of thy righteousness,\r\n*  and of thy praise, all the day long.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(36, '<div class=\"text-center\"><h4><i>Dixit injustus.</i></h4></div><hr/><blockquote>\r\n<b>\r\nMY heart showeth me the wickedness of the ungodly, \r\n* that there is no fear of God before his eyes.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor he flattereth himself in his own sight, \r\n* until his abominable sin be found out.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe words of his mouth are unrighteous and \r\nfull of deceit: \r\n* he hath left off to behave himself wisely, and to do good.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe imagineth mischief upon his bed, and hath \r\nset himself in no good way; \r\n* neither doth he abhor any thing that is evil.<br/> \r\n<small> &nbsp; <br/></small>\r\n</b>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(37, '<div class=\"text-center\"><h4><i>Noli aemulari.</i></h4></div><hr/><blockquote>\r\n<b>\r\nFRET not thyself because of the ungodly; \r\n* neither be thou envious against the evil doers.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor they shall soon be cut down like the grass, \r\n* and be withered even as the green herb.<br/> \r\n<small> &nbsp; <br/></small>\r\nPut thou thy trust in the LORD, and be doing good; \r\n* dwell in the land, and verily thou shalt be fed.<br/> \r\n<small> &nbsp; <br/></small>\r\nDelight thou in the LORD, \r\n* and he shall give thee thy heart\'s desire.<br/> \r\n<small> &nbsp; <br/></small>\r\nCommit thy way unto the LORD, and put thy \r\ntrust in him, \r\n* and he shall bring it to pass.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe shall make thy righteousness as clear as the light, \r\n* and thy just dealing as the noon-day.<br/> \r\n<small> &nbsp; <br/></small>\r\nHold thee still in the LORD, and abide patiently \r\nupon him: \r\n* but grieve not thyself at him whose way doth prosper, \r\nagainst the man that doeth after evil counsels.<br/> \r\n<small> &nbsp; <br/></small>\r\nLeave off from wrath, and let go displeasure: \r\n* fret not thyself, else shalt thou be moved to do evil.<br/> \r\n<small> &nbsp; <br/></small>\r\nWicked doers shall be rooted out; \r\n* and they that patiently abide the LORD, those \r\nshall inherit the land.<br/> \r\n<small> &nbsp; <br/></small>\r\nYet a little while, and the ungodly shall be clean gone: \r\n* thou shalt look after his place, and he shall be away.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut the meek-spirited shall possess the earth, \r\n* and shall be refreshed in the multitude of peace.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe ungodly seeketh counsel against the just, \r\n* and gnasheth upon him with his teeth.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe Lord shall laugh him to scorn; \r\n* for he hath seen that his day is coming.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe ungodly have drawn out the sword, and have \r\nbent their bow, \r\n* to cast down the poor and needy, and to slay such as \r\nbe upright in their ways.<br/> \r\n<small> &nbsp; <br/></small>\r\nTheir sword shall go through their own heart, \r\n* and their bow shall be broken.<br/> \r\n<small> &nbsp; <br/></small>\r\nA small thing that the righteous hath, \r\n* is better than great riches of the ungodly.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor the arms of the ungodly shall be broken, \r\n* and the LORD upholdeth the righteous.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD knoweth the days of the godly; \r\n* and their inheritance shall endure for ever.<br/> \r\n<small> &nbsp; <br/></small>\r\nThey shall not be confounded in the perilous time; \r\n* and in the days of dearth they shall have enough.<br/> \r\n<small> &nbsp; <br/></small>\r\nAs for the ungodly, they shall perish, and the enemies of  \r\nthe LORD shall consume as the fat of lambs: \r\n* yea, even as the smoke shall they consume away.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe ungodly borroweth, and payeth not again; \r\n* but the righteous is merciful and liberal.<br/> \r\n<small> &nbsp; <br/></small>\r\nSuch as are blessed of God, shall possess the land; \r\n* and they that are cursed of him, shall be rooted out.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD ordereth a good man\'s going, \r\n* and maketh his way acceptable to himself.<br/> \r\n<small> &nbsp; <br/></small>\r\nThough he fall, he shall not be cast away; \r\n* for the LORD upholdeth him with his hand.<br/> \r\n<small> &nbsp; <br/></small>\r\n</b>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(38, '<div class=\"text-center\"><h4><i>Domine, ne in furore.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nPUT me not to rebuke, O LORD, in thine anger;\r\n*  neither chasten me in thy heavy displeasure:<br/>\r\n<small> &nbsp; <br/></small>\r\nFor thine arrows stick fast in me,\r\n*  and thy hand presseth me sore.<br/>\r\n<small> &nbsp; <br/></small>\r\nThere is no health in my flesh, because of thy displeasure;\r\n*  neither is there any rest in my bones, by reason of my sin.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor my wickednesses are gone over my head,\r\n*  and are like a sore burden, too heavy for me to bear.<br/>\r\n<small> &nbsp; <br/></small>\r\nMy wounds stink, and are corrupt,\r\n*  through my foolishness.<br/>\r\n<small> &nbsp; <br/></small>\r\nI am brought into so great trouble and misery,\r\n*  that I go mourning all the day long.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor my loins are filled with a sore disease,\r\n*  and there is no whole part in my body.<br/>\r\n<small> &nbsp; <br/></small>\r\nI am feeble and sore smitten;\r\n*  I have roared for the very disquietness of my heart.<br/>\r\n<small> &nbsp; <br/></small>\r\nLord, thou knowest all my desire;\r\n*  and my groaning is not hid from thee.<br/>\r\n<small> &nbsp; <br/></small>\r\nMy heart panteth, my strength hath failed me,\r\n*  and the light of mine eyes is gone from me.<br/>\r\n<small> &nbsp; <br/></small>\r\nMy lovers and my neighbours did stand looking upon my trouble,\r\n*  and my kinsmen stood afar off.<br/>\r\n<small> &nbsp; <br/></small>\r\nThey also that sought after my life laid snares for me;\r\n*  and they that went about to do me evil talked of wickedness, and imagined deceit all the day long.<br/>\r\n<small> &nbsp; <br/></small>\r\nAs for me, I was like a deaf man, and heard not;\r\n*  and as one that is dumb, who doth not open his mouth.<br/>\r\n<small> &nbsp; <br/></small>\r\nI became even as a man that heareth not,\r\n*  and in whose mouth are no reproofs.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor in thee, O LORD, have I put my trust;\r\n*  thou shalt answer for me, O Lord my God.<br/>\r\n<small> &nbsp; <br/></small>\r\nI have required that they, even mine enemies, should not triumph over me;\r\n*  for when my foot slipt, they rejoiced greatly against me.<br/>\r\n<small> &nbsp; <br/></small>\r\nAnd I truly am set in the plague,\r\n*  and my heaviness is ever in my sight.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor I will confess my wickedness,\r\n*  and be sorry for my sin.<br/>\r\n<small> &nbsp; <br/></small>\r\nBut mine enemies live, and are mighty;\r\n*  and they that hate me wrongfully are many in number.<br/>\r\n<small> &nbsp; <br/></small>\r\nThey also that reward evil for good are against me;\r\n*  because I follow the thing that good is.<br/>\r\n<small> &nbsp; <br/></small>\r\nForsake me not, O LORD my God;\r\n*  be not thou far from me.<br/>\r\n<small> &nbsp; <br/></small>\r\nHaste thee to help me,\r\n*  O Lord God of my salvation.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(39, '<div class=\"text-center\"><h4><i>Dixi, Custodiam.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nI SAID, I will take heed to my ways, \r\n* that I offend not in my tongue<br/> \r\n<small> &nbsp; <br/></small>\r\nI will keep my mouth as it were with a bridle, \r\n* while the ungodly is in my sight. <br/> \r\n<small> &nbsp; <br/></small>\r\nI held my tongue, and spake nothing: \r\n* I kept silence, yea, even from good words; but it was pain and grief to me. <br/> \r\n<small> &nbsp; <br/></small>\r\nMy heart was hot within me: and while I was thus musing the fire kindled, \r\n*and at the last I spake with my tongue: <br/> \r\n<small> &nbsp; <br/></small>\r\nLORD, let me know mine end, and the number of my days; \r\n* that I may be certified how long I have to live. <br/> \r\n<small> &nbsp; <br/></small>\r\nBehold, thou hast made my days as it were a span long, and mine age is even as nothing in respect of thee;\r\n* and verily every man living is altogether vanity. <br/> \r\n<small> &nbsp; <br/></small>\r\nFor man walketh in a vain shadow, and disquieteth  himself in vain; \r\n* he heapeth up riches, and cannot tell who shall gather them. <br/> \r\n<small> &nbsp; <br/></small>\r\nAnd now, Lord, what is my hope? \r\n* truly my hope is even in thee. <br/> \r\n<small> &nbsp; <br/></small>\r\nDeliver me from all mine offences; \r\n* and make me not a rebuke unto the foolish. <br/> \r\n<small> &nbsp; <br/></small>\r\nI became dumb, and opened not my mouth; \r\n* for it was thy doing. <br/> \r\n<small> &nbsp; <br/></small>\r\nTake thy plague away from me: \r\n* I am even consumed by the means of thy heavy hand. <br/> \r\n<small> &nbsp; <br/></small>\r\nWhen thou with rebukes dost chasten man for sin, thou makest his beauty to consume away, like as it were a moth fretting a garment: \r\n* every man therefore is but vanity. <br/> \r\n<small> &nbsp; <br/></small>\r\nHear my prayer, O LORD, and with thine ears consider my calling; \r\n* hold not thy peace at my tears; <br/> \r\n<small> &nbsp; <br/></small>\r\nFor I am a stranger with thee, and a sojourner, \r\n* as  all my fathers were. <br/> \r\n<small> &nbsp; <br/></small>\r\nO spare me a little, that I may recover my strength,\r\n*  before I go hence, and be no more seen.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(40, '<div class=\"text-center\"><h4><i>Expectans expectavi.</i></h4></div><hr/><blockquote>\r\n<b>\r\nI WAITED patiently for the LORD, \r\n* and he inclined unto me, and heard my calling.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe brought me also out of the horrible pit, \r\nout of the mire and clay, \r\n* and set my feet upon the rock, and ordered my goings.<br/> \r\n<small> &nbsp; <br/></small>\r\nAnd he hath put a new song in my mouth, \r\n* even a thanksgiving unto our God.<br/> \r\n<small> &nbsp; <br/></small>\r\nMany shall see it, and fear, \r\n* and shall put their trust in the LORD.<br/> \r\n<small> &nbsp; <br/></small>\r\nBlessed is the man that hath set his hope in the LORD, \r\n* and turned not unto the proud, and to such as go \r\nabout with lies.<br/> \r\n<small> &nbsp; <br/></small>\r\nO LORD my God, great are the wondrous works which \r\nthou hast done, like as be also thy thoughts, which are \r\nto us-ward; \r\n* and yet there is no man that ordereth them unto thee.<br/> \r\n<small> &nbsp; <br/></small>\r\nIf I should declare them, and speak of them, \r\n* they should be more than I am able to express.<br/> \r\n<small> &nbsp; <br/></small>\r\nSacrifice and offering thou wouldest not, \r\n* but mine ears hast thou opened.<br/> \r\n<small> &nbsp; <br/></small>\r\nBurnt-offering and sacrifice for sin hast thou \r\nnot required: \r\n* then said I, LO, I come; <br/>\r\n<small> &nbsp; <br/></small>\r\nIn the volume of the book it is written of me, \r\nthat I should fulfil thy will, O my God: \r\n* I am content to do it; yea, thy law is within my heart.<br/> \r\n<small> &nbsp; <br/></small>\r\nI have declared thy righteousness in the great \r\ncongregation: \r\n* lo, I will not refrain my lips, O LORD, and that \r\nthou knowest.<br/> \r\n<small> &nbsp; <br/></small>\r\nI have not hid thy righteousness within my heart; \r\n* my talk hath been of thy truth, and of thy salvation.<br/> \r\n<small> &nbsp; <br/></small>\r\nI have not kept back thy loving mercy and truth \r\n* from the great congregation.<br/> \r\n<small> &nbsp; <br/></small>\r\nWithdraw not thou thy mercy from me, O LORD; \r\n* let thy loving-kindness and thy truth alway preserve me.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor innumerable troubles are come about me; my sins \r\nhave taken such hold upon me, that I am not able \r\nto look up; \r\n* yea, they are more in number than the hairs of my head, \r\nand my heart hath failed me.<br/> \r\n<small> &nbsp; <br/></small>\r\nO LORD, let it be thy pleasure to deliver me; \r\n* make haste, O LORD, to help me.<br/> \r\n<small> &nbsp; <br/></small>\r\n</b>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(41, '<div class=\"text-center\"><h4><i>Beatus qui intelligit.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nBLESSED is he that considereth the poor and needy; \r\n* the LORD shall deliver him in the time of trouble.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe LORD preserve him, and keep him alive, that he may be blessed upon earth; \r\n* and deliver not thou him into the will of his enemies. <br/>\r\n<small> &nbsp; <br/></small>\r\nThe LORD comfort him when he lieth sick upon his bed; \r\n* make thou all his bed in his sickness. <br/>\r\n<small> &nbsp; <br/></small>\r\nI said, LORD, be merciful unto me; \r\n* heal my soul, for I have sinned against thee. <br/>\r\n<small> &nbsp; <br/></small>\r\nMine enemies speak evil of me, \r\n* When shall he die, and his name perish? <br/>\r\n<small> &nbsp; <br/></small>\r\nAnd if he come to see me, he speaketh vanity, \r\n* and his heart conceiveth falsehood within himself; and when he cometh forth, he telleth it.<br/>\r\n<small> &nbsp; <br/></small>\r\nAll mine enemies whisper together against me; \r\n* even against me do they imagine this evil. <br/>\r\n<small> &nbsp; <br/></small>\r\nAn evil disease, say they, cleaveth fast unto him; \r\n* and now that he lieth, he shall rise up no more. <br/>\r\n<small> &nbsp; <br/></small>\r\nYea, even mine own familiar friend whom I trusted, \r\n* who did also eat of my bread, hath laid great wait for me. <br/>\r\n<small> &nbsp; <br/></small>\r\nBut be thou merciful unto me, O LORD; \r\n* raise thou me up again, and I shall reward them. <br/>\r\n<small> &nbsp; <br/></small>\r\nBy this I know thou favourest me, \r\n* that mine enemy doth not triumph against me. <br/>\r\n<small> &nbsp; <br/></small>\r\nAnd in my innocency thou upholdest me, \r\n* and shalt set me before thy face for ever. <br/>\r\n<small> &nbsp; <br/></small>\r\nBlessed be the LORD God of Israel, \r\n* world without end. Amen.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(42, '<div class=\"text-center\"><h4><i>Quemadmodum.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nLIKE as the hart desireth the water-brooks, \r\n* so longeth my soul after thee, O God.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy soul is athirst for God, yea, even for the living God: \r\n* when shall I come to appear before the presence of God?<br/>\r\n<small> &nbsp; <br/></small>\r\nMy tears have been my meat day and night, \r\n* while they daily say unto me, Where is now thy God? <br/>\r\n<small> &nbsp; <br/></small>\r\nNow when I think thereupon, I pour out my \r\nheart by myself; \r\n* for I went with the multitude, and brought them forth \r\ninto the house of God; <br/>\r\n<small> &nbsp; <br/></small>\r\nIn the voice of praise and thanksgiving, \r\n* among such as keep holy-day.<br/> \r\n<small> &nbsp; <br/></small>\r\nWhy art thou so full of heaviness, O my soul? \r\n* and why art thou so disquieted within me? <br/>\r\n<small> &nbsp; <br/></small>\r\nO put thy trust in God; \r\n* for I will yet thank him, which is the help of my \r\ncountenance, and my God.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy soul is vexed within me; \r\n* therefore will I remember thee from the land of Jordan, \r\nfrom Hermon and the little hill.<br/> \r\n<small> &nbsp; <br/></small>\r\nOne deep calleth another, because of the noise of \r\nthy water-floods; \r\n* all thy waves and storms are gone over me.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD will grant his loving-kindness \r\nin the daytime; \r\n* and in the night season will I sing of him, and make \r\nmy prayer unto the God of my life.<br/> \r\n<small> &nbsp; <br/></small>\r\nI will say unto the God of my strength, Why hast \r\nthou forgotten me? \r\n* why go I thus heavily, while the enemy oppresseth me? <br/>\r\n<small> &nbsp; <br/></small>\r\nMy bones are smitten asunder as with a sword, \r\n* while mine enemies that trouble me cast me in the teeth; <br/>\r\n<small> &nbsp; <br/></small>\r\nNamely, while they say daily unto me, \r\n* Where is now thy God? <br/>\r\n<small> &nbsp; <br/></small>\r\nWhy art thou so vexed, O my soul? \r\n* and why art thou so disquieted within me? <br/>\r\n<small> &nbsp; <br/></small>\r\nO put thy trust in God; \r\n* for I will yet thank him, which is the help of my \r\ncountenance, and my God.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(43, '<div class=\"text-center\"><h4><i>Judica me, Deus</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nGIVE sentence with me, O God, and defend my cause \r\nagainst the ungodly people; \r\n* O deliver me from the deceitful and wicked man.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor thou art the God of my strength; why hast thou \r\nput me from thee? \r\n* and why go I so heavily, while the enemy oppresseth me?<br/>\r\n<small> &nbsp; <br/></small>\r\nO send out thy light and thy truth, that they \r\nmay lead me, \r\n* and bring me unto thy holy hill, and to thy dwelling; <br/>\r\n<small> &nbsp; <br/></small>\r\nAnd that I may go unto the altar of God, even unto \r\nthe God of my joy and gladness; \r\n* and upon the harp will I give thanks unto thee, \r\nO God, my God.<br/> \r\n<small> &nbsp; <br/></small>\r\nWhy art thou so heavy, O my soul? \r\n* and why art thou so disquieted within me?<br/> \r\n<small> &nbsp; <br/></small>\r\nO put thy trust in God; \r\n* for I will yet give him thanks, which is the help of my \r\ncountenance, and my God.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(44, '<div class=\"text-center\"><h4><i>Deus, auribus.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nWE have heard with our ears, O God, \r\nour fathers have told us \r\n* what thou hast done in their time of old:<br/>\r\n<small> &nbsp; <br/></small>\r\nHow thou hast driven out the heathen with thy hand, \r\nand planted our fathers in; \r\n* how thou hast destroyed the nations, and made thy \r\npeople to flourish.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor they gat not the land in possession through \r\ntheir own sword, \r\n* neither was it their own arm that helped them; <br/>\r\n<small> &nbsp; <br/></small>\r\nBut thy right hand, and thine arm, and the light of \r\nthy countenance; \r\n* because thou hadst a favour unto them.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou art my King, O God; \r\n* send help unto Jacob.<br/> \r\n<small> &nbsp; <br/></small>\r\nThrough thee will we overthrow our enemies, \r\n* and in thy Name will we tread them under that rise \r\nup against us.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor I will not trust in my bow, \r\n* it is not my sword that shall help me; <br/>\r\n<small> &nbsp; <br/></small>\r\nBut it is thou that savest us from our enemies, \r\n* and puttest them to confusion that hate us.<br/> \r\n<small> &nbsp; <br/></small>\r\nWe make our boast of God all day long, \r\n* and will praise thy Name for ever.<br/> \r\n<small> &nbsp; <br/></small>\r\nBut now thou art far off, and puttest us to confusion, \r\n* and goest not forth with our armies.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou makest us to turn our backs upon our enemies, \r\n* so that they which hate us spoil our goods.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou lettest us be eaten up like sheep, \r\n* and hast scattered us among the heathen.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou sellest thy people for nought, \r\n* and takest no money for them.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou makest us to be rebuked of our neighbours,  \r\n* to be laughed to scorn, and had in derision of them \r\nthat are round about us.<br/> \r\n<small> &nbsp; <br/></small>\r\nThou makest us to be a by-word among the nations, \r\n* and that the peoples shake their heads at us.<br/> \r\n<small> &nbsp; <br/></small>\r\nMy confusion is daily before me, \r\n* and the shame of my face hath covered me; <br/>\r\n<small> &nbsp; <br/></small>\r\nFor the voice of the slanderer and blasphemer, \r\n* for the enemy and avenger.<br/> \r\n<small> &nbsp; <br/></small>\r\nAnd though all this be come upon us, yet do we \r\nnot forget thee, \r\n* nor behave ourselves frowardly in thy covenant.<br/> \r\n<small> &nbsp; <br/></small>\r\nOur heart is not turned back,  \r\n* neither our steps gone out of thy way; <br/>\r\n<small> &nbsp; <br/></small>\r\nNo, not when thou hast smitten us into the place \r\nof dragons, \r\n* and covered us with the shadow of death.<br/> \r\n<small> &nbsp; <br/></small>\r\nIf we have forgotten the Name of our God, and holden \r\nup our hands to any strange god, \r\n* shall not God search it out? for he knoweth the very \r\nsecrets of the heart.<br/> \r\n<small> &nbsp; <br/></small>\r\nFor thy sake also are we killed all the day long, \r\n* and are counted as sheep appointed to be slain.<br/> \r\n<small> &nbsp; <br/></small>\r\nUp, Lord, why sleepest thou? \r\n* awake, and be not absent from us for ever.<br/> \r\n<small> &nbsp; <br/></small>\r\nWherefore hidest thou thy face, \r\n* and forgettest our misery and trouble? <br/>\r\n<small> &nbsp; <br/></small>\r\nFor our soul is brought low, even unto the dust;  \r\n* our belly cleaveth unto the ground.<br/> \r\n<small> &nbsp; <br/></small>\r\nArise, and help us,  \r\n* and deliver us, for thy mercy\'s sake.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(45, '<div class=\"text-center\"><h4><i>Eructavit cor meum.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nMY heart overfloweth with a good matter; I speak the\r\nthings which I have made concerning the King. \r\n* My tongue is the pen of a ready writer.<br/>\r\n<small> &nbsp; <br/></small>\r\nThou art fairer than the children of men; \r\n* full of grace are thy lips, because God hath blessed \r\nthee for ever.<br/>\r\n<small> &nbsp; <br/></small>\r\nGird thee with thy sword upon thy thigh, \r\nO thou Most Mighty, \r\n* according to thy worship and renown.<br/>\r\n<small> &nbsp; <br/></small>\r\nGood luck have thou with thine honour: \r\n* ride on, because of the word of truth, of meekness,\r\nand righteousness; and thy right hand shall teach \r\nthee terrible things.<br/>\r\n<small> &nbsp; <br/></small>\r\nThy arrows are very sharp in the heart of the \r\nKing\'s enemies,\r\n* and the people shall be subdued unto thee.<br/>\r\n<small> &nbsp; <br/></small>\r\nThy seat, O God, endureth for ever;\r\n* the sceptre of thy kingdom is a right sceptre.<br/>\r\n<small> &nbsp; <br/></small>\r\nThou hast loved righteousness, and hated iniquity; \r\n* wherefore God, even thy God, hath anointed thee \r\nwith the oil of gladness above thy fellows.<br/>\r\n<small> &nbsp; <br/></small>\r\nAll thy garments smell of myrrh, aloes, and cassia; \r\n* out of the ivory palaces, whereby they have made \r\nthee glad.<br/>\r\n<small> &nbsp; <br/></small>\r\nKings\' daughters are among thy honourable women;\r\n* upon thy right hand doth stand the queen in a vesture \r\nof gold, wrought about with divers colours.<br/>\r\n<small> &nbsp; <br/></small>\r\nHearken, O daughter, and consider; incline thine ear; \r\n* forget also thine own people, and thy father\'s house.<br/>\r\n<small> &nbsp; <br/></small>\r\nSo shall the King have pleasure in thy beauty; \r\n* for he is thy Lord, and worship thou him.<br/>\r\n<small> &nbsp; <br/></small>\r\nAnd the daughter of Tyre shall be there with a gift; \r\n* like as the rich also among the people shall make their \r\nsupplication before thee.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe King\'s daughter is all glorious within; \r\n* her clothing is of wrought gold.<br/>\r\n<small> &nbsp; <br/></small>\r\nShe shall be brought unto the King in raiment \r\nof needlework: \r\n* the virgins that be her fellows shall bear her company, \r\nand shall be brought unto thee.<br/>\r\n<small> &nbsp; <br/></small>\r\nWith joy and gladness shall they be brought, \r\n* and shall enter into the King\'s palace.<br/>\r\n<small> &nbsp; <br/></small>\r\nInstead of thy fathers, thou shalt have children, \r\n* whom thou mayest make princes in all lands.<br/>\r\n<small> &nbsp; <br/></small>\r\nI will make thy Name to be remembered from one \r\ngeneration to another; \r\n* therefore shall the people give thanks unto thee, world \r\nwithout end.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(46, '<div class=\"text-center\"><h4><i>Deus noster refugium.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nGOD is our hope and strength, \r\n* a very present help in trouble.<br/> \r\n<small> &nbsp; <br/></small>\r\nTherefore will we not fear, though the earth be moved, \r\n* and though the hills be carried into the midst of the sea;<br/> \r\n<small> &nbsp; <br/></small>\r\nThough the waters thereof rage and swell, \r\n* and though the mountains shake at the tempest \r\nof the same.<br/> \r\n<small> &nbsp; <br/></small>\r\nThere is a river, the streams whereof make glad \r\nthe city of God; \r\n*  the holy place of the tabernacle of the Most Highest.<br/> \r\n<small> &nbsp; <br/></small>\r\nGod is in the midst of her, therefore shall she not \r\nbe removed; \r\n* God shall help her, and that right early.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe nations make much ado, and the kingdoms \r\nare moved; \r\n* but God hath showed his voice, and the earth \r\nshall melt away.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD of hosts is with us; \r\n* the God of Jacob is our refuge.<br/> \r\n<small> &nbsp; <br/></small>\r\nO come hither, and behold the works of the LORD, \r\n* what destruction he hath brought upon the earth.<br/> \r\n<small> &nbsp; <br/></small>\r\nHe maketh wars to cease in all the world; \r\n* he breaketh the bow, and knappeth the spear in sunder, \r\nand burneth the chariots in the fire.<br/> \r\n<small> &nbsp; <br/></small>\r\nBe still then, and know that I am God: \r\n* I will be exalted among the nations, and I will \r\nbe exalted in the earth.<br/> \r\n<small> &nbsp; <br/></small>\r\nThe LORD of hosts is with us; \r\n* the God of Jacob is our refuge.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(47, '<div class=\"text-center\"><h4><i>Omnes gentes, plaudite.</i></h4></div><hr/><blockquote>\r\n<p>\r\n<b>\r\nO CLAP your hands together, all ye peoples:\r\n* O sing unto God with the voice of melody.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor the LORD is high, and to be feared;\r\n* he is the great King upon all the earth.<br/>\r\n<small> &nbsp; <br/></small>\r\nHe shall subdue the peoples under us, \r\n* and the nations under our feet.<br/>\r\n<small> &nbsp; <br/></small>\r\nHe shall choose out an heritage for us, \r\n* even the excellency of Jacob, whom he loved.<br/>\r\n<small> &nbsp; <br/></small>\r\nGod is gone up with a merry noise,\r\n* and the LORD with the sound of the trump.<br/>\r\n<small> &nbsp; <br/></small>\r\nO sing praises, sing praises unto our God;\r\n* O sing praises, sing praises unto our King.<br/>\r\n<small> &nbsp; <br/></small>\r\nFor God is the King of all the earth:\r\n* sing ye praises with understanding.<br/>\r\n<small> &nbsp; <br/></small>\r\nGod reigneth over the nations;\r\n* God sitteth upon his holy seat.<br/>\r\n<small> &nbsp; <br/></small>\r\nThe princes of the peoples are joined unto the people of  the God of Abraham; \r\n* for God, which is very high exalted, doth defend the earth, as it were with a shield.\r\n</b>\r\n</p>\r\n</blockquote>', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
